// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "../interface/IERC20.sol";

interface IPair {
    function getAmountOut(address from, address to, uint256 amount) external view returns (uint256);

    function swap(address _from, address _to, uint256 _amountOut, address _receiver, bytes calldata data) external;

    function claimFees() external returns (uint256[] memory);

    function tokens() external returns (address[] memory);

    function burn(address to) external returns (uint256[] memory amounts);

    function mint(address to) external returns (uint256 liquidity);

    function setAdminFeeRate(uint256 _adminFeeRate) external;

    function initialize() external;
}

interface IVolatilePair is IPair {
    function getUint256Reserves() external view returns (uint256 _reserve0, uint256 _reserve1, uint256 _blockTimestampLast);
}

interface IStablePair is IPair {
    function calculateTokenAmount(address[] calldata _tokens, uint256[] calldata _amounts, bool _deposit)
        external
        view
        returns (uint256);

    function calculateRemoveLiquidity(uint256 _amount)
        external
        view
        returns (uint256[] memory);
}

