        // bytes4(keccak256("implementation()")) == 0x5c60da1b
        (bool success, bytes memory returndata) = address(proxy).staticcall(hex"5c60da1b");
        require(success);
        return abi.decode(returndata, (address));
    }

    /**
     * @dev Returns the current admin of `proxy`.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function getProxyAdmin(TransparentUpgradeableProxy proxy) public view virtual returns (address) {
        // We need to manually run the static call since the getter cannot be flagged as view
        // bytes4(keccak256("admin()")) == 0xf851a440
        (bool success, bytes memory returndata) = address(proxy).staticcall(hex"f851a440");
        require(success);
        return abi.decode(returndata, (address));
    }

    /**
     * @dev Changes the admin of `proxy` to `newAdmin`.
     *
     * Requirements:
     *
     * - This contract must be the current admin of `proxy`.
     */
    function changeProxyAdmin(TransparentUpgradeableProxy proxy, address newAdmin) public virtual onlyOwner {
        proxy.changeAdmin(newAdmin);
    }

    /**
     * @dev Upgrades `proxy` to `implementation`. See {TransparentUpgradeableProxy-upgradeTo}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgrade(TransparentUpgradeableProxy proxy, address implementation) public virtual onlyOwner {
        proxy.upgradeTo(implementation);
    }

    /**
     * @dev Upgrades `proxy` to `implementation` and calls a function on the new implementation. See
     * {TransparentUpgradeableProxy-upgradeToAndCall}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgradeAndCall(
        TransparentUpgradeableProxy proxy,
        address implementation,
        bytes memory data
    ) public payable virtual onlyOwner {
        proxy.upgradeToAndCall{value: msg.value}(implementation, data);
    }
}

contracts/PairFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "hardhat/console.sol";
import "./utils/Arrays.sol";
import "./interface/IPairFactory.sol";
import { IPair } from "./interface/IPair.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";

contract PairFactory is IPairFactory, Initializable {
    using Arrays for address[];

    uint256 public constant MAX_ADMIN_FEE = 3; // 0.3%
    address public manager;
    address public pendingManager;

    uint256 public adminFeeRate;

    address[] internal temps;
    bytes internal tempData;

    mapping(address => bool) public isPair; // simplified check if its a pair, given that `stable` flag might not be available in peripherals
    address[] public allPairs;
    mapping(bytes => bool) public hasPair;
    mapping(address => mapping(address => mapping(bool => address[]))) public pair;

    address volatilePairImpl;
    address stablePairImpl;

    event PairCreated(address[] tokens, bool stable, address pair, uint256 pairsAmount);
    event SetAdminFeeRate(uint256 _old, uint256 _new);
    event SetPairAdminFeeRate(uint256 _old, uint256 _new);

    struct PairTokens {
        address _tokenA;
        address _tokenB;
    }

    function initialize(
        uint256 _adminFeeRate,
        address _volatilePairImpl,
        address _stablePairImpl
    ) public initializer {
        require(_adminFeeRate <= MAX_ADMIN_FEE, "Factory: Must be less than MAX_ADMIN_FEE");
        manager = msg.sender;
        adminFeeRate = _adminFeeRate;

        volatilePairImpl = _volatilePairImpl;
        stablePairImpl = _stablePairImpl;
    }

    constructor(
        uint256 _adminFeeRate,
        address _volatilePairImpl,
        address _stablePairImpl
    ) {
        initialize(_adminFeeRate, _volatilePairImpl, _stablePairImpl);
    }

    function initializeImpl(address[] memory _tokens) external {
        bytes memory _data = abi.encode(10000, 10);
        (temps, tempData) = (_tokens, _data);
        IPair(volatilePairImpl).initialize();
        IPair(stablePairImpl).initialize();
    }

    function allPairsLength() external view returns (uint256) {
        return allPairs.length;
    }

    function setManager(address _manager) external {
        require(msg.sender == manager, "not fee manager");
        pendingManager = _manager;
    }

    function acceptManager() external {
        require(msg.sender == pendingManager, "not pending fee manager");
        manager = pendingManager;
    }

    function setAdminFeeRate(uint256 _adminFeeRate) external {
        require(msg.sender == manager, "not fee manager");
        uint256 _old = adminFeeRate;
        adminFeeRate = _adminFeeRate;
        emit SetAdminFeeRate(_old, _adminFeeRate);
    }

    function setPairAdminFeeRate(address _pair, uint256 _adminFeeRate) external {
        require(msg.sender == manager, "not fee manager");
        uint256 _old = adminFeeRate;
        IPair(_pair).setAdminFeeRate(_adminFeeRate);
        emit SetPairAdminFeeRate(_old, _adminFeeRate);
    }

    function getInitializable()
        external
        view
        returns (
            address[] memory,
            bytes memory,
            uint256
        )
    {
        bytes memory _data = tempData;
        return (temps, _data, adminFeeRate);
    }

    function getPairAddress(address[] memory _tokens, bool _stable) external view returns (address) {
        bytes32 salt = keccak256(abi.encodePacked(_tokens.sortArray(), _stable));
        return Clones.predictDeterministicAddress(_stable ? stablePairImpl : volatilePairImpl, salt);
    }

    function getPair(
        address _tokenA,
        address _tokenB,
        bool _stable
    ) external view override returns (address[] memory) {
        return pair[_tokenA][_tokenB][_stable];
    }

    function isHasPair(address[] memory _tokens, bool _stable) external view returns (bool) {
        return hasPair[abi.encodePacked(_tokens, _stable)];
    }

    function createPair(
        address[] memory _tokens,
        bool _stable,
        bytes memory _data
    ) external returns (address _pair) {
        _tokens = _tokens.sortArray();
        checkTokens(_tokens);

        checkPair(_tokens, _stable);

        (temps, tempData) = (_tokens, _data);
        bytes32 salt = keccak256(abi.encodePacked(_tokens, _stable)); // notice salt includes stable as well, 2 parameters

        address _pairImpl = volatilePairImpl;
        if (_stable) {
            require(manager == msg.sender, "Factory: Stable pair can only be created by manager");
            _pair = stablePairImpl;
        }
        _pair = Clones.cloneDeterministic(_pairImpl, salt);

        IPair(_pair).initialize();

        _setPair(_tokens, _stable, _pair);

        hasPair[abi.encodePacked(_tokens, _stable)] = true;
        allPairs.push(_pair);
        isPair[_pair] = true;

        emit PairCreated(_tokens, _stable, _pair, allPairs.length);
    }

    function getEveryPair(address[] memory _tokens) public pure returns (PairTokens[] memory) {
        PairTokens[] memory _pairs = new PairTokens[]((_tokens.length - 1) * _tokens.length);
        uint256 _lastIndex = 0;
        for (uint256 _i = 0; _i < _tokens.length - 1; _i++) {
            for (uint256 _j = _i + 1; _j < _tokens.length; _j++) {
                _pairs[_lastIndex++] = PairTokens({ _tokenA: _tokens[_i], _tokenB: _tokens[_j] });
                _pairs[_lastIndex++] = PairTokens({ _tokenA: _tokens[_j], _tokenB: _tokens[_i] });
            }
        }
        return _pairs;
    }

    function checkTokens(address[] memory _tokens) public pure {
        require(_tokens[0] != address(0), "ZA"); // ZERO_ADDRESS
        for (uint256 _i = 0; _i < _tokens.length - 1; _i++) {
            for (uint256 _j = _i + 1; _j < _tokens.length; _j++) {
                require(_tokens[_j] != address(0), "ZA"); // ZERO_ADDRESS
                require(_tokens[_j] != _tokens[_i], "Factory: IA"); //IDENTICAL_ADDRESSES
            }
        }
    }

    function checkPair(address[] memory _tokens, bool _stable) public view returns (bytes memory _key) {
        _key = abi.encodePacked(_tokens, _stable);
        require(!hasPair[_key], "Factory: Pair already exists");
    }

    function _setPair(
        address[] memory _tokens,
        bool _stable,
        address _pair
    ) internal {
        PairTokens[] memory _pairs = getEveryPair(_tokens);
        for (uint256 _i = 0; _i < _pairs.length; _i++) {
            pair[_pairs[_i]._tokenA][_pairs[_i]._tokenB][_stable].push(_pair);
        }
    }
}

contracts/PairFees.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

// Pair Fees contract is used as a 1:1 pair relationship to split out fees, this ensures that the curve does not need to be modified for LP shares
contract PairFees {

    address internal immutable pair; // The pair it is bonded to
    address internal immutable token0; // token0 of pair, saved localy and statically for gas optimization
    address internal immutable token1; // Token1 of pair, saved localy and statically for gas optimization

    constructor(address _token0, address _token1) {
        pair = msg.sender;
        token0 = _token0;
        token1 = _token1;
    }

    function _safeTransfer(address token,address to,uint256 value) internal {
        require(token.code.length > 0);
        (bool success, bytes memory data) =
        token.call(abi.encodeWithSelector(IERC20.transfer.selector, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))));
    }

    // Allow the pair to transfer fees to users
    function claimFeesFor(address recipient, uint amount0, uint amount1) external {
        require(msg.sender == pair);
        if (amount0 > 0) _safeTransfer(token0, recipient, amount0);
        if (amount1 > 0) _safeTransfer(token1, recipient, amount1);
    }

}

contracts/Router.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import "./interface/IWETH.sol";
import "./interface/IPair.sol";
import "./interface/IERC20.sol";
import "./interface/IPairFactory.sol";

import "./utils/Arrays.sol";

import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract Router is Initializable {
    using Arrays for address[];

    struct Route {
        address from;
        address to;
        address pair;
        bool stable;
    }

    address public factory;
    IWETH public weth;
    uint256 internal constant MINIMUM_LIQUIDITY = 10**3;

    modifier ensure(uint256 _deadline) {
        require(_deadline >= block.timestamp, "Router: EXPIRED");
        _;
    }

    function initialize(address _factory, address _weth) public initializer {
        factory = _factory;
        weth = IWETH(_weth);
    }

    constructor(address _factory, address _weth) {
        initialize(_factory, _weth);
    }

    function sortTokens(address _tokenA, address _tokenB) public pure returns (address _token0, address _token1) {
        require(_tokenA != _tokenB, "Router: IDENTICAL_ADDRESSES");
        (_token0, _token1) = _tokenA < _tokenB ? (_tokenA, _tokenB) : (_tokenB, _tokenA);
        require(_token0 != address(0), "Router: ZERO_ADDRESS");
    }

    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(address[] memory _tokens, bool _stable) public view returns (address _pair) {
        _pair = IPairFactory(factory).getPairAddress(_tokens, _stable);
    }

    // performs chained getAmountOut calculations on any number of pairs
    function getAmountOut(
        uint256 _amountIn,
        address _tokenIn,
        address _tokenOut
    ) external view returns (uint256, bool) {
        address _pair = _pairFor(_tokenIn, _tokenOut, true);
        uint256 _amountStable;
        uint256 _amountVolatile;
        if (IPairFactory(factory).isPair(_pair)) {
            _amountStable = IPair(_pair).getAmountOut(_tokenIn, _tokenOut, _amountIn);
        }
        _pair = _pairFor(_tokenIn, _tokenOut, false);
        if (IPairFactory(factory).isPair(_pair)) {
            _amountVolatile = IPair(_pair).getAmountOut(_tokenIn, _tokenOut, _amountIn);
        }
        return _amountStable > _amountVolatile ? (_amountStable, true) : (_amountVolatile, false);
    }

    // performs chained getAmountOut calculations on any number of pairs
    function getAmountsOut(uint256 _amountIn, Route[] memory _routes) public view returns (uint256[] memory _amounts) {
        require(_routes.length >= 1, "Router: INVALID_PATH");
        _amounts = new uint256[](_routes.length + 1);
        _amounts[0] = _amountIn;
        for (uint256 i = 0; i < _routes.length; i++) {
            if (IPairFactory(factory).isPair(_routes[i].pair)) {
                _amounts[i + 1] = IPair(_routes[i].pair).getAmountOut(_routes[i].from, _routes[i].to, _amounts[i]);
            }
        }
    }

    function isPair(address _pair) external view returns (bool) {
        return IPairFactory(factory).isPair(_pair);
    }

    function quoteAddLiquidity(
        address[] memory _tokens,
        bool _stable,
        uint256[] memory _amountDesireds
    )
        external
        view
        returns (
            // address _tokenA,
            // address _tokenB,
            // uint256 _amountADesired,
            // uint256 _amountBDesired
            uint256[] memory,
            // uint256 _amountA,
            // uint256 _amountB,
            uint256
        )
    {
        // create the pair if it doesn't exist yet
        address _pair = pairFor(_tokens, _stable);
        bool _isPair = IPairFactory(factory).isPair(_pair);
        // if (!IPairFactory(factory).isPair(_pair))
        //     return;
        uint256[] memory _amounts;
        uint256 _liquidity;
        if (_stable) {
            if (_isPair) {
                _amounts = _amountDesireds;
                _liquidity = IStablePair(_pair).calculateTokenAmount(_tokens, _amountDesireds, true);
            }

            return (_amounts, _liquidity);
        }
        (uint256 _reserveA, uint256 _reserveB) = (0, 0);
        uint256 _totalSupply = 0;
        if (_isPair) {
            _totalSupply = IERC20(_pair).totalSupply();
            (_reserveA, _reserveB) = _getVolatilePairReserves(_tokens[0], _tokens[1]);
        }
        if (_reserveA == 0 && _reserveB == 0) {
            (_amounts[0], _amounts[1]) = (_amountDesireds[0], _amountDesireds[1]);
            _liquidity = Math.sqrt(_amounts[0] * _amounts[1]) - MINIMUM_LIQUIDITY;
        } else {
            uint256 _amountBOptimal = _quoteVolatilePairLiquidity(_amountDesireds[0], _reserveA, _reserveB);
            if (_amountBOptimal <= _amountDesireds[1]) {
                (_amounts[0], _amounts[1]) = (_amountDesireds[0], _amountBOptimal);
                _liquidity = Math.min((_amounts[0] * _totalSupply) / _reserveA, (_amounts[1] * _totalSupply) / _reserveB);
            } else {
                uint256 _amountAOptimal = _quoteVolatilePairLiquidity(_amountDesireds[1], _reserveB, _reserveA);
                (_amounts[0], _amounts[1]) = (_amountAOptimal, _amountDesireds[1]);
                _liquidity = Math.min((_amounts[0] * _totalSupply) / _reserveA, (_amounts[1] * _totalSupply) / _reserveB);
            }
        }
        return (_amounts, _liquidity);
    }

    //TODO need StablePair version
    function quoteRemoveLiquidity(
        address[] memory _tokens,
        bool _stable,
        uint256 _liquidity
    ) external view returns (uint256[] memory _amounts) {
        // create the pair if it doesn't exist yet
        address _pair = pairFor(_tokens, _stable);

        if (!IPairFactory(factory).isPair(_pair)) {
            return _amounts;
        }

        if (_stable) {
            _amounts = IStablePair(_pair).calculateRemoveLiquidity(_liquidity);
        } else {
            (uint256 _reserveA, uint256 _reserveB) = _getVolatilePairReserves(_tokens[0], _tokens[1]);
            uint256 _totalSupply = IERC20(_pair).totalSupply();

            _amounts = new uint256[](2);

            _amounts[0] = (_liquidity * _reserveA) / _totalSupply; // using balances ensures pro-rata distribution
            _amounts[1] = (_liquidity * _reserveB) / _totalSupply; // using balances ensures pro-rata distribution
        }
    }

    function _addVolatilePairLiquidity(
        address _tokenA,
        address _tokenB,
        uint256 _amountADesired,
        uint256 _amountBDesired,
        uint256 _amountAMin,
        uint256 _amountBMin
    ) internal returns (uint256 _amountA, uint256 _amountB) {
        require(_amountADesired >= _amountAMin);
        require(_amountBDesired >= _amountBMin);
        // create the pair if it doesn't exist yet
        address _pair = IPairFactory(factory).getPair(_tokenA, _tokenB, false)[0];
        if (_pair == address(0)) {
            address[] memory _tokens = new address[](2);
            _tokens[0] = _tokenA;
            _tokens[1] = _tokenB;
            _pair = IPairFactory(factory).createPair(_tokens, false, new bytes(0));
        }
        require(_pair != address(0), "Router: not have this pair");
        (uint256 _reserveA, uint256 _reserveB) = _getVolatilePairReserves(_tokenA, _tokenB);
        if (_reserveA == 0 && _reserveB == 0) {
            (_amountA, _amountB) = (_amountADesired, _amountBDesired);
        } else {
            uint256 _amountBOptimal = _quoteVolatilePairLiquidity(_amountADesired, _reserveA, _reserveB);
            if (_amountBOptimal <= _amountBDesired) {
                require(_amountBOptimal >= _amountBMin, "Router: INSUFFICIENT_B_AMOUNT");
                (_amountA, _amountB) = (_amountADesired, _amountBOptimal);
            } else {
                uint256 _amountAOptimal = _quoteVolatilePairLiquidity(_amountBDesired, _reserveB, _reserveA);
                assert(_amountAOptimal <= _amountADesired);
                require(_amountAOptimal >= _amountAMin, "Router: INSUFFICIENT_A_AMOUNT");
                (_amountA, _amountB) = (_amountAOptimal, _amountBDesired);
            }
        }
    }

    function addLiquidity(
        address[] memory _tokens,
        bool _stable,
        // uint256 _amountADesired,
        // uint256 _amountBDesired,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    )
        external
        ensure(_deadline)
        returns (
            // uint256 _amountA,
            // uint256 _amountB,
            uint256[] memory _amounts,
            uint256 _liquidity
        )
    {
        address _pair = pairFor(_tokens, _stable);
        _amounts = _amountDesireds;
        if (!_stable) {
            (_amounts[0], _amounts[1]) = _addVolatilePairLiquidity(_tokens[0], _tokens[1], _amountDesireds[0], _amountDesireds[1], _amountsMin[0], _amountsMin[1]);
            // address _pair = _pairFor(_tokens[0], _tokens[1], _stable);
            // _amounts[0] = _amountA;
            // _amounts[1] = _amountB;
            // _safeTransferFrom(_tokens[0], msg.sender, _pair, _amountA);
            // _safeTransferFrom(_tokens[1], msg.sender, _pair, _amountB);
            // _liquidity = IPair(_pair).mint(_to);
        }
        // else {
        //TODO StablePair version use minLiquidity
        // }
        for (uint256 i = 0; i < _tokens.length; i++) {
            _safeTransferFrom(_tokens[i], msg.sender, _pair, _amounts[i]);
        }
        _liquidity = IPair(_pair).mint(_to);
        require(_liquidity >= _minLiquidity, "Couldn't mint min requested");
    }

    function addLiquidityETH(
        address _token,
        uint256 _amountTokenDesired,
        uint256 _amountTokenMin,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline
    )
        external
        payable
        ensure(_deadline)
        returns (
            uint256 _amountToken,
            uint256 _amountETH,
            uint256 _liquidity
        )
    {
        (_amountToken, _amountETH) = _addVolatilePairLiquidity(_token, address(weth), _amountTokenDesired, msg.value, _amountTokenMin, _amountETHMin);
        address _pair = _pairFor(_token, address(weth), false);
        _safeTransferFrom(_token, msg.sender, _pair, _amountToken);
        weth.deposit{ value: _amountETH }();
        assert(weth.transfer(_pair, _amountETH));
        _liquidity = IPair(_pair).mint(_to);
        // refund dust eth, if any
        if (msg.value > _amountETH) _safeTransferETH(msg.sender, msg.value - _amountETH);
    }



    // **** REMOVE LIQUIDITY ****
    function removeLiquidity(
        address[] memory _tokens,
        bool _stable,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public ensure(_deadline) returns (uint256[] memory _amounts) {
        address _pair = pairFor(_tokens, _stable);
        require(IPairFactory(factory).isPair(_pair), "Router: pair doesn't exist"); // send liquidity to pair
        require(IERC20(_pair).transferFrom(msg.sender, _pair, _liquidity)); // send liquidity to pair
        _amounts = IPair(_pair).burn(_to);

        for (uint256 i = 0; i < _tokens.length; i++) {
            require(_amounts[i] >= _amountsMin[i], "Router: INSUFFICIENT_A_AMOUNT");
        }
    }

    ///@dev only VolatilePair has eth
    function removeLiquidityETH(
        address _token,
        uint256 _liquidity,
        uint256 _amountTokenMin,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline
    ) public ensure(_deadline) returns (uint256 _amountToken, uint256 _amountETH) {
        address[] memory _tokens = new address[](2);
        _tokens[0] = _token;
        _tokens[1] = address(weth);
        uint256[] memory _amountMin = new uint256[](2);
        _amountMin[0] = _amountTokenMin;
        _amountMin[1] = _amountETHMin;
        uint256[] memory _amounts = removeLiquidity(_tokens, false, _liquidity, _amountMin, address(this), _deadline);
        _amountToken = _amounts[0];
        _amountETH = _amounts[1];
        _safeTransfer(_token, _to, _amountToken);
        weth.withdraw(_amountETH);
        _safeTransferETH(_to, _amountETH);
    }

    function removeLiquidityWithPermit(
        address[] memory _tokens,
        bool _stable,
        uint256 _liquidity,
        uint256[] memory _amountMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256[] memory _amounts) {
        address _pair = pairFor(_tokens, _stable);
        {
            uint256 _value = _approveMax ? type(uint256).max : _liquidity;
            IERC20(_pair).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        }

        _amounts = removeLiquidity(_tokens, _stable, _liquidity, _amountMin, _to, _deadline);
    }

    ///@dev only VolatilePair has eth
    function removeLiquidityETHWithPermit(
        address _token,
        uint256 _liquidity,
        uint256 _amountTokenMin,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256 _amountToken, uint256 _amountETH) {
        address _pair = _pairFor(_token, address(weth), false);
        uint256 _value = _approveMax ? type(uint256).max : _liquidity;
        IERC20(_pair).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        (_amountToken, _amountETH) = removeLiquidityETH(_token, _liquidity, _amountTokenMin, _amountETHMin, _to, _deadline);
    }

    // **** SWAP ****
    // requires the initial amount to have already been sent to the first pair
    function _swap(
        uint256[] memory _amounts,
        Route[] memory _routes,
        address _receiver
    ) internal virtual {
        for (uint256 i = 0; i < _routes.length; i++) {
            uint256 _amountOut = _amounts[i + 1];
            address _to = i < _routes.length - 1 ? _routes[i + 1].pair : _receiver;
            IPair(_routes[i].pair).swap(_routes[i].from, _routes[i].to, _amountOut, _to, new bytes(0));
        }
    }

    // function swapExactTokensForTokensSimple(
    //     uint256 _amountIn,
    //     uint256 _amountOutMin,
    //     address _tokenFrom,
    //     address _tokenTo,
    //     bool _stable,
    //     address _to,
    //     uint256 _deadline
    // ) external ensure(_deadline) returns (uint256[] memory _amounts) {
    //     Route[] memory _routes = new Route[](1);
    //     _routes[0].from = _tokenFrom;
    //     _routes[0].to = _tokenTo;
    //     _routes[0].stable = _stable;
    //     return swapExactTokensForTokens(_amountIn, _amountOutMin, _routes, _to, _deadline);
    // }

    function swapExactTokensForTokens(
        uint256 _amountIn,
        uint256 _amountOutMin,
        Route[] memory _routes,
        address _to,
        uint256 _deadline
    ) public ensure(_deadline) returns (uint256[] memory _amounts) {
        _amounts = getAmountsOut(_amountIn, _routes);
        require(_amounts[_amounts.length - 1] >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");
        _safeTransferFrom(_routes[0].from, msg.sender, _routes[0].pair, _amounts[0]);
        _swap(_amounts, _routes, _to);
    }

    function swapExactETHForTokens(
        uint256 _amountOutMin,
        Route[] calldata _routes,
        address _to,
        uint256 _deadline
    ) external payable ensure(_deadline) returns (uint256[] memory _amounts) {
        require(_routes[0].from == address(weth), "Router: INVALID_PATH");
        _amounts = getAmountsOut(msg.value, _routes);
        require(_amounts[_amounts.length - 1] >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");
        weth.deposit{ value: _amounts[0] }();
        assert(weth.transfer(_routes[0].pair, _amounts[0]));
        _swap(_amounts, _routes, _to);
    }

    function swapExactTokensForETH(
        uint256 _amountIn,
        uint256 _amountOutMin,
        Route[] calldata _routes,
        address _to,
        uint256 _deadline
    ) external ensure(_deadline) returns (uint256[] memory _amounts) {
        require(_routes[_routes.length - 1].to == address(weth), "Router: INVALID_PATH");
        _amounts = getAmountsOut(_amountIn, _routes);
        require(_amounts[_amounts.length - 1] >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");
        _safeTransferFrom(_routes[0].from, msg.sender, _routes[0].pair, _amounts[0]);
        _swap(_amounts, _routes, address(this));
        weth.withdraw(_amounts[_amounts.length - 1]);
        _safeTransferETH(_to, _amounts[_amounts.length - 1]);
    }

    function UNSAFE_swapExactTokensForTokens(
        uint256[] memory _amounts,
        Route[] calldata _routes,
        address _to,
        uint256 _deadline
    ) external ensure(_deadline) returns (uint256[] memory) {
        _safeTransferFrom(_routes[0].from, msg.sender, _routes[0].pair, _amounts[0]);
        _swap(_amounts, _routes, _to);
        return _amounts;
    }

    function _safeTransferETH(address _to, uint256 _value) internal {
        (bool _success, ) = _to.call{ value: _value }(new bytes(0));
        require(_success, "TransferHelper: ETH_TRANSFER_FAILED");
    }

    function _safeTransfer(
        address _token,
        address _to,
        uint256 _value
    ) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IERC20.transfer.selector, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function _safeTransferFrom(
        address _token,
        address _from,
        address _to,
        uint256 _value
    ) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IERC20.transferFrom.selector, _from, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function _pairFor(
        address _tokenA,
        address _tokenB,
        bool _stable
    ) internal view returns (address _pair) {
        address[] memory _tokens = new address[](2);
        _tokens[0] = _tokenA;
        _tokens[1] = _tokenB;
        _pair = pairFor(_tokens, _stable);
    }

    // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function _quoteVolatilePairLiquidity(
        uint256 _amountA,
        uint256 _reserveA,
        uint256 _reserveB
    ) internal pure returns (uint256 amountB) {
        require(_amountA > 0, "Router: INSUFFICIENT_AMOUNT");
        require(_reserveA > 0 && _reserveB > 0, "Router: INSUFFICIENT_LIQUIDITY");
        amountB = (_amountA * _reserveB) / _reserveA;
    }

    // fetches and sorts the reserves for a pair
    function _getVolatilePairReserves(address _tokenA, address _tokenB) internal view returns (uint256 _reserveA, uint256 _reserveB) {
        (address _token0, ) = sortTokens(_tokenA, _tokenB);
        (uint256 _reserve0, uint256 _reserve1, ) = IVolatilePair(_pairFor(_tokenA, _tokenB, false)).getUint256Reserves();
        (_reserveA, _reserveB) = _tokenA == _token0 ? (_reserve0, _reserve1) : (_reserve1, _reserve0);
    }

    receive() external payable {
        assert(msg.sender == address(weth)); // only accept ETH via fallback from the WETH contract
    }
}

contracts/StablePair.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

// import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
// import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
// import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
// import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./utils/SwapUtils.sol";
import "./utils/AmplificationUtils.sol";
import "./utils/ERC20Call.sol";
import "./interface/IERC20.sol";
import "./interface/IPair.sol";
import "./interface/IPairFactory.sol";

/**
 * @title Swap - A StableSwap implementation in solidity.
 * @notice This contract is responsible for custody of closely pegged assets (eg. group of stablecoins)
 * and automatic market making system. Users become an LP (Liquidity Provider) by depositing their tokens
 * in desired ratios for an exchange of the pool token that represents their share of the pool.
 * Users can burn pool tokens and withdraw their share of token(s).
 *
 * Each time a swap between the pooled tokens happens, a set fee incurs which effectively gets
 * distributed to the LPs.
 *
 * In case of emergencies, admin can pause additional deposits, swaps, or single-asset withdraws - which
 * stops the ratio of the tokens in the pool from changing.
 * Users can always withdraw their tokens via multi-asset withdraws.
 *
 * @dev Most of the logic is stored as a library `SwapUtils` for the sake of reducing contract's
 * deployment size.
 */
contract StablePair is Initializable, IERC20, IPair {
    using SwapUtils for SwapUtils.Swap;
    using AmplificationUtils for SwapUtils.Swap;
    using ERC20Call for address;

    address public factory;

    bool public stable;

    string public name;
    string public symbol;
    uint8 public constant decimals = 18;

    bytes32 public DOMAIN_SEPARATOR;
    // keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
    bytes32 public constant PERMIT_TYPEHASH = 0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
    mapping(address => uint256) public nonces;

    uint256 private unlocked_ = 1;

    // Struct storing data responsible for automatic market maker functionalities. In order to
    // access this data, this contract uses SwapUtils library. For more details, see SwapUtils.sol
    SwapUtils.Swap public swapStorage;

    // Maps token address to an index in the pool. Used to prevent duplicate tokens in the pool.
    // getTokenIndex function also relies on this mapping to retrieve token index.
    mapping(address => uint8) internal tokenIndexes_;

    /*** EVENTS ***/

    // events replicated from SwapUtils to make the ABI easier for dumb
    // clients
    event TokenSwap(
        address indexed buyer,
        uint256 tokensSold,
        uint256 tokensBought,
        uint128 soldId,
        uint128 boughtId
    );
    event AddLiquidity(
        address indexed provider,
        uint256[] tokenAmounts,
        uint256[] fees,
        uint256 invariant,
        uint256 lpTokenSupply
    );
    event RemoveLiquidity(
        address indexed provider,
        uint256[] tokenAmounts,
        uint256 lpTokenSupply
    );
    event RemoveLiquidityOne(
        address indexed provider,
        uint256 lpTokenAmount,
        uint256 lpTokenSupply,
        uint256 boughtId,
        uint256 tokensBought
    );
    event RemoveLiquidityImbalance(
        address indexed provider,
        uint256[] tokenAmounts,
        uint256[] fees,
        uint256 invariant,
        uint256 lpTokenSupply
    );
    event NewAdminFee(uint256 newAdminFee);
    event NewSwapFee(uint256 newSwapFee);
    event NewWithdrawFee(uint256 newWithdrawFee);
    event RampA(
        uint256 oldA,
        uint256 newA,
        uint256 initialTime,
        uint256 futureTime
    );
    event StopRampA(uint256 currentA, uint256 time);



    function initialize() external initializer {
        factory = msg.sender;
        stable = true;

        (address[] memory _tokens, bytes memory _data, uint256 _adminFeeRate) = IPairFactory(msg.sender).getInitializable();
        string memory _lpTokenName = "DForce Stable AMM - ";
        string memory _lpTokenSymbol = "dAMM-Stable-";
        string memory _separator = "-";
        uint8[] memory _decimals = new uint8[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _decimals[i] = IERC20(_tokens[i]).decimals();
            string memory _tokenSymbol = _tokens[0].callSymbol();
            if (i == _tokens.length - 1)
                _separator = "";
            _lpTokenName = string(abi.encodePacked(_lpTokenName, _tokenSymbol, _separator));
            _lpTokenSymbol = string(abi.encodePacked(_lpTokenSymbol, _tokenSymbol, _separator));
        }

        name = _lpTokenName;
        symbol = _lpTokenSymbol;

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"), keccak256(bytes(name)), keccak256(bytes("1")), block.chainid, address(this))
        );

        (uint256 _a, uint256 _swapFee) = abi.decode(_data, (uint256, uint256));
        __SwapV2_init(_tokens, _decimals, _a, _swapFee, _adminFeeRate);
    }

    /**
     * @notice Initializes this Swap contract with the given parameters.
     * This will also clone a LPToken contract that represents users'
     * LP positions. The owner of LPToken will be this contract - which means
     * only this contract is allowed to mint/burn tokens.
     *
     * @param _pooledTokens an array of ERC20s this pool will accept
     * @param _decimals the decimals to use for each pooled token,
     * eg 8 for WBTC. Cannot be larger than POOL_PRECISION_DECIMALS
     * @param _a the amplification coefficient * n * (n - 1). See the
     * StableSwap paper for details
     * @param _fee default swap fee to be initialized with
     * @param _adminFee default adminFee to be initialized with
     */
    function __SwapV2_init(
        address[] memory _pooledTokens,
        uint8[] memory _decimals,
        uint256 _a,
        uint256 _fee,
        uint256 _adminFee
    ) internal virtual {
        // Check _pooledTokens and precisions parameter
        require(_pooledTokens.length > 1, "_pooledTokens.length <= 1");
        require(_pooledTokens.length <= 32, "_pooledTokens.length > 32");
        require(
            _pooledTokens.length == _decimals.length,
            "_pooledTokens decimals mismatch"
        );

        uint256[] memory _precisionMultipliers = new uint256[](_decimals.length);

        for (uint8 i = 0; i < _pooledTokens.length; i++) {
            if (i > 0) {
                // Check if index is already used. Check if 0th element is a duplicate.
                require(
                    tokenIndexes_[_pooledTokens[i]] == 0 &&
                        _pooledTokens[0] != _pooledTokens[i],
                    "Duplicate tokens"
                );
            }
            require(
                _pooledTokens[i] != address(0),
                "The 0 address isn't an ERC-20"
            );
            require(
                _decimals[i] <= SwapUtils.POOL_PRECISION_DECIMALS,
                "Token decimals exceeds max"
            );
            _precisionMultipliers[i] =
                10 **
                    (uint256(SwapUtils.POOL_PRECISION_DECIMALS) -
                        uint256(_decimals[i]));
            tokenIndexes_[_pooledTokens[i]] = i;
        }

        // Check _a, _fee, _adminFee, _withdrawFee parameters
        require(_a < AmplificationUtils.MAX_A, "_a exceeds maximum");
        require(_fee < SwapUtils.MAX_SWAP_FEE, "_fee exceeds maximum");
        require(
            _adminFee < SwapUtils.MAX_ADMIN_FEE,
            "_adminFee exceeds maximum"
        );

        swapStorage.pooledTokens = _pooledTokens;
        swapStorage.tokenPrecisionMultipliers = _precisionMultipliers;
        swapStorage.balances = new uint256[](_pooledTokens.length);
        swapStorage.initialA = _a * AmplificationUtils.A_PRECISION;
        swapStorage.futureA = _a * AmplificationUtils.A_PRECISION;
        // swapStorage.initialATime = 0;
        // swapStorage.futureATime = 0;
        swapStorage.swapFee = _fee;
        swapStorage.adminFee = _adminFee;
    }

    /*** MODIFIERS ***/

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyManager() {
        require(factory == msg.sender || IPairFactory(factory).manager() == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    modifier lock() {
        require(unlocked_ == 1, "Pair: LOCKED");
        unlocked_ = 0;
        _;
        unlocked_ = 1;
    }

    /*** VIEW FUNCTIONS ***/

    /**
     * @notice Return A, the amplification coefficient * n * (n - 1)
     * @dev See the StableSwap paper for details
     * @return A parameter
     */
    function getA() external view virtual returns (uint256) {
        return swapStorage.getA();
    }

    /**
     * @notice Return A in its raw precision form
     * @dev See the StableSwap paper for details
     * @return A parameter in its raw precision form
     */
    function getAPrecise() external view virtual returns (uint256) {
        return swapStorage.getAPrecise();
    }

    /**
     * @notice Return address of the pooled token at given index. Reverts if tokenIndex is out of range.
     * @param _index the index of the token
     * @return address of the token at given index
     */
    function getToken(uint8 _index) public view virtual returns (address) {
        require(_index < swapStorage.pooledTokens.length, "Out of range");
        return swapStorage.pooledTokens[_index];
    }

    function tokens() external view returns (address[] memory) {
        return swapStorage.pooledTokens;
    }

    /**
     * @notice Return the index of the given token address. Reverts if no matching
     * token is found.
     * @param _tokenAddress address of the token
     * @return the index of the given token address
     */
    function getTokenIndex(address _tokenAddress)
        public
        view
        virtual
        returns (uint8)
    {
        uint8 _index = tokenIndexes_[_tokenAddress];
        require(
            address(getToken(_index)) == _tokenAddress,
            "Token does not exist"
        );
        return _index;
    }

    /**
     * @notice Return current balance of the pooled token at given index
     * @param _index the index of the token
     * @return current balance of the pooled token at given index with token's native precision
     */
    function getTokenBalance(uint8 _index)
        external
        view
        virtual
        returns (uint256)
    {
        require(_index < swapStorage.pooledTokens.length, "Index out of range");
        return swapStorage.balances[_index];
    }

    // function getUint256Reserves()
    //     public
    //     view
    //     override
    //     returns (
    //         uint256 _reserve0,
    //         uint256 _reserve1,
    //         uint256 _blockTimestampLast
    //     )
    // {
    //     (_reserve0, _reserve1, _blockTimestampLast) = (reserve0, reserve1, blockTimestampLast);
    // }

    /**
     * @notice Get the virtual price, to help calculate profit
     * @return the virtual price, scaled to the POOL_PRECISION_DECIMALS
     */
    function getVirtualPrice() external view virtual returns (uint256) {
        return swapStorage.getVirtualPrice();
    }

    /**
     * @notice Calculate amount of tokens you receive on swap
     * @param _tokenIndexFrom the token the user wants to sell
     * @param _tokenIndexTo the token the user wants to buy
     * @param _dx the amount of tokens the user wants to sell. If the token charges
     * a fee on transfers, use the amount that gets transferred after the fee.
     * @return amount of tokens the user will receive
     */
    function calculateSwap(
        uint8 _tokenIndexFrom,
        uint8 _tokenIndexTo,
        uint256 _dx
    ) external view virtual returns (uint256) {
        return swapStorage.calculateSwap(_tokenIndexFrom, _tokenIndexTo, _dx);
    }

    function getAmountOut(
        address _from,
        address _to,
        uint256 _amount
    ) external view returns (uint256) {
        return swapStorage.calculateSwap(tokenIndexes_[_from], tokenIndexes_[_to], _amount);
    }

    /**
     * @notice A simple method to calculate prices from deposits or
     * withdrawals, excluding fees but including slippage. This is
     * helpful as an input into the various "min" parameters on calls
     * to fight front-running
     *
     * @dev This shouldn't be used outside frontends for user estimates.
     *
     * @param _amounts an array of token amounts to deposit or withdrawal,
     * corresponding to pooledTokens. The amount should be in each
     * pooled token's native precision. If a token charges a fee on transfers,
     * use the amount that gets transferred after the fee.
     * @param _deposit whether this is a deposit or a withdrawal
     * @return token amount the user will receive
     */
    function calculateTokenAmount(uint256[] calldata _amounts, bool _deposit)
        external
        view
        virtual
        returns (uint256)
    {
        return swapStorage.calculateTokenAmount(_amounts, _deposit);
    }

    function calculateTokenAmount(address[] calldata _tokens, uint256[] calldata _amounts, bool _deposit)
        external
        view
        virtual
        returns (uint256)
    {
        uint256[] memory _actualAmounts = new uint256[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _actualAmounts[(tokenIndexes_[_tokens[i]])] = _amounts[i];
        }
        return swapStorage.calculateTokenAmount(_actualAmounts, _deposit);
    }

    /**
     * @notice A simple method to calculate amount of each underlying
     * tokens that is returned upon burning given amount of LP tokens
     * @param _amount the amount of LP tokens that would be burned on withdrawal
     * @return array of token balances that the user will receive
     */
    function calculateRemoveLiquidity(uint256 _amount)
        external
        view
        virtual
        returns (uint256[] memory)
    {
        return swapStorage.calculateRemoveLiquidity(_amount);
    }

    /**
     * @notice Calculate the amount of underlying token available to withdraw
     * when withdrawing via only single token
     * @param _tokenAmount the amount of LP token to burn
     * @param _tokenIndex index of which token will be withdrawn
     * @return calculated amount of underlying token
     * available to withdraw
     */
    function calculateRemoveLiquidityOneToken(
        uint256 _tokenAmount,
        uint8 _tokenIndex
    ) external view virtual returns (uint256) {
        return swapStorage.calculateWithdrawOneToken(_tokenAmount, _tokenIndex);
    }

    /**
     * @notice This function reads the accumulated amount of admin fees of the token with given index
     * @param _index Index of the pooled token
     * @return admin's token balance in the token's precision
     */
    function getAdminBalance(uint256 _index)
        external
        view
        virtual
        returns (uint256)
    {
        return swapStorage.getAdminBalance(_index);
    }

    /*** STATE MODIFYING FUNCTIONS ***/

    function swap(
        address _from,
        address _to,
        uint256 _amountOut,
        address _receiver,
        bytes calldata _data
    ) external lock {
        _data;
        swapStorage.swap(tokenIndexes_[_from], tokenIndexes_[_to], _amountOut, _receiver);
    }

    function mint(address _to) external lock returns (uint256) {
        return swapStorage.addLiquidity(_to);
    }

    function burn(address _to) external lock returns (uint256[] memory) {
        return swapStorage.removeLiquidity(_to);
    }

    /*** ADMIN FUNCTIONS ***/

    /**
     * @notice Withdraw all admin fees to the contract owner
     */
    function claimFees() external returns (uint256[] memory) {
        return swapStorage.withdrawAdminFees(IPairFactory(factory).manager());
    }

    /**
     * @notice Update the admin fee. Admin fee takes portion of the swap fee.
     * @param _newAdminFee new admin fee to be applied on future transactions
     */
    function setAdminFeeRate(uint256 _newAdminFee) external onlyManager {
        swapStorage.setAdminFee(_newAdminFee);
    }

    /**
     * @notice Update the swap fee to be applied on swaps
     * @param _newSwapFee new swap fee to be applied on future transactions
     */
    function setSwapFee(uint256 _newSwapFee) external payable onlyManager {
        swapStorage.setSwapFee(_newSwapFee);
    }

    /**
     * @notice Start ramping up or down A parameter towards given futureA and futureTime
     * Checks if the change is too rapid, and commits the new A value only when it falls under
     * the limit range.
     * @param _futureA the new A to ramp towards
     * @param _futureTime timestamp when the new A should be reached
     */
    function rampA(uint256 _futureA, uint256 _futureTime)
        external
        payable
        onlyManager
    {
        swapStorage.rampA(_futureA, _futureTime);
    }

    /**
     * @notice Stop ramping A immediately. Reverts if ramp A is already stopped.
     */
    function stopRampA() external payable onlyManager {
        swapStorage.stopRampA();
    }

    /*** ERC20 FUNCTIONS ***/

    function allowance(address _owner, address _spender) external view returns (uint256) {
        return swapStorage.allowance[_owner][_spender];
    }

    function balanceOf(address _owner) external view returns (uint256) {
        return swapStorage.balanceOf[_owner];
    }

    function totalSupply() external view returns (uint256) {
        return swapStorage.totalSupply;
    }

    function approve(address _spender, uint256 _value) external returns (bool) {
        swapStorage._approve(msg.sender, _spender, _value);
        return true;
    }

    function transfer(address _to, uint256 _value) external returns (bool) {
        swapStorage._transfer(msg.sender, _to, _value);
        return true;
    }

    function transferFrom(
        address _from,
        address _to,
        uint256 _value
    ) external returns (bool) {
        swapStorage._spendAllowance(_from, _to, _value);
        swapStorage._transfer(_from, _to, _value);
        return true;
    }

    function permit(
        address _owner,
        address _spender,
        uint256 _value,
        uint256 _deadline,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external {
        require(_deadline >= block.timestamp, "PairERC20: EXPIRED");
        bytes32 _digest = keccak256(abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, keccak256(abi.encode(PERMIT_TYPEHASH, _owner, _spender, _value, nonces[_owner]++, _deadline))));
        address _recoveredAddress = ecrecover(_digest, _v, _r, _s);
        require(_recoveredAddress != address(0) && _recoveredAddress == _owner, "PairERC20: INVALID_SIGNATURE");
        swapStorage._approve(_owner, _spender, _value);
    }
}

