contracts/interface/IPair.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

interface IPair {
    function getAmountOut(address from, address to, uint256 amount) external view returns (uint256);

    function tokens() external returns (address[] memory);

    function initialize(address[] memory _tokens, bytes memory _data) external;

    function PAIR_TYPE() external view returns (uint8);

    function AUTH() external view returns (bool);
}

interface IVolatilePair is IPair {
    function mint(address to) external returns (uint256 liquidity);

    function burn(address to) external returns (uint256 amount0, uint256 amount1);

    function swap(uint256 amount0Out, uint256 amount1Out, address to, bytes calldata data) external;

    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

interface IStablePair is IPair {
    function calculateTokenAmount(address[] calldata _tokens, uint256[] calldata _amounts, bool _deposit) external view returns (uint256);

    function calculateRemoveLiquidityOneToken(address _token, uint256 _liquidity) external view returns (uint256);

    function calculateRemoveLiquidity(address[] calldata _tokens, uint256 _amount) external view returns (uint256[] memory);

    function addLiquidity(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        uint256 _minToMint,
        address _receiver,
        uint256 _deadline
    ) external payable returns (uint256);

    function removeLiquidity(
        uint256 _amount,
        uint256[] calldata _minAmounts,
        address _receiver,
        uint256 _deadline
    ) external payable returns (uint256[] memory);

    function removeLiquidityOneToken(
        uint256 _tokenAmount,
        address _token,
        uint256 _minAmount,
        address _receiver,
        uint256 _deadline
    ) external returns (uint256);

    function removeLiquidityImbalance(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _receiver,
        uint256 _deadline
    ) external returns (uint256);

    function lpToken() external view returns (address);

    function swap(
        address _tokenFrom,
        address _tokenTo,
        uint256 _dx,
        uint256 _minDy,
        address _receiver,
        uint256 _deadline
    ) external returns (uint256);
}

