contracts/Router.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import "./interface/IWETH.sol";
import "./interface/IPair.sol";
import "./interface/IPairRouter.sol";
import "./interface/IPairERC20.sol";
import "./interface/IPairFactory.sol";

import "./utils/Arrays.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "hardhat/console.sol";

contract Router is Initializable {
    using Arrays for address[];
    using Address for address;

    struct Route {
        address from;
        address to;
        address pair;
    }

    address public factory;
    IWETH public weth;
    mapping(uint8 => address) public pairTypes;

    event SetPairTypes(uint8 pairType, address router);

    modifier onlyManager() {
        require(msg.sender == IPairFactory(factory).manager(), "not manager");
        _;
    }

    function initialize(address _factory, address _weth) public initializer {
        factory = _factory;
        weth = IWETH(_weth);
    }

    constructor(address _factory, address _weth) {
        initialize(_factory, _weth);
    }

    function setPairTypes(uint8 _pairType, address _router) external onlyManager {
        pairTypes[_pairType] = _router;
        emit SetPairTypes(_pairType, _router);
    }

    function quoteAddLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view returns (uint256[] memory _amountIn, uint256 liquidity) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteAddLiquidity.selector, _tokens, _amountDesireds)
        );
        (_amountIn, liquidity) = abi.decode(_returns, (uint256[], uint256));
    }

    function quoteRemoveLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity
    ) external view returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteRemoveLiquidity.selector, _tokens, _liquidity)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function addLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory _amounts, uint256 _liquidity) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IPairRouter.addLiquidity.selector, _tokens, _amountDesireds, _amountsMin, _minLiquidity, _to, _deadline)
        );
        (_amounts, _liquidity) = abi.decode(_returns, (uint256[], uint256));
    }

    function removeLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IPairRouter.removeLiquidity.selector, _tokens, _liquidity, _amountsMin, _to, _deadline)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function removeLiquidityETH(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IPairRouter.removeLiquidityETH.selector, _tokens, _liquidity, _amountsMin, _to, _deadline)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function removeLiquidityWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(
                IPairRouter.removeLiquidityWithPermit.selector,
                _tokens,
                _liquidity,
                _amountsMin,
                _to,
                _deadline,
                _approveMax,
                _v,
                _r,
                _s
            )
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function removeLiquidityETHWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(
                IPairRouter.removeLiquidityETHWithPermit.selector,
                _tokens,
                _liquidity,
                _amountsMin,
                _to,
                _deadline,
                _approveMax,
                _v,
                _r,
                _s
            )
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function quoteRemoveLiquidityOneToken(
        uint8 _pairType,
        address[] calldata _tokens,
        address _token,
        uint256 _liquidity
    ) external view returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityOneToken.selector, _tokens, _token, _liquidity)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function quoteRemoveLiquidityImbalance(
        uint8 _pairType,
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external view returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityImbalance.selector, _tokens, _amounts)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function removeLiquidityOneToken(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(
                IStablPairRouter.removeLiquidityOneToken.selector,
                _tokens,
                _liquidity,
                _token,
                _minAmount,
                _to,
                _deadline
            )
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function removeLiquidityImbalance(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IStablPairRouter.removeLiquidityImbalance.selector, _tokens, _amounts, _maxBurnAmount, _to, _deadline)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function swap(
        Route[] memory _routes,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amountOut) {
        for (uint i = 0; i < _routes.length; i++) {
            uint8 _pairType = IPair(_routes[i].pair).PAIR_TYPE();
            address _router = pairTypes[_pairType];
            bytes memory _returns = _router.functionDelegateCall(
                abi.encodeWithSelector(IPairRouter.swap.selector, _routes[i], _amountIn, _amountOutMin, _to, _deadline)
            );
            _amountIn = abi.decode(_returns, (uint256));
        }
        _amountOut = _amountIn;
    }

    function getAmountsOut(uint256 _amountIn, Route[] memory _routes) public view returns (uint256 _amountsOut) {
        require(_routes.length >= 1, "Router: INVALID_PATH");
        _amountsOut = _amountIn;
        for (uint256 i = 0; i < _routes.length; i++) {
            _amountsOut = IPair(_routes[i].pair).getAmountOut(_routes[i].from, _routes[i].to, _amountsOut);
        }
    }

    function pairFor(address[] memory _tokens, uint8 _type) external view returns (address _pair, bool _has) {
        _pair = IPairFactory(factory).getPairAddress(_tokens, _type);
        _has = IPairFactory(factory).isPair(_pair);
    }

    ///@dev only volatiole pair
    function getReserves(address[] memory _tokens) external view returns (uint256 reserveA, uint256 reserveB) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, 1);
        if (IPairFactory(factory).isPair(_pair)) return IVolatileRouter(pairTypes[1]).getReserves(_pair, _tokens[0], _tokens[1]);
    }

    receive() external payable {
        assert(msg.sender == address(weth)); // only accept ETH via fallback from the WETH contract
    }
}

contracts/routers/StableRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "../interface/IPair.sol";
import "../interface/IWETH.sol";
import "../interface/IPairRouter.sol";
import "../interface/IPairFactory.sol";
import "../interface/IPairERC20.sol";
import "../utils/Arrays.sol";

contract StableRouter is IStablPairRouter {
    using Arrays for address[];

    uint8 public constant PAIR_TYPE = 2;

    address public immutable factory;
    IWETH public immutable weth;

    modifier ensure(uint256 _deadline) {
        require(_deadline >= block.timestamp, "StableRouter: EXPIRED");
        _;
    }

    constructor(address _factory, address _weth) {
        factory = _factory;
        weth = IWETH(_weth);
    }

    function quoteAddLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view override returns (uint256[] memory _amountIn, uint256 _liquidity) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        bool _isPair = IPairFactory(factory).isPair(_pair);
        if (_isPair) {
            _amountIn = _amountDesireds;
            _liquidity = IStablePair(_pair).calculateTokenAmount(_tokens, _amountDesireds, true);
        }
    }

    function quoteRemoveLiquidity(address[] memory _tokens, uint256 _liquidity) external view override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (IPairFactory(factory).isPair(_pair)) _amounts = IStablePair(_pair).calculateRemoveLiquidity(_tokens, _liquidity);
    }

    function quoteRemoveLiquidityOneToken(
        address[] calldata _tokens,
        address _token,
        uint256 _liquidity
    ) external view override returns (uint256 _amount) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (IPairFactory(factory).isPair(_pair)) _amount = IStablePair(_pair).calculateRemoveLiquidityOneToken(_token, _liquidity);
    }

    function quoteRemoveLiquidityImbalance(
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external view override returns (uint256 _liquidity) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (IPairFactory(factory).isPair(_pair)) _liquidity = IStablePair(_pair).calculateTokenAmount(_tokens, _amounts, false) + 1;
    }

    function addLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: is not pair");
        _amounts = _amountDesireds;
        for (uint256 i = 0; i < _tokens.length; i++) {
            _safeTransferFrom(_tokens[i], msg.sender, address(this), _amounts[i]);
            _safeApprove(_tokens[i], _pair, _amounts[i]);
        }
        _liquidity = IStablePair(_pair).addLiquidity(_tokens, _amounts, _minLiquidity, _to, _deadline);
    }

    /// @param _tokens The last one is the token of weth
    /// @param _amountDesireds The last one is the amount of weth, And the array length is one bit longer than _tokens
    /// @param _amountsMins only minLiquidity
    function addLiquidityETH(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMins,
        address _to,
        uint256 _deadline
    ) external payable override returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = new uint256[](2);
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: is not pair");
        _amounts = _amountDesireds;
        weth.deposit{ value: _amounts[_amounts.length - 1] }();
        for (uint256 i = 0; i < _tokens.length; i++) {
            _safeTransferFrom(_tokens[i], msg.sender, address(this), _amounts[i]);
            _safeApprove(_tokens[i], _pair, _amounts[i]);
        }
        _liquidity = IStablePair(_pair).addLiquidity(_tokens, _amounts, _amountsMins[0], _to, _deadline);
        if (msg.value > _amounts[_amounts.length - 1]) _safeTransferETH(msg.sender, msg.value - _amounts[_amounts.length - 1]);
    }

    function removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: pair doesn't exist"); // send liquidity to pair

        address _lpToken = IStablePair(_pair).lpToken();
        _safeTransferFrom(_lpToken, msg.sender, address(this), _liquidity);
        _safeApprove(_lpToken, _pair, _liquidity);

        _amounts = IStablePair(_pair).removeLiquidity(_liquidity, _amountsMin, _to, _deadline);
    }

    /// @param _tokens The last one is the token of weth
    /// @param _amountsMin Minimum return value of each token
    function removeLiquidityETH(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: pair doesn't exist"); // send liquidity to pair

        _amounts = removeLiquidity(_tokens, _liquidity, _amountsMin, _to, _deadline);

        uint256 _ethAmount = _amounts[_amounts.length - 1];
        weth.withdraw(_ethAmount);
        _safeTransferETH(msg.sender, _ethAmount);
    }

    function removeLiquidityWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        {
            uint256 _value = _approveMax ? type(uint256).max : _liquidity;
            address _lpToken = IStablePair(_pair).lpToken();
            IPairERC20(_lpToken).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        }

        _amounts = removeLiquidity(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    function removeLiquidityETHWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        {
            uint256 _value = _approveMax ? type(uint256).max : _liquidity;
            address _lpToken = IStablePair(_pair).lpToken();
            IPairERC20(_lpToken).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        }

        _amounts = removeLiquidityETH(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    function removeLiquidityOneToken(
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) external override returns (uint256 _amount) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        _amount = IStablePair(_pair).removeLiquidityOneToken(_liquidity, _token, _minAmount, _to, _deadline);
    }

    function removeLiquidityImbalance(
        address[] memory _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) external override returns (uint256 _amount) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        _amount = IStablePair(_pair).removeLiquidityImbalance(_tokens, _amounts, _maxBurnAmount, _to, _deadline);
    }

    function swap(
        Route memory _route,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external override returns (uint256 _amountOut) {
        require(IPairFactory(factory).isPair(_route.pair), "StableRouter: is not pair");

        _safeTransferFrom(_route.from, msg.sender, address(this), _amountIn);
        _safeApprove(_route.from, _route.pair, _amountIn);

        return IStablePair(_route.pair).swap(_route.from, _route.to, _amountIn, _amountOutMin, _to, _deadline);
    }

    function _safeTransferFrom(address _token, address _from, address _to, uint256 _value) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IPairERC20.transferFrom.selector, _from, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function _safeApprove(address token, address to, uint256 amount) internal {
        (bool success, bytes memory returndata) = token.call(abi.encodeWithSelector(IPairERC20(token).approve.selector, to, amount));

        if (!success || (returndata.length > 0 && !abi.decode(returndata, (bool)))) {
            _callOptionalReturn(token, abi.encodeWithSelector(IPairERC20(token).approve.selector, to, 0));
            _callOptionalReturn(token, abi.encodeWithSelector(IPairERC20(token).approve.selector, to, amount));
        }
    }

    function _callOptionalReturn(address token, bytes memory data) private {
        (bool success, bytes memory result) = token.call(data);
        require(success, "StablePair: Low-level call failed");

        if (result.length > 0) {
            require(abi.decode(result, (bool)), "StablePair: ERC20 operation did not succeed");
        }
    }

    function _safeTransferETH(address _to, uint256 _value) internal {
        (bool _success, ) = _to.call{ value: _value }(new bytes(0));
        require(_success, "TransferHelper: ETH_TRANSFER_FAILED");
    }
}

contracts/routers/VolatileRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "../interface/IPair.sol";
import "../interface/IWETH.sol";
import "../interface/IPairRouter.sol";
import "../interface/IPairFactory.sol";
import "../interface/IPairERC20.sol";
import "../utils/Arrays.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract VolatileRouter is IPairRouter {
    using Arrays for address[];

    uint8 public constant PAIR_TYPE = 1;
    uint256 internal constant MINIMUM_LIQUIDITY = 10 ** 3;

    address public immutable factory;
    IWETH public immutable weth;

    modifier ensure(uint256 _deadline) {
        require(_deadline >= block.timestamp, "Router: EXPIRED");
        _;
    }

    constructor(address _factory, address _weth) {
        factory = _factory;
        weth = IWETH(_weth);
    }

    function quoteAddLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view override returns (uint256[] memory _amountIn, uint256 liquidity) {
        // create the pair if it doesn't exist yet
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (!IPairFactory(factory).isPair(_pair)) {
            return (_amountIn, liquidity);
        }
        (uint256 reserveA, uint256 reserveB) = (0, 0);
        uint256 _totalSupply = 0;
        if (_pair != address(0)) {
            _totalSupply = IPairERC20(_pair).totalSupply();
            (reserveA, reserveB) = getReserves(_pair, _tokens[0], _tokens[1]);
        }
        (uint256 amountA, uint256 amountB) = (0, 0);
        (uint256 amountADesired, uint256 amountBDesired) = (_amountDesireds[0], _amountDesireds[1]);
        if (reserveA == 0 && reserveB == 0) {
            (amountA, amountB) = (amountADesired, amountBDesired);
            liquidity = Math.sqrt(amountA * amountB) - MINIMUM_LIQUIDITY;
        } else {
            uint256 amountBOptimal = _quoteLiquidity(amountADesired, reserveA, reserveB);
            if (amountBOptimal <= amountBDesired) {
                (amountA, amountB) = (amountADesired, amountBOptimal);
                liquidity = Math.min((amountA * _totalSupply) / reserveA, (amountB * _totalSupply) / reserveB);
            } else {
                uint256 amountAOptimal = _quoteLiquidity(amountBDesired, reserveB, reserveA);
                (amountA, amountB) = (amountAOptimal, amountBDesired);
                liquidity = Math.min((amountA * _totalSupply) / reserveA, (amountB * _totalSupply) / reserveB);
            }
        }
        _amountIn = new uint256[](2);
        (_amountIn[0], _amountIn[1]) = (amountA, amountB);
    }

    function quoteRemoveLiquidity(address[] memory _tokens, uint256 _liquidity) external view override returns (uint256[] memory _amounts) {
        // create the pair if it doesn't exist yet
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);

        if (!IPairFactory(factory).isPair(_pair)) {
            return _amounts;
        }

        (uint256 _reserveA, uint256 _reserveB) = getReserves(_pair, _tokens[0], _tokens[1]);
        uint256 _totalSupply = IPairERC20(_pair).totalSupply();

        _amounts = new uint256[](2);

        _amounts[0] = (_liquidity * _reserveA) / _totalSupply; // using balances ensures pro-rata distribution
        _amounts[1] = (_liquidity * _reserveB) / _totalSupply; // using balances ensures pro-rata distribution
    }

    function addLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = _amountDesireds;
        (_amounts[0], _amounts[1]) = _addLiquidity(
            _tokens[0],
            _tokens[1],
            _amountDesireds[0],
            _amountDesireds[1],
            _amountsMin[0],
            _amountsMin[1]
        );
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _safeTransferFrom(_tokens[i], msg.sender, _pair, _amounts[i]);
        }
        _liquidity = IVolatilePair(_pair).mint(_to);
        require(_liquidity >= _minLiquidity, "Couldn't mint min requested");
    }

    function _addLiquidity(
        address _tokenA,
        address _tokenB,
        uint256 _amountADesired,
        uint256 _amountBDesired,
        uint256 _amountAMin,
        uint256 _amountBMin
    ) internal returns (uint256 _amountA, uint256 _amountB) {
        require(_amountADesired >= _amountAMin);
        require(_amountBDesired >= _amountBMin);
        // create the pair if it doesn't exist yet
        (uint256 _reserveA, uint256 _reserveB) = (0, 0);
        address _pair = getPair(_tokenA, _tokenB);
        if (!IPairFactory(factory).isPair(_pair)) {
            address[] memory _tokens = new address[](2);
            _tokens[0] = _tokenA;
            _tokens[1] = _tokenB;
            _pair = IPairFactory(factory).createPair(_tokens, PAIR_TYPE, new bytes(0));
        } else {
            (_reserveA, _reserveB) = getReserves(_pair, _tokenA, _tokenB);
        }

        require(_pair != address(0), "Router: not have this pair");
        if (_reserveA == 0 && _reserveB == 0) {
            (_amountA, _amountB) = (_amountADesired, _amountBDesired);
        } else {
            uint256 _amountBOptimal = _quoteLiquidity(_amountADesired, _reserveA, _reserveB);
            if (_amountBOptimal <= _amountBDesired) {
                require(_amountBOptimal >= _amountBMin, "Router: INSUFFICIENT_B_AMOUNT");
                (_amountA, _amountB) = (_amountADesired, _amountBOptimal);
            } else {
                uint256 _amountAOptimal = _quoteLiquidity(_amountBDesired, _reserveB, _reserveA);
                assert(_amountAOptimal <= _amountADesired);
                require(_amountAOptimal >= _amountAMin, "Router: INSUFFICIENT_A_AMOUNT");
                (_amountA, _amountB) = (_amountAOptimal, _amountBDesired);
            }
        }
    }

    /// @param _tokens only other token
    /// @param _amountDesireds only other token
    /// @param _amountMins 0: other token, 1: weth
    /// @return _amounts 0: other token, 1: weth
    function addLiquidityETH(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountMins,
        address _to,
        uint256 _deadline
    ) external payable override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = new uint256[](2);
        (_amounts[0], _amounts[1]) = _addLiquidity(
            _tokens[0],
            address(weth),
            _amountDesireds[0],
            msg.value,
            _amountMins[0],
            _amountMins[1]
        );
        address _pair = getPair(_tokens[0], address(weth));
        _safeTransferFrom(_tokens[0], msg.sender, _pair, _amounts[0]);
        weth.deposit{ value: _amounts[1] }();
        assert(weth.transfer(_pair, _amounts[1]));
        _liquidity = IVolatilePair(_pair).mint(_to);
        // refund dust eth, if any
        if (msg.value > _amounts[1]) _safeTransferETH(msg.sender, msg.value - _amounts[1]);
    }

    function removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override ensure(_deadline) returns (uint256[] memory _amounts) {
        return _removeLiquidity(_tokens[0], _tokens[1], _liquidity, _amountsMin, _to);
    }

    function _removeLiquidity(
        address _token0,
        address _token1,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to
    ) internal returns (uint256[] memory _amounts) {
        address _pair = getPair(_token0, _token1);
        require(IPairFactory(factory).isPair(_pair), "Router: pair doesn't exist"); // send liquidity to pair
        IPairERC20(_pair).transferFrom(msg.sender, _pair, _liquidity);
        (uint256 _amount0, uint256 _amount1) = IVolatilePair(_pair).burn(_to);
        _amounts = new uint256[](2);
        (_amounts[0], _amounts[1]) = (_amount0, _amount1);

        for (uint256 i = 0; i < _amounts.length; i++) {
            require(_amounts[i] >= _amountsMin[i], "Router: INSUFFICIENT_A_AMOUNT");
        }
    }

    /// @param _tokens only other token
    /// @param _amountsMin 0: other token amount, 1: eth amount
    function removeLiquidityETH(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override ensure(_deadline) returns (uint256[] memory _amounts) {
        _amounts = _removeLiquidity(_tokens[0], address(weth), _liquidity, _amountsMin, address(this));
        _safeTransfer(_tokens[0], _to, _amounts[0]);
        weth.withdraw(_amounts[1]);
        _safeTransferETH(_to, _amounts[1]);
    }

    function removeLiquidityWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        {
            uint256 _value = _approveMax ? type(uint256).max : _liquidity;
            IPairERC20(_pair).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        }
        _amounts = removeLiquidity(_tokens, _liquidity, _amountMin, _to, _deadline);
    }

    function removeLiquidityETHWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory) {
        address _pair = getPair(_tokens[0], address(weth));
        uint256 _value = _approveMax ? type(uint256).max : _liquidity;
        IPairERC20(_pair).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        return removeLiquidityETH(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    function swap(
        Route memory _route,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external ensure(_deadline) returns (uint256 _amountOut) {
        address[] memory _tokens = IVolatilePair(_route.pair).tokens();
        _amountOut = IVolatilePair(_route.pair).getAmountOut(_route.from, _route.to, _amountIn);
        require(_amountOut >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");
        (uint256 amount0Out, uint256 amount1Out) = _route.pair == _tokens[0] ? (uint256(0), _amountOut) : (_amountOut, uint256(0));

        ///@dev The delegatecall function of the super router
        _safeTransfer(_route.from, _route.pair, _amountIn);
        IVolatilePair(_route.pair).swap(amount0Out, amount1Out, _to, new bytes(0));
    }

    function _swap(uint256[] memory amounts, address[] memory path, address _to) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0, ) = sortTokens(input, output);
            uint256 amountOut = amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) = input == token0 ? (uint256(0), amountOut) : (amountOut, uint256(0));
            address to = i < path.length - 2 ? getPair(output, path[i + 2]) : _to;
            IVolatilePair(getPair(input, output)).swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }

    function getReserves(address _pair, address tokenA, address tokenB) public view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = IVolatilePair(_pair).getReserves();
        (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
    }

    function sortTokens(address tokenA, address tokenB) public pure returns (address token0, address token1) {
        require(tokenA != tokenB, "Router: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), "Router: ZERO_ADDRESS");
    }

    // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function _quoteLiquidity(uint256 amountA, uint256 reserveA, uint256 reserveB) internal pure returns (uint256 amountB) {
        require(amountA > 0, "Router: INSUFFICIENT_AMOUNT");
        require(reserveA > 0 && reserveB > 0, "Router: INSUFFICIENT_LIQUIDITY");
        amountB = (amountA * reserveB) / reserveA;
    }

    function _safeTransferFrom(address _token, address _from, address _to, uint256 _value) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IPairERC20.transferFrom.selector, _from, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function _safeTransferETH(address _to, uint256 _value) internal {
        (bool _success, ) = _to.call{ value: _value }(new bytes(0));
        require(_success, "TransferHelper: ETH_TRANSFER_FAILED");
    }

    function _safeTransfer(address _token, address _to, uint256 _value) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IPairERC20.transfer.selector, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function getPair(address _tokenA, address _tokenB) internal view returns (address _pair) {
        address[] memory _tokens = new address[](2);
        _tokens[0] = _tokenA;
        _tokens[1] = _tokenB;
        _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
    }

    function getAmountsOut(uint256 _amountIn, Route[] memory _routes) public view returns (uint256[] memory _amounts) {
        require(_routes.length >= 1, "Router: INVALID_PATH");
        _amounts = new uint256[](_routes.length + 1);
        _amounts[0] = _amountIn;
        for (uint256 i = 0; i < _routes.length; i++) {
            if (IPairFactory(factory).isPair(_routes[i].pair)) {
                _amounts[i + 1] = IPair(_routes[i].pair).getAmountOut(_routes[i].from, _routes[i].to, _amounts[i]);
            }
        }
    }
}

contracts/utils/Arrays.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

library Arrays {
    function sortArray(address[] memory arr) internal pure returns (address[] memory) {
        uint256 l = arr.length;
        for (uint256 i = 0; i < l; i++) {
            for (uint256 j = i + 1; j < l; j++) {
                if (arr[i] > arr[j]) {
                    address temp = arr[i];
                    arr[i] = arr[j];
                    arr[j] = temp;
                }
            }
        }
        return arr;
    }

    function copyAndAddOne(address[] memory arr, address _add) internal pure returns (address[] memory _result) {
        _result = new address[](arr.length + 1);
        for (uint256 i = 0; i < arr.length; i++) {
            _result[i] = arr[i];
        }
        _result[arr.length] = _add;
    }

    function removeEmpty(address[] memory arr) internal pure returns (address[] memory _new) {
        uint256 count;
        for (uint256 i = 0; i < arr.length; i++) {
            if (arr[i] != address(0)) {
                count++;
            }
        }
        _new = new address[](count);
        uint256 j;
        for (uint256 i = 0; i < arr.length; i++) {
            if (arr[i] == address(0)) {
                continue;
            }
            _new[j] = arr[i];
            j++;
        }
    }
}

