contracts/PairFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "./utils/Arrays.sol";
import "./interface/IPairFactory.sol";
import { IPair } from "./interface/IPair.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract PairFactory is IPairFactory, Initializable {
    using Arrays for address[];
    using EnumerableSet for EnumerableSet.AddressSet;

    address public manager;
    address public pendingManager;

    uint256 public defSwapFeeRate;
    uint256 public defAdminFeeRate;

    mapping(address => bool) public isPair;
    address[] public allPairs;

    EnumerableSet.AddressSet internal pairImpls;
    mapping(uint8 => PairParams) public pairParams;

    event PairCreated(address[] tokens, uint8 _type, address pair, uint256 pairsAmount);
    event SetDefAdminFeeRate(uint256 _old, uint256 _new);
    event SetDefSwapFeeRate(uint256 _old, uint256 _new);
    event SetPairAdminFeeRate(uint256 _old, uint256 _new);
    event SetPairType(address _impl, uint8 _type, bool _auth, address _old);
    event RemovePairType(address _impl, uint8 _type, bool _auth);

    struct PairTokens {
        address _tokenA;
        address _tokenB;
    }

    modifier onlyManager() {
        require(msg.sender == manager, "not manager");
        _;
    }

    function initialize(uint256 _defSwapFeeRate, uint256 _defAdminFeeRate) public initializer {
        manager = msg.sender;
        defSwapFeeRate = _defSwapFeeRate;
        defAdminFeeRate = _defAdminFeeRate;
    }

    constructor(uint256 _defSwapFeeRate, uint256 _defAdminFeeRate) {
        initialize(_defSwapFeeRate, _defAdminFeeRate);
    }

    function allPairsLength() external view returns (uint256) {
        return allPairs.length;
    }

    function setManager(address _manager) external onlyManager {
        pendingManager = _manager;
    }

    function acceptManager() external {
        require(msg.sender == pendingManager, "not pending fee manager");
        manager = pendingManager;
    }

    function setDefAdminFeeRate(uint256 _defAdminFeeRate) external onlyManager {
        uint256 _old = defAdminFeeRate;
        defAdminFeeRate = _defAdminFeeRate;
        emit SetDefAdminFeeRate(_old, _defAdminFeeRate);
    }

    function setDefSwapFeeRate(uint256 _defSwapFeeRate) external onlyManager {
        uint256 _old = defSwapFeeRate;
        defSwapFeeRate = _defSwapFeeRate;
        emit SetDefSwapFeeRate(_old, _defSwapFeeRate);
    }

    function containsPair(address value) external view returns (bool) {
        return pairImpls.contains(value);
    }

    function setPairType(address _impl) external onlyManager {
        uint8 _type = IPair(_impl).PAIR_TYPE();
        bool _auth = IPair(_impl).AUTH();

        address _old = pairParams[_type]._impl;
        if (_old != address(0)) pairImpls.remove(_old);

        require(pairImpls.add(_impl), "Factory: This pair already exists");
        pairParams[_type] = PairParams({ _impl: _impl, _auth: _auth });

        emit SetPairType(_impl, _type, _auth, _old);
    }

    function addPairType(address _impl) external onlyManager {
        uint8 _type = IPair(_impl).PAIR_TYPE();
        bool _auth = IPair(_impl).AUTH();
        require(pairParams[_type]._impl == address(0), "Factory: This pair type already exists");

        require(pairImpls.add(_impl), "Factory: This pair already exists");
        pairParams[_type] = PairParams({ _impl: _impl, _auth: _auth });

        emit SetPairType(_impl, _type, _auth, address(0));
    }

    function removePairType(address _impl) external onlyManager {
        require(pairImpls.contains(_impl), "Factory: This pair does not exist");

        uint8 _type = IPair(_impl).PAIR_TYPE();

        pairImpls.remove(_impl);
        PairParams memory _old = pairParams[_type];
        delete pairParams[_type];

        emit RemovePairType(_impl, _type, _old._auth);
    }

    function pairTypeValues() external view returns (address[] memory) {
        return pairImpls.values();
    }

    function pairTypeAmount() external view returns (uint256) {
        return pairImpls.length();
    }

    function atPairType(uint256 _index) external view returns (address) {
        return pairImpls.at(_index);
    }

    function getPairAddress(address[] memory _tokens, uint8 _pairType) public view returns (address) {
        bytes32 salt = keccak256(abi.encodePacked(_tokens.sortArray(), _pairType));
        return Clones.predictDeterministicAddress(pairParams[_pairType]._impl, salt);
    }

    function createPair(address[] memory _tokens, uint8 _pairType, bytes memory _data) external returns (address _pair) {
        _tokens = _tokens.sortArray();

        require(!isPair[getPairAddress(_tokens, _pairType)], "Factory: Pair already exists");

        bytes32 salt = keccak256(abi.encodePacked(_tokens, _pairType));

        PairParams memory _params = pairParams[_pairType];
        if (_pairType == 1) {
            _data = abi.encode(defSwapFeeRate, defAdminFeeRate);
        }
        if (_params._auth) {
            require(manager == msg.sender, "Factory: this pair type can only be created by manager");
        }
        _pair = Clones.cloneDeterministic(_params._impl, salt);

        IPair(_pair).initialize(_tokens, _data);

        allPairs.push(_pair);
        isPair[_pair] = true;

        emit PairCreated(_tokens, _pairType, _pair, allPairs.length);
    }

}

