     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgrade(TransparentUpgradeableProxy proxy, address implementation) public virtual onlyOwner {
        proxy.upgradeTo(implementation);
    }

    /**
     * @dev Upgrades `proxy` to `implementation` and calls a function on the new implementation. See
     * {TransparentUpgradeableProxy-upgradeToAndCall}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgradeAndCall(
        TransparentUpgradeableProxy proxy,
        address implementation,
        bytes memory data
    ) public payable virtual onlyOwner {
        proxy.upgradeToAndCall{value: msg.value}(implementation, data);
    }
}

contracts/PairFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "./utils/Arrays.sol";
import "./interface/IPairFactory.sol";
import { IPair } from "./interface/IPair.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract PairFactory is IPairFactory, Initializable {
    using Arrays for address[];
    using EnumerableSet for EnumerableSet.AddressSet;

    uint256 public constant MAX_SWAP_FEE = 10 ** 8;
    uint256 public constant MAX_ADMIN_FEE = 10 ** 10;

    address public manager;
    address public pendingManager;

    uint256 public defSwapFeeRate;
    uint256 public defAdminFeeRate;

    mapping(address => bool) public isPair;
    address[] public allPairs;

    EnumerableSet.AddressSet internal pairImpls;
    mapping(uint8 => PairParams) public pairParams;

    event PairCreated(address[] tokens, uint8 _type, address pair, uint256 pairsAmount);
    event SetDefAdminFeeRate(uint256 _old, uint256 _new);
    event SetDefSwapFeeRate(uint256 _old, uint256 _new);
    event SetPairAdminFeeRate(uint256 _old, uint256 _new);
    event SetPairType(address _impl, uint8 _type, bool _auth, address _old);
    event RemovePairType(address _impl, uint8 _type, bool _auth);

    struct PairTokens {
        address _tokenA;
        address _tokenB;
    }

    modifier onlyManager() {
        require(msg.sender == manager, "not manager");
        _;
    }

    function initialize(uint256 _defSwapFeeRate, uint256 _defAdminFeeRate) public initializer {
        manager = msg.sender;
        setDefSwapFeeRate(_defSwapFeeRate);
        setDefAdminFeeRate(_defAdminFeeRate);
    }

    constructor(uint256 _defSwapFeeRate, uint256 _defAdminFeeRate) {
        initialize(_defSwapFeeRate, _defAdminFeeRate);
    }

    function allPairsLength() external view returns (uint256) {
        return allPairs.length;
    }

    function setManager(address _manager) external onlyManager {
        pendingManager = _manager;
    }

    function acceptManager() external {
        require(msg.sender == pendingManager, "not pending fee manager");
        manager = pendingManager;
    }

    function setDefAdminFeeRate(uint256 _defAdminFeeRate) public onlyManager {
        require(_defAdminFeeRate <= MAX_ADMIN_FEE, "Factory: Over MAX_ADMIN_FEE is not allowed");
        uint256 _old = defAdminFeeRate;
        defAdminFeeRate = _defAdminFeeRate;
        emit SetDefAdminFeeRate(_old, _defAdminFeeRate);
    }

    function setDefSwapFeeRate(uint256 _defSwapFeeRate) public onlyManager {
        require(_defSwapFeeRate <= MAX_SWAP_FEE, "Factory: Over MAX_SWAP_FEE is not allowed");
        uint256 _old = defSwapFeeRate;
        defSwapFeeRate = _defSwapFeeRate;
        emit SetDefSwapFeeRate(_old, _defSwapFeeRate);
    }

    function containsPair(address value) external view returns (bool) {
        return pairImpls.contains(value);
    }

    function setPairType(address _impl) external onlyManager {
        uint8 _type = IPair(_impl).PAIR_TYPE();
        bool _auth = IPair(_impl).AUTH();

        address _old = pairParams[_type]._impl;
        if (_old != address(0)) pairImpls.remove(_old);

        require(pairImpls.add(_impl), "Factory: This pair already exists");
        pairParams[_type] = PairParams({ _impl: _impl, _auth: _auth });

        emit SetPairType(_impl, _type, _auth, _old);
    }

    function addPairType(address _impl) external onlyManager {
        uint8 _type = IPair(_impl).PAIR_TYPE();
        bool _auth = IPair(_impl).AUTH();

        require(pairImpls.add(_impl), "Factory: This pair already exists");
        require(pairParams[_type]._impl == address(0), "Factory: This pair type already exists");

        pairParams[_type] = PairParams({ _impl: _impl, _auth: _auth });

        emit SetPairType(_impl, _type, _auth, address(0));
    }

    function removePairType(address _impl) external onlyManager {
        require(pairImpls.contains(_impl), "Factory: This pair does not exist");

        uint8 _type = IPair(_impl).PAIR_TYPE();

        pairImpls.remove(_impl);
        PairParams memory _old = pairParams[_type];
        delete pairParams[_type];

        emit RemovePairType(_impl, _type, _old._auth);
    }

    function pairTypeValues() external view returns (address[] memory) {
        return pairImpls.values();
    }

    function pairTypeAmount() external view returns (uint256) {
        return pairImpls.length();
    }

    function atPairType(uint256 _index) external view returns (address) {
        return pairImpls.at(_index);
    }

    function getPairAddress(address[] memory _tokens, uint8 _pairType) public view returns (address) {
        bytes32 salt = keccak256(abi.encodePacked(_tokens.sortArray(), _pairType));
        return Clones.predictDeterministicAddress(pairParams[_pairType]._impl, salt);
    }

    function createPair(address[] memory _tokens, uint8 _pairType, bytes memory _data) external returns (address _pair) {
        _tokens = _tokens.sortArray();

        require(!isPair[getPairAddress(_tokens, _pairType)], "Factory: Pair already exists");

        bytes32 salt = keccak256(abi.encodePacked(_tokens, _pairType));

        PairParams memory _params = pairParams[_pairType];
        if (_pairType == 1) {
            _data = abi.encode(defSwapFeeRate, defAdminFeeRate);
        }
        if (_params._auth) {
            require(manager == msg.sender, "Factory: this pair type can only be created by manager");
        }
        _pair = Clones.cloneDeterministic(_params._impl, salt);

        IPair(_pair).initialize(_tokens, _data);

        allPairs.push(_pair);
        isPair[_pair] = true;

        emit PairCreated(_tokens, _pairType, _pair, allPairs.length);
    }
}

contracts/PairFees.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

// Pair Fees contract is used as a 1:1 pair relationship to split out fees, this ensures that the curve does not need to be modified for LP shares
contract PairFees {

    address internal immutable pair; // The pair it is bonded to
    address internal immutable token0; // token0 of pair, saved localy and statically for gas optimization
    address internal immutable token1; // Token1 of pair, saved localy and statically for gas optimization

    constructor(address _token0, address _token1) {
        pair = msg.sender;
        token0 = _token0;
        token1 = _token1;
    }

    function _safeTransfer(address token,address to,uint256 value) internal {
        require(token.code.length > 0);
        (bool success, bytes memory data) =
        token.call(abi.encodeWithSelector(IERC20.transfer.selector, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))));
    }

    // Allow the pair to transfer fees to users
    function claimFeesFor(address recipient, uint amount0, uint amount1) external {
        require(msg.sender == pair);
        if (amount0 > 0) _safeTransfer(token0, recipient, amount0);
        if (amount1 > 0) _safeTransfer(token1, recipient, amount1);
    }

}

contracts/Router.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import "./interface/IWETH.sol";
import "./interface/IPair.sol";
import "./interface/IPairRouter.sol";
import "./interface/IPairERC20.sol";
import "./interface/IPairFactory.sol";

import "./utils/Arrays.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract Router is Initializable {
    using Arrays for address[];
    using Address for address;

    struct Route {
        address from;
        address to;
        address pair;
    }

    address public factory;
    IWETH public weth;
    mapping(uint8 => address) public pairTypes;

    event SetPairTypes(uint8 pairType, address router);

    modifier onlyManager() {
        require(msg.sender == IPairFactory(factory).manager(), "not manager");
        _;
    }

    function initialize(address _factory, address _weth) public initializer {
        factory = _factory;
        weth = IWETH(_weth);
    }

    constructor(address _factory, address _weth) {
        initialize(_factory, _weth);
    }

    function setPairTypes(uint8 _pairType, address _router) external onlyManager {
        pairTypes[_pairType] = _router;
        emit SetPairTypes(_pairType, _router);
    }

    function quoteAddLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view returns (uint256[] memory _amountIn, uint256 liquidity) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteAddLiquidity.selector, _tokens, _amountDesireds)
        );
        (_amountIn, liquidity) = abi.decode(_returns, (uint256[], uint256));
    }

    function quoteRemoveLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity
    ) external view returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteRemoveLiquidity.selector, _tokens, _liquidity)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function addLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory _amounts, uint256 _liquidity) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IPairRouter.addLiquidity.selector, _tokens, _amountDesireds, _amountsMin, _minLiquidity, _to, _deadline)
        );
        (_amounts, _liquidity) = abi.decode(_returns, (uint256[], uint256));
    }

    function removeLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IPairRouter.removeLiquidity.selector, _tokens, _liquidity, _amountsMin, _to, _deadline)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function removeLiquidityETH(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IPairRouter.removeLiquidityETH.selector, _tokens, _liquidity, _amountsMin, _to, _deadline)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function removeLiquidityWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(
                IPairRouter.removeLiquidityWithPermit.selector,
                _tokens,
                _liquidity,
                _amountsMin,
                _to,
                _deadline,
                _approveMax,
                _v,
                _r,
                _s
            )
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function removeLiquidityETHWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256[] memory _amounts) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(
                IPairRouter.removeLiquidityETHWithPermit.selector,
                _tokens,
                _liquidity,
                _amountsMin,
                _to,
                _deadline,
                _approveMax,
                _v,
                _r,
                _s
            )
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    function quoteRemoveLiquidityOneToken(
        uint8 _pairType,
        address[] calldata _tokens,
        address _token,
        uint256 _liquidity
    ) external view returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityOneToken.selector, _tokens, _token, _liquidity)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function quoteRemoveLiquidityImbalance(
        uint8 _pairType,
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external view returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityImbalance.selector, _tokens, _amounts)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function removeLiquidityOneToken(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(
                IStablPairRouter.removeLiquidityOneToken.selector,
                _tokens,
                _liquidity,
                _token,
                _minAmount,
                _to,
                _deadline
            )
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function removeLiquidityImbalance(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amount) {
        address _router = pairTypes[_pairType];
        bytes memory _returns = _router.functionDelegateCall(
            abi.encodeWithSelector(IStablPairRouter.removeLiquidityImbalance.selector, _tokens, _amounts, _maxBurnAmount, _to, _deadline)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    function swap(
        Route[] memory _routes,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amountOut) {
        for (uint i = 0; i < _routes.length; i++) {
            uint8 _pairType = IPair(_routes[i].pair).PAIR_TYPE();
            address _router = pairTypes[_pairType];
            bytes memory _returns = _router.functionDelegateCall(
                abi.encodeWithSelector(IPairRouter.swap.selector, _routes[i], _amountIn, _amountOutMin, _to, _deadline)
            );
            _amountIn = abi.decode(_returns, (uint256));
        }
        _amountOut = _amountIn;
    }

    function getAmountsOut(uint256 _amountIn, Route[] memory _routes) public view returns (uint256 _amountsOut) {
        require(_routes.length >= 1, "Router: INVALID_PATH");
        _amountsOut = _amountIn;
        for (uint256 i = 0; i < _routes.length; i++) {
            _amountsOut = IPair(_routes[i].pair).getAmountOut(_routes[i].from, _routes[i].to, _amountsOut);
        }
    }

    function pairFor(address[] memory _tokens, uint8 _type) external view returns (address _pair, bool _has) {
        _pair = IPairFactory(factory).getPairAddress(_tokens, _type);
        _has = IPairFactory(factory).isPair(_pair);
    }

    receive() external payable {
        assert(msg.sender == address(weth)); // only accept ETH via fallback from the WETH contract
    }
}

contracts/routers/StableRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "../interface/IPair.sol";
import "../interface/IWETH.sol";
import "../interface/IPairRouter.sol";
import "../interface/IPairFactory.sol";
import "../interface/IPairERC20.sol";
import "../utils/Arrays.sol";

contract StableRouter is IStablPairRouter {
    using Arrays for address[];

    uint8 public constant PAIR_TYPE = 1;

    address public immutable factory;
    IWETH public immutable weth;

    modifier ensure(uint256 _deadline) {
        require(_deadline >= block.timestamp, "StableRouter: EXPIRED");
        _;
    }

    constructor(address _factory, address _weth) {
        factory = _factory;
        weth = IWETH(_weth);
    }

    function quoteAddLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view override returns (uint256[] memory _amountIn, uint256 _liquidity) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        bool _isPair = IPairFactory(factory).isPair(_pair);
        if (_isPair) {
            _amountIn = _amountDesireds;
            _liquidity = IStablePair(_pair).calculateTokenAmount(_tokens, _amountDesireds, true);
        }
    }

    function quoteRemoveLiquidity(address[] memory _tokens, uint256 _liquidity) external view override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (IPairFactory(factory).isPair(_pair)) _amounts = IStablePair(_pair).calculateRemoveLiquidity(_tokens, _liquidity);
    }

    function quoteRemoveLiquidityOneToken(
        address[] calldata _tokens,
        address _token,
        uint256 _liquidity
    ) external view override returns (uint256 _amount) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (IPairFactory(factory).isPair(_pair)) _amount = IStablePair(_pair).calculateRemoveLiquidityOneToken(_token, _liquidity);
    }

    function quoteRemoveLiquidityImbalance(
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external view override returns (uint256 _liquidity) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (IPairFactory(factory).isPair(_pair)) _liquidity = IStablePair(_pair).calculateTokenAmount(_tokens, _amounts, false) + 1;
    }

    function addLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: is not pair");
        _amounts = _amountDesireds;
        for (uint256 i = 0; i < _tokens.length; i++) {
            _safeTransferFrom(_tokens[i], msg.sender, address(this), _amounts[i]);
            _safeApprove(_tokens[i], _pair, _amounts[i]);
        }
        _liquidity = IStablePair(_pair).addLiquidity(_tokens, _amounts, _minLiquidity, _to, _deadline);
    }

    /// @param _tokens The last one is the token of weth
    /// @param _amountDesireds The last one is the amount of weth, And the array length is one bit longer than _tokens
    /// @param _amountsMins only minLiquidity
    function addLiquidityETH(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMins,
        address _to,
        uint256 _deadline
    ) external payable override returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = new uint256[](2);
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: is not pair");
        _amounts = _amountDesireds;
        weth.deposit{ value: _amounts[_amounts.length - 1] }();
        for (uint256 i = 0; i < _tokens.length; i++) {
            _safeTransferFrom(_tokens[i], msg.sender, address(this), _amounts[i]);
            _safeApprove(_tokens[i], _pair, _amounts[i]);
        }
        _liquidity = IStablePair(_pair).addLiquidity(_tokens, _amounts, _amountsMins[0], _to, _deadline);
        if (msg.value > _amounts[_amounts.length - 1]) _safeTransferETH(msg.sender, msg.value - _amounts[_amounts.length - 1]);
    }

    function removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: pair doesn't exist"); // send liquidity to pair

        address _lpToken = IStablePair(_pair).lpToken();
        _safeTransferFrom(_lpToken, msg.sender, address(this), _liquidity);
        _safeApprove(_lpToken, _pair, _liquidity);

        _amounts = IStablePair(_pair).removeLiquidity(_liquidity, _amountsMin, _to, _deadline);
    }

    /// @param _tokens The last one is the token of weth
    /// @param _amountsMin Minimum return value of each token
    function removeLiquidityETH(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        require(IPairFactory(factory).isPair(_pair), "StableRouter: pair doesn't exist"); // send liquidity to pair

        _amounts = removeLiquidity(_tokens, _liquidity, _amountsMin, _to, _deadline);

        uint256 _ethAmount = _amounts[_amounts.length - 1];
        weth.withdraw(_ethAmount);
        _safeTransferETH(msg.sender, _ethAmount);
    }

    function removeLiquidityWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        {
            uint256 _value = _approveMax ? type(uint256).max : _liquidity;
            address _lpToken = IStablePair(_pair).lpToken();
            IPairERC20(_lpToken).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        }

        _amounts = removeLiquidity(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    function removeLiquidityETHWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        {
            uint256 _value = _approveMax ? type(uint256).max : _liquidity;
            address _lpToken = IStablePair(_pair).lpToken();
            IPairERC20(_lpToken).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        }

        _amounts = removeLiquidityETH(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    function removeLiquidityOneToken(
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) external override returns (uint256 _amount) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        _amount = IStablePair(_pair).removeLiquidityOneToken(_liquidity, _token, _minAmount, _to, _deadline);
    }

    function removeLiquidityImbalance(
        address[] memory _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) external override returns (uint256 _amount) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        _amount = IStablePair(_pair).removeLiquidityImbalance(_tokens, _amounts, _maxBurnAmount, _to, _deadline);
    }

    function swap(
        Route memory _route,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external override returns (uint256 _amountOut) {
        require(IPairFactory(factory).isPair(_route.pair), "StableRouter: is not pair");

        _safeTransferFrom(_route.from, msg.sender, address(this), _amountIn);
        _safeApprove(_route.from, _route.pair, _amountIn);

        return IStablePair(_route.pair).swap(_route.from, _route.to, _amountIn, _amountOutMin, _to, _deadline);
    }

    function _safeTransferFrom(address _token, address _from, address _to, uint256 _value) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IPairERC20.transferFrom.selector, _from, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function _safeApprove(address token, address to, uint256 amount) internal {
        (bool success, bytes memory returndata) = token.call(abi.encodeWithSelector(IPairERC20(token).approve.selector, to, amount));

        if (!success || (returndata.length > 0 && !abi.decode(returndata, (bool)))) {
            _callOptionalReturn(token, abi.encodeWithSelector(IPairERC20(token).approve.selector, to, 0));
            _callOptionalReturn(token, abi.encodeWithSelector(IPairERC20(token).approve.selector, to, amount));
        }
    }

    function _callOptionalReturn(address token, bytes memory data) private {
        (bool success, bytes memory result) = token.call(data);
        require(success, "StablePair: Low-level call failed");

        if (result.length > 0) {
            require(abi.decode(result, (bool)), "StablePair: ERC20 operation did not succeed");
        }
    }

    function _safeTransferETH(address _to, uint256 _value) internal {
        (bool _success, ) = _to.call{ value: _value }(new bytes(0));
        require(_success, "TransferHelper: ETH_TRANSFER_FAILED");
    }
}

contracts/routers/VolatileRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "../interface/IPair.sol";
import "../interface/IWETH.sol";
import "../interface/IPairRouter.sol";
import "../interface/IPairFactory.sol";
import "../interface/IPairERC20.sol";
import "../utils/Arrays.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract VolatileRouter is IPairRouter {
    using Arrays for address[];

    uint8 public constant PAIR_TYPE = 1;
    uint256 internal constant MINIMUM_LIQUIDITY = 10 ** 3;

    address public immutable factory;
    IWETH public immutable weth;

    modifier ensure(uint256 _deadline) {
        require(_deadline >= block.timestamp, "Router: EXPIRED");
        _;
    }

    constructor(address _factory, address _weth) {
        factory = _factory;
        weth = IWETH(_weth);
    }

    function quoteAddLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view override returns (uint256[] memory _amountIn, uint256 liquidity) {
        // create the pair if it doesn't exist yet
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        if (!IPairFactory(factory).isPair(_pair)) {
            return (_amountIn, liquidity);
        }
        (uint256 reserveA, uint256 reserveB) = (0, 0);
        uint256 _totalSupply = 0;
        if (_pair != address(0)) {
            _totalSupply = IPairERC20(_pair).totalSupply();
            (reserveA, reserveB) = getReserves(_pair, _tokens[0], _tokens[1]);
        }
        (uint256 amountA, uint256 amountB) = (0, 0);
        (uint256 amountADesired, uint256 amountBDesired) = (_amountDesireds[0], _amountDesireds[1]);
        if (reserveA == 0 && reserveB == 0) {
            (amountA, amountB) = (amountADesired, amountBDesired);
            liquidity = Math.sqrt(amountA * amountB) - MINIMUM_LIQUIDITY;
        } else {
            uint256 amountBOptimal = _quoteLiquidity(amountADesired, reserveA, reserveB);
            if (amountBOptimal <= amountBDesired) {
                (amountA, amountB) = (amountADesired, amountBOptimal);
                liquidity = Math.min((amountA * _totalSupply) / reserveA, (amountB * _totalSupply) / reserveB);
            } else {
                uint256 amountAOptimal = _quoteLiquidity(amountBDesired, reserveB, reserveA);
                (amountA, amountB) = (amountAOptimal, amountBDesired);
                liquidity = Math.min((amountA * _totalSupply) / reserveA, (amountB * _totalSupply) / reserveB);
            }
        }
        _amountIn = new uint256[](2);
        (_amountIn[0], _amountIn[1]) = (amountA, amountB);
    }

    function quoteRemoveLiquidity(address[] memory _tokens, uint256 _liquidity) external view override returns (uint256[] memory _amounts) {
        // create the pair if it doesn't exist yet
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);

        if (!IPairFactory(factory).isPair(_pair)) {
            return _amounts;
        }

        (uint256 _reserveA, uint256 _reserveB) = getReserves(_pair, _tokens[0], _tokens[1]);
        uint256 _totalSupply = IPairERC20(_pair).totalSupply();

        _amounts = new uint256[](2);

        _amounts[0] = (_liquidity * _reserveA) / _totalSupply; // using balances ensures pro-rata distribution
        _amounts[1] = (_liquidity * _reserveB) / _totalSupply; // using balances ensures pro-rata distribution
    }

    function addLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = _amountDesireds;
        (_amounts[0], _amounts[1]) = _addLiquidity(
            _tokens[0],
            _tokens[1],
            _amountDesireds[0],
            _amountDesireds[1],
            _amountsMin[0],
            _amountsMin[1]
        );
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _safeTransferFrom(_tokens[i], msg.sender, _pair, _amounts[i]);
        }
        _liquidity = IVolatilePair(_pair).mint(_to);
        require(_liquidity >= _minLiquidity, "Couldn't mint min requested");
    }

    function _addLiquidity(
        address _tokenA,
        address _tokenB,
        uint256 _amountADesired,
        uint256 _amountBDesired,
        uint256 _amountAMin,
        uint256 _amountBMin
    ) internal returns (uint256 _amountA, uint256 _amountB) {
        require(_amountADesired >= _amountAMin);
        require(_amountBDesired >= _amountBMin);
        // create the pair if it doesn't exist yet
        address _pair = getPair(_tokenA, _tokenB);
        if (_pair == address(0)) {
            address[] memory _tokens = new address[](2);
            _tokens[0] = _tokenA;
            _tokens[1] = _tokenB;
            _pair = IPairFactory(factory).createPair(_tokens, PAIR_TYPE, new bytes(0));
        }
        require(_pair != address(0), "Router: not have this pair");
        (uint256 _reserveA, uint256 _reserveB) = getReserves(_pair, _tokenA, _tokenB);
        if (_reserveA == 0 && _reserveB == 0) {
            (_amountA, _amountB) = (_amountADesired, _amountBDesired);
        } else {
            uint256 _amountBOptimal = _quoteLiquidity(_amountADesired, _reserveA, _reserveB);
            if (_amountBOptimal <= _amountBDesired) {
                require(_amountBOptimal >= _amountBMin, "Router: INSUFFICIENT_B_AMOUNT");
                (_amountA, _amountB) = (_amountADesired, _amountBOptimal);
            } else {
                uint256 _amountAOptimal = _quoteLiquidity(_amountBDesired, _reserveB, _reserveA);
                assert(_amountAOptimal <= _amountADesired);
                require(_amountAOptimal >= _amountAMin, "Router: INSUFFICIENT_A_AMOUNT");
                (_amountA, _amountB) = (_amountAOptimal, _amountBDesired);
            }
        }
    }

    /// @param _tokens only other token
    /// @param _amountDesireds only other token
    /// @param _amountMins 0: other token, 1: weth
    /// @return _amounts 0: other token, 1: weth
    function addLiquidityETH(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountMins,
        address _to,
        uint256 _deadline
    ) external payable override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = new uint256[](2);
        (_amounts[0], _amounts[1]) = _addLiquidity(
            _tokens[0],
            address(weth),
            _amountDesireds[0],
            msg.value,
            _amountMins[0],
            _amountMins[1]
        );
        address _pair = getPair(_tokens[0], address(weth));
        _safeTransferFrom(_tokens[0], msg.sender, _pair, _amounts[0]);
        weth.deposit{ value: _amounts[1] }();
        assert(weth.transfer(_pair, _amounts[1]));
        _liquidity = IVolatilePair(_pair).mint(_to);
        // refund dust eth, if any
        if (msg.value > _amounts[1]) _safeTransferETH(msg.sender, msg.value - _amounts[1]);
    }

    function removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override ensure(_deadline) returns (uint256[] memory _amounts) {
        return _removeLiquidity(_tokens[0], _tokens[1], _liquidity, _amountsMin, _to);
    }

    function _removeLiquidity(
        address _token0,
        address _token1,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to
    ) internal returns (uint256[] memory _amounts) {
        address _pair = getPair(_token0, _token1);
        require(IPairFactory(factory).isPair(_pair), "Router: pair doesn't exist"); // send liquidity to pair
        IPairERC20(_pair).transferFrom(msg.sender, _pair, _liquidity);
        (uint256 _amount0, uint256 _amount1) = IVolatilePair(_pair).burn(_to);
        _amounts = new uint256[](2);
        (_amounts[0], _amounts[1]) = (_amount0, _amount1);

        for (uint256 i = 0; i < _amounts.length; i++) {
            require(_amounts[i] >= _amountsMin[i], "Router: INSUFFICIENT_A_AMOUNT");
        }
    }

    /// @param _tokens only other token
    /// @param _amountsMin 0: other token amount, 1: eth amount
    function removeLiquidityETH(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override ensure(_deadline) returns (uint256[] memory _amounts) {
        _amounts = _removeLiquidity(_tokens[0], address(weth), _liquidity, _amountsMin, address(this));
        _safeTransfer(_tokens[0], _to, _amounts[0]);
        weth.withdraw(_amounts[1]);
        _safeTransferETH(_to, _amounts[1]);
    }

    function removeLiquidityWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
        {
            uint256 _value = _approveMax ? type(uint256).max : _liquidity;
            IPairERC20(_pair).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        }
        _amounts = removeLiquidity(_tokens, _liquidity, _amountMin, _to, _deadline);
    }

    function removeLiquidityETHWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory) {
        address _pair = getPair(_tokens[0], address(weth));
        uint256 _value = _approveMax ? type(uint256).max : _liquidity;
        IPairERC20(_pair).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
        return removeLiquidityETH(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    function swap(
        Route memory _route,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external ensure(_deadline) returns (uint256 _amountOut) {
        address[] memory _tokens = IVolatilePair(_route.pair).tokens();
        _amountOut = IVolatilePair(_route.pair).getAmountOut(_route.from, _route.to, _amountIn);
        require(_amountOut >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");
        (uint256 amount0Out, uint256 amount1Out) = _route.pair == _tokens[0] ? (uint256(0), _amountOut) : (_amountOut, uint256(0));

        ///@dev The delegatecall function of the super router
        _safeTransfer(_route.from, _route.pair, _amountIn);
        IVolatilePair(_route.pair).swap(amount0Out, amount1Out, _to, new bytes(0));
    }

    function _swap(uint256[] memory amounts, address[] memory path, address _to) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0, ) = sortTokens(input, output);
            uint256 amountOut = amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) = input == token0 ? (uint256(0), amountOut) : (amountOut, uint256(0));
            address to = i < path.length - 2 ? getPair(output, path[i + 2]) : _to;
            IVolatilePair(getPair(input, output)).swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }

    function getReserves(address _pair, address tokenA, address tokenB) public view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = IVolatilePair(_pair).getReserves();
        (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
    }

    function sortTokens(address tokenA, address tokenB) public pure returns (address token0, address token1) {
        require(tokenA != tokenB, "Router: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), "Router: ZERO_ADDRESS");
    }

    // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function _quoteLiquidity(uint256 amountA, uint256 reserveA, uint256 reserveB) internal pure returns (uint256 amountB) {
        require(amountA > 0, "Router: INSUFFICIENT_AMOUNT");
        require(reserveA > 0 && reserveB > 0, "Router: INSUFFICIENT_LIQUIDITY");
        amountB = (amountA * reserveB) / reserveA;
    }

    function _safeTransferFrom(address _token, address _from, address _to, uint256 _value) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IPairERC20.transferFrom.selector, _from, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function _safeTransferETH(address _to, uint256 _value) internal {
        (bool _success, ) = _to.call{ value: _value }(new bytes(0));
        require(_success, "TransferHelper: ETH_TRANSFER_FAILED");
    }

    function _safeTransfer(address _token, address _to, uint256 _value) internal {
        require(_token.code.length > 0);
        (bool _success, bytes memory _data) = _token.call(abi.encodeWithSelector(IPairERC20.transfer.selector, _to, _value));
        require(_success && (_data.length == 0 || abi.decode(_data, (bool))));
    }

    function getPair(address _tokenA, address _tokenB) internal view returns (address _pair) {
        address[] memory _tokens = new address[](2);
        _tokens[0] = _tokenA;
        _tokens[1] = _tokenB;
        _pair = IPairFactory(factory).getPairAddress(_tokens, PAIR_TYPE);
    }

    function getAmountsOut(uint256 _amountIn, Route[] memory _routes) public view returns (uint256[] memory _amounts) {
        require(_routes.length >= 1, "Router: INVALID_PATH");
        _amounts = new uint256[](_routes.length + 1);
        _amounts[0] = _amountIn;
        for (uint256 i = 0; i < _routes.length; i++) {
            if (IPairFactory(factory).isPair(_routes[i].pair)) {
                _amounts[i + 1] = IPair(_routes[i].pair).getAmountOut(_routes[i].from, _routes[i].to, _amounts[i]);
            }
        }
    }
}

contracts/StablePair.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./utils/SwapUtils.sol";
import "./utils/AmplificationUtils.sol";
import "./utils/ERC20Call.sol";
import "./interface/IPairERC20.sol";
import "./interface/IPairFactory.sol";

/**
 * @title Swap - A StableSwap implementation in solidity.
 * @notice This contract is responsible for custody of closely pegged assets (eg. group of stablecoins)
 * and automatic market making system. Users become an LP (Liquidity Provider) by depositing their tokens
 * in desired ratios for an exchange of the pool token that represents their share of the pool.
 * Users can burn pool tokens and withdraw their share of token(s).
 *
 * Each time a swap between the pooled tokens happens, a set fee incurs which effectively gets
 * distributed to the LPs.
 *
 * In case of emergencies, admin can pause additional deposits, swaps, or single-asset withdraws - which
 * stops the ratio of the tokens in the pool from changing.
 * Users can always withdraw their tokens via multi-asset withdraws.
 *
 * @dev Most of the logic is stored as a library `SwapUtils` for the sake of reducing contract's
 * deployment size.
 */
contract StablePair is Initializable {
    using SwapUtils for SwapUtils.Swap;
    using AmplificationUtils for SwapUtils.Swap;
    using ERC20Call for address;

    uint8 public constant PAIR_TYPE = 2;
    bool public constant AUTH = true;

    address public factory;

    uint256 private unlocked_;

    // Struct storing data responsible for automatic market maker functionalities. In order to
    // access this data, this contract uses SwapUtils library. For more details, see SwapUtils.sol
    SwapUtils.Swap public swapStorage;

    // Maps token address to an index in the pool. Used to prevent duplicate tokens in the pool.
    // getTokenIndex function also relies on this mapping to retrieve token index.
    mapping(address => uint8) internal tokenIndexes_;

    /*** EVENTS ***/

    // events replicated from SwapUtils to make the ABI easier for dumb
    // clients
    event TokenSwap(address indexed buyer, uint256 tokensSold, uint256 tokensBought, uint128 soldId, uint128 boughtId);
    event AddLiquidity(address indexed provider, uint256[] tokenAmounts, uint256[] fees, uint256 invariant, uint256 lpTokenSupply);
    event RemoveLiquidity(address indexed provider, uint256[] tokenAmounts, uint256 lpTokenSupply);
    event RemoveLiquidityOne(
        address indexed provider,
        uint256 lpTokenAmount,
        uint256 lpTokenSupply,
        uint256 boughtId,
        uint256 tokensBought
    );
    event RemoveLiquidityImbalance(
        address indexed provider,
        uint256[] tokenAmounts,
        uint256[] fees,
        uint256 invariant,
        uint256 lpTokenSupply
    );
    event SwapFee(address[] tokens, uint256[] swapFees);
    event NewAdminFee(uint256 newAdminFee);
    event NewSwapFee(uint256 newSwapFee);
    event NewWithdrawFee(uint256 newWithdrawFee);
    event RampA(uint256 oldA, uint256 newA, uint256 initialTime, uint256 futureTime);
    event StopRampA(uint256 currentA, uint256 time);

    function initialize(address[] calldata _tokens, bytes calldata _data) external initializer {
        factory = msg.sender;
        unlocked_ = 1;
        string memory _lpTokenName = "DForce Stable AMM - ";
        string memory _lpTokenSymbol = "dAMM-Stable-";
        string memory _separator = "-";
        uint8[] memory _decimals = new uint8[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _decimals[i] = IPairERC20(_tokens[i]).decimals();
            string memory _tokenSymbol = _tokens[0].callSymbol();
            if (i == _tokens.length - 1) _separator = "";
            _lpTokenName = string(abi.encodePacked(_lpTokenName, _tokenSymbol, _separator));
            _lpTokenSymbol = string(abi.encodePacked(_lpTokenSymbol, _tokenSymbol, _separator));
        }

        (uint256 _swapFee, uint256 _adminFeeRate, uint256 _a, address _lpTokenTargetAddress) = abi.decode(
            _data,
            (uint256, uint256, uint256, address)
        );
        __SwapV2_init(_tokens, _decimals, _lpTokenName, _lpTokenSymbol, _a, _swapFee, _adminFeeRate, _lpTokenTargetAddress);
    }

    /**
     * @notice Initializes this Swap contract with the given parameters.
     * This will also clone a LPToken contract that represents users'
     * LP positions. The owner of LPToken will be this contract - which means
     * only this contract is allowed to mint/burn tokens.
     *
     * @param _pooledTokens an array of ERC20s this pool will accept
     * @param _decimals the decimals to use for each pooled token,
     * eg 8 for WBTC. Cannot be larger than POOL_PRECISION_DECIMALS
     * @param _lpTokenName the long-form name of the token to be deployed
     * @param _lpTokenSymbol the short symbol for the token to be deployed
     * @param _a the amplification coefficient * n * (n - 1). See the
     * StableSwap paper for details
     * @param _fee default swap fee to be initialized with
     * @param _adminFee default adminFee to be initialized with
     * @param _lpTokenTargetAddress the address of an existing LPToken contract to use as a target
     */
    function __SwapV2_init(
        address[] memory _pooledTokens,
        uint8[] memory _decimals,
        string memory _lpTokenName,
        string memory _lpTokenSymbol,
        uint256 _a,
        uint256 _fee,
        uint256 _adminFee,
        address _lpTokenTargetAddress
    ) internal virtual {
        // Check _pooledTokens and precisions parameter
        require(_pooledTokens.length > 1, "_pooledTokens.length <= 1");
        require(_pooledTokens.length <= 32, "_pooledTokens.length > 32");
        require(_pooledTokens.length == _decimals.length, "_pooledTokens decimals mismatch");

        uint256[] memory _precisionMultipliers = new uint256[](_decimals.length);
        IERC20[] memory _poolTokens = new IERC20[](_decimals.length);

        for (uint8 i = 0; i < _pooledTokens.length; i++) {
            if (i > 0) {
                // Check if index is already used. Check if 0th element is a duplicate.
                require(tokenIndexes_[_pooledTokens[i]] == 0 && _pooledTokens[0] != _pooledTokens[i], "Duplicate tokens");
            }
            require(_pooledTokens[i] != address(0) && _pooledTokens[i] != address(this), "The 0 address isn't an ERC-20");
            require(_decimals[i] <= SwapUtils.POOL_PRECISION_DECIMALS, "Token decimals exceeds max");
            _precisionMultipliers[i] = 10 ** (uint256(SwapUtils.POOL_PRECISION_DECIMALS) - uint256(_decimals[i]));
            _poolTokens[i] = IERC20(_pooledTokens[i]);
            tokenIndexes_[_pooledTokens[i]] = i;
        }

        // Check _a, _fee, _adminFee, _withdrawFee parameters
        require(_a < AmplificationUtils.MAX_A, "_a exceeds maximum");
        require(_fee < SwapUtils.MAX_SWAP_FEE, "_fee exceeds maximum");
        require(_adminFee < SwapUtils.MAX_ADMIN_FEE, "_adminFee exceeds maximum");

        // Clone and initialize a LPToken contract
        LPToken _lpToken = LPToken(Clones.clone(_lpTokenTargetAddress));
        require(_lpToken.initialize(_lpTokenName, _lpTokenSymbol), "could not init lpToken clone");

        // Initialize swapStorage struct
        swapStorage.lpToken = _lpToken;
        swapStorage.pooledTokens = _poolTokens;
        swapStorage.tokenPrecisionMultipliers = _precisionMultipliers;
        swapStorage.balances = new uint256[](_pooledTokens.length);
        swapStorage.initialA = _a * AmplificationUtils.A_PRECISION;
        swapStorage.futureA = _a * AmplificationUtils.A_PRECISION;
        // swapStorage.initialATime = 0;
        // swapStorage.futureATime = 0;
        swapStorage.swapFee = _fee;
        swapStorage.adminFee = _adminFee;
    }

    /*** MODIFIERS ***/

    /**
     * @notice Modifier to check sender against factory manager.
     */
    modifier onlyManager() {
        require(factory == msg.sender || IPairFactory(factory).manager() == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    /**
     * @notice contract function lock modifier.
     */
    modifier lock() {
        require(unlocked_ == 1, "Pair: LOCKED");
        unlocked_ = 0;
        _;
        unlocked_ = 1;
    }

    /**
     * @notice Modifier to check _deadline against current timestamp
     * @param _deadline latest timestamp to accept this transaction
     */
    modifier deadlineCheck(uint256 _deadline) {
        require(block.timestamp <= _deadline, "Deadline not met");
        _;
    }

    /*** VIEW FUNCTIONS ***/

    /**
     * @notice Return A, the amplification coefficient * n * (n - 1)
     * @dev See the StableSwap paper for details
     * @return A parameter
     */
    function getA() external view virtual returns (uint256) {
        return swapStorage.getA();
    }

    /**
     * @notice Return A in its raw precision form
     * @dev See the StableSwap paper for details
     * @return A parameter in its raw precision form
     */
    function getAPrecise() external view virtual returns (uint256) {
        return swapStorage.getAPrecise();
    }

    /**
     * @notice Return address of the pooled token at given index. Reverts if _tokenIndex is out of range.
     * @param _index the index of the token
     * @return address of the token at given index
     */
    function getToken(uint8 _index) public view virtual returns (address) {
        require(_index < swapStorage.pooledTokens.length, "Out of range");
        return address(swapStorage.pooledTokens[_index]);
    }

    /**
     * @notice Query all token addresses in pair.
     * @return all token addresses
     */
    function tokens() external view returns (IERC20[] memory) {
        return swapStorage.pooledTokens;
    }

    /**
     * @notice Query lpToken addresse.
     * @return lpToken addresse
     */
    function lpToken() external view returns (address) {
        return address(swapStorage.lpToken);
    }

    /**
     * @notice Return the index of the given token address. Reverts if no matching
     * token is found.
     * @param _tokenAddress address of the token
     * @return the index of the given token address
     */
    function getTokenIndex(address _tokenAddress) external view virtual returns (uint8) {
        uint8 _index = tokenIndexes_[_tokenAddress];
        require(getToken(_index) == _tokenAddress, "Token does not exist");
        return _index;
    }

    /**
     * @notice Return current balance of the pooled token at given index
     * @param _index the index of the token
     * @return current balance of the pooled token at given index with token's native precision
     */
    function getTokenBalance(uint8 _index) external view virtual returns (uint256) {
        require(_index < swapStorage.pooledTokens.length, "Index out of range");
        return swapStorage.balances[_index];
    }

    /**
     * @notice Return current balances of the pooled tokens
     * @return current balances of the pooled tokens
     */
    function getTokenBalances() external view virtual returns (uint256[] memory) {
        return swapStorage.balances;
    }

    /**
     * @notice Get the virtual price, to help calculate profit
     * @return the virtual price, scaled to the POOL_PRECISION_DECIMALS
     */
    function getVirtualPrice() external view virtual returns (uint256) {
        return swapStorage.getVirtualPrice();
    }

    /**
     * @notice Calculate amount of tokens you receive on swap
     * @param _tokenFrom the token address the user wants to sell
     * @param _tokenTo the token address the user wants to buy
     * @param _dx the amount of tokens the user wants to sell. If the token charges
     * a fee on transfers, use the amount that gets transferred after the fee.
     * @return amount of tokens the user will receive
     */
    function getAmountOut(address _tokenFrom, address _tokenTo, uint256 _dx) external view returns (uint256) {
        return swapStorage.calculateSwap(tokenIndexes_[_tokenFrom], tokenIndexes_[_tokenTo], _dx);
    }

    /**
     * @notice Convert the array index, according to tokenIndexes_.
     * @param _tokens an array of all token addresses for the pair,
     * @param _amounts an array of token amounts, corresponding to param _tokens.
     * @return _newAmounts amount of tokens after conversion
     */
    function _convertIndex(address[] calldata _tokens, uint256[] calldata _amounts) internal view returns (uint256[] memory _newAmounts) {
        _newAmounts = _amounts;
        for (uint256 i = 0; i < _tokens.length; i++) {
            _newAmounts[tokenIndexes_[_tokens[i]]] = _amounts[i];
        }
    }

    /**
     * @notice A simple method to calculate prices from deposits or
     * withdrawals, excluding fees but including slippage. This is
     * helpful as an input into the various "min" parameters on calls
     * to fight front-running
     *
     * @dev This shouldn't be used outside frontends for user estimates.
     *
     * @param _tokens an array of all token addresses for the pair,
     * @param _amounts an array of token amounts to deposit or withdrawal,
     * corresponding to param _tokens. The amount should be in each
     * pooled token's native precision. If a token charges a fee on transfers,
     * use the amount that gets transferred after the fee.
     * @param _deposit whether this is a deposit or a withdrawal
     * @return token amount the user will receive
     */
    function calculateTokenAmount(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        bool _deposit
    ) external view virtual returns (uint256) {
        return swapStorage.calculateTokenAmount(_convertIndex(_tokens, _amounts), _deposit);
    }

    /**
     * @notice A simple method to calculate amount of each underlying
     * tokens that is returned upon burning given amount of LP tokens
     * @param _tokens token address list
     * @param _amount the amount of LP tokens that would be burned on withdrawal
     * @return array of token balances that the user will receive
     */
    function calculateRemoveLiquidity(address[] calldata _tokens, uint256 _amount) external view virtual returns (uint256[] memory) {
        uint256[] memory _amounts = swapStorage.calculateRemoveLiquidity(_amount);
        uint256[] memory _actualAmounts = _amounts;
        for (uint256 i = 0; i < _tokens.length; i++) {
            _actualAmounts[i] = _amounts[tokenIndexes_[_tokens[i]]];
        }
        return _actualAmounts;
    }

    /**
     * @notice Calculate the amount of underlying token available to withdraw
     * when withdrawing via only single token
     * @param _token address of tokens that will be withdrawn
     * @param _tokenAmount the amount of LP token to burn
     * @return calculated amount of underlying token
     * available to withdraw
     */
    function calculateRemoveLiquidityOneToken(address _token, uint256 _tokenAmount) external view virtual returns (uint256) {
        return swapStorage.calculateWithdrawOneToken(_tokenAmount, tokenIndexes_[_token]);
    }

    /**
     * @notice This function reads the accumulated amount of admin fees of the token with given index
     * @param _index Index of the pooled token
     * @return admin's token balance in the token's precision
     */
    function getAdminBalance(uint256 _index) external view virtual returns (uint256) {
        return swapStorage.getAdminBalance(_index);
    }

    /*** STATE MODIFYING FUNCTIONS ***/

    /**
     * @notice Calculate amount of tokens you receive on swap
     * @param _tokenFrom the token address the user wants to sell
     * @param _tokenTo the token address the user wants to buy
     * @param _dx the amount of tokens the user wants to swap from
     * @param _minDy the min amount the user would like to receive, or revert.
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     */
    function swap(
        address _tokenFrom,
        address _tokenTo,
        uint256 _dx,
        uint256 _minDy,
        address _receiver,
        uint256 _deadline
    ) external lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.swap(tokenIndexes_[_tokenFrom], tokenIndexes_[_tokenTo], _dx, _minDy, _receiver);
    }

    /**
     * @notice Add liquidity to the pool with the given amounts of tokens
     * @param _tokens token address list
     * @param _amounts the amounts of each token to add, in their native precision,corresponding to param _tokens
     * @param _minToMint the minimum LP tokens adding this amount of liquidity
     * should mint, otherwise revert. Handy for front-running mitigation
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amount of LP token user minted and received
     */
    function addLiquidity(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        uint256 _minToMint,
        address _receiver,
        uint256 _deadline
    ) external lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.addLiquidity(_convertIndex(_tokens, _amounts), _minToMint, _receiver);
    }

    /**
     * @notice Burn LP tokens to remove liquidity from the pool. Withdraw fee that decays linearly
     * over period of 4 weeks since last deposit will apply.
     * @dev Liquidity can always be removed, even when the pool is paused.
     * @param _amount the amount of LP tokens to burn
     * @param _tokens token address list
     * @param _minAmounts the minimum amounts of each token in the pool
     *        acceptable for this burn. Useful as a front-running mitigation
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amounts of tokens user received
     */
    function removeLiquidity(
        uint256 _amount,
        address[] calldata _tokens,
        uint256[] calldata _minAmounts,
        address _receiver,
        uint256 _deadline
    ) external payable virtual lock deadlineCheck(_deadline) returns (uint256[] memory) {
        return swapStorage.removeLiquidity(_amount, _convertIndex(_tokens, _minAmounts), _receiver);
    }

    /**
     * @notice Remove liquidity from the pool all in one token. Withdraw fee that decays linearly
     * over period of 4 weeks since last deposit will apply.
     * @param _tokenAmount the amount of the token you want to receive
     * @param _token address of the token you want to receive
     * @param _minAmount the minimum amount to withdraw, otherwise revert
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amount of chosen token user received
     */
    function removeLiquidityOneToken(
        uint256 _tokenAmount,
        address _token,
        uint256 _minAmount,
        address _receiver,
        uint256 _deadline
    ) external lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.removeLiquidityOneToken(_tokenAmount, tokenIndexes_[_token], _minAmount, _receiver);
    }

    /**
     * @notice Remove liquidity from the pool, weighted differently than the
     * pool's current balances. Withdraw fee that decays linearly
     * over period of 4 weeks since last deposit will apply.
     * @param _tokens token address list
     * @param _amounts how much of each token to withdraw
     * @param _maxBurnAmount the max LP token provider is willing to pay to
     * remove liquidity. Useful as a front-running mitigation.
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amount of LP tokens burned
     */
    function removeLiquidityImbalance(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _receiver,
        uint256 _deadline
    ) external lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.removeLiquidityImbalance(_convertIndex(_tokens, _amounts), _maxBurnAmount, _receiver);
    }

    /*** ADMIN FUNCTIONS ***/

    /**
     * @notice Withdraw all admin fees to the contract factory manager
     */
    function claimFees() external returns (uint256[] memory) {
        return swapStorage.withdrawAdminFees(IPairFactory(factory).manager());
    }

    /**
     * @notice Update the admin fee. Admin fee takes portion of the swap fee.
     * @param _newAdminFee new admin fee to be applied on future transactions
     */
    function setAdminFeeRate(uint256 _newAdminFee) external onlyManager {
        swapStorage.setAdminFee(_newAdminFee);
    }

    /**
     * @notice Update the swap fee to be applied on swaps
     * @param _newSwapFee new swap fee to be applied on future transactions
     */
    function setSwapFee(uint256 _newSwapFee) external payable onlyManager {
        swapStorage.setSwapFee(_newSwapFee);
    }

    /**
     * @notice Start ramping up or down A parameter towards given futureA and futureTime
     * Checks if the change is too rapid, and commits the new A value only when it falls under
     * the limit range.
     * @param _futureA the new A to ramp towards
     * @param _futureTime timestamp when the new A should be reached
     */
    function rampA(uint256 _futureA, uint256 _futureTime) external payable onlyManager {
        swapStorage.rampA(_futureA, _futureTime);
    }

    /**
     * @notice Stop ramping A immediately. Reverts if ramp A is already stopped.
     */
    function stopRampA() external payable onlyManager {
        swapStorage.stopRampA();
    }
}

