//SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

library ERC20Call {

    function callSymbol(address _token)
        internal
        view
        returns (string memory _symbol)
    {
        if (_token != address(0)) {
            (bool _success, bytes memory _res) =
                _token.staticcall(abi.encodeWithSignature("symbol()"));
            if (_success)
                _symbol = _res.length == 32
                    ? string(_res)
                    : abi.decode(_res, (string));
        }
    }
}

