     * Requirements:
     *
     * - This contract must be the current admin of `proxy`.
     */
    function changeProxyAdmin(TransparentUpgradeableProxy proxy, address newAdmin) public virtual onlyOwner {
        proxy.changeAdmin(newAdmin);
    }

    /**
     * @dev Upgrades `proxy` to `implementation`. See {TransparentUpgradeableProxy-upgradeTo}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgrade(TransparentUpgradeableProxy proxy, address implementation) public virtual onlyOwner {
        proxy.upgradeTo(implementation);
    }

    /**
     * @dev Upgrades `proxy` to `implementation` and calls a function on the new implementation. See
     * {TransparentUpgradeableProxy-upgradeToAndCall}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgradeAndCall(
        TransparentUpgradeableProxy proxy,
        address implementation,
        bytes memory data
    ) public payable virtual onlyOwner {
        proxy.upgradeToAndCall{ value: msg.value }(implementation, data);
    }
}

contracts/PairFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

import "@openzeppelin/contracts-upgradeable/proxy/Initializable.sol";

import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";

import "./interface/IPairFactory.sol";

import "./utils/Arrays.sol";
import { IPair } from "./interface/IPair.sol";

/**
 * @title PairFactory
 * @dev This contract is responsible for creating and managing pairs.
 */
contract PairFactory is IPairFactory, Initializable {
    using EnumerableSet for EnumerableSet.AddressSet;
    using Arrays for address[];

    // Maximum swap fee and admin fee rates
    uint256 public constant MAX_SWAP_FEE = 10 ** 8;
    uint256 public constant MAX_ADMIN_FEE = 10 ** 10;

    // Address of the current manager
    address public override manager;

    // Address of the pending manager
    address public pendingManager;

    // Default swap fee rate
    uint256 public defSwapFeeRate;

    // Default admin fee rate
    uint256 public defAdminFeeRate;

    // Mapping of pair addresses to boolean values indicating whether the pair exists
    mapping(address => bool) public override isPair;

    // Array of all pair addresses
    address[] public allPairs;

    // Set of pair implementation addresses
    EnumerableSet.AddressSet internal pairImpls_;

    //1: volatile pair, 2: stable pair, 3: yeild pair
    // Struct to store pair implementation and authorization status
    struct PairParams {
        address impl; // Address of the pair implementation
        bool auth; // Authorization status of the pair
    }
    // Mapping of pair type to pair implementation and authorization status
    mapping(uint8 => PairParams) public pairParams;

    /**
     * @dev Event emitted when a new pair is created.
     * @param tokens The tokens in the new pair.
     * @param pairType The type of the new pair.
     * @param pair The address of the new pair.
     * @param pairsAmount The total number of pairs.
     */
    event PairCreated(address[] tokens, uint8 pairType, address pair, uint256 pairsAmount);

    /**
     * @dev Event emitted when a new manager is pending.
     * @param manager The current manager.
     * @param pendingManager The new pending manager.
     */
    event SetPendingManager(address manager, address pendingManager);

    /**
     * @dev Event emitted when the manager is changed.
     * @param oldManager The old manager.
     * @param newManager The new manager.
     */
    event ChangeManager(address oldManager, address newManager);

    /**
     * @dev Event emitted when the default swap fee rate is changed.
     * @param oldDefSwapFeeRate The old default swap fee rate.
     * @param newDefSwapFeeRate The new default swap fee rate.
     */
    event SetDefSwapFeeRate(uint256 oldDefSwapFeeRate, uint256 newDefSwapFeeRate);

    /**
     * @dev Event emitted when the default admin fee rate is changed.
     * @param oldDefAdminFeeRate The old default admin fee rate.
     * @param newDefAdminFeeRate The new default admin fee rate.
     */
    event SetDefAdminFeeRate(uint256 oldDefAdminFeeRate, uint256 newDefAdminFeeRate);

    /**
     * @dev Event emitted when a new pair type is added.
     * @param impl The address of the pair implementation.
     * @param pairType The type of the new pair.
     * @param auth The authorization status of the new pair.
     * @param oldImpl The address of the old pair implementation.
     */
    event SetPairType(address impl, uint8 pairType, bool auth, address oldImpl);

    /**
     * @dev Event emitted when a pair type is removed.
     * @param impl The address of the pair implementation.
     * @param pairType The type of the pair to be removed.
     * @param auth The authorization status of the pair to be removed.
     */
    event RemovePairType(address impl, uint8 pairType, bool auth);

    /**
     * @dev Constructor function that initializes the default swap fee rate and default admin fee rate.
     * @param _defSwapFeeRate The default swap fee rate.
     * @param _defAdminFeeRate The default admin fee rate.
     */
    constructor(uint256 _defSwapFeeRate, uint256 _defAdminFeeRate) public {
        initialize(_defSwapFeeRate, _defAdminFeeRate);
    }

    /**
     * @dev Function that initializes the default swap fee rate, default admin fee rate, and manager.
     * @param _defSwapFeeRate The default swap fee rate.
     * @param _defAdminFeeRate The default admin fee rate.
     */
    function initialize(uint256 _defSwapFeeRate, uint256 _defAdminFeeRate) public initializer {
        manager = msg.sender;
        setDefSwapFeeRate(_defSwapFeeRate);
        setDefAdminFeeRate(_defAdminFeeRate);
    }

    /**
     * @dev Modifier that checks if the caller is the manager.
     */
    modifier onlyManager() {
        require(msg.sender == manager, "PairFactory: not manager");
        _;
    }

    /**
     * @dev Function that returns the length of the allPairs array.
     * @return The length of the allPairs array.
     */
    function allPairsLength() external view override returns (uint256) {
        return allPairs.length;
    }

    /**
     * @dev Function that checks if a pair with the given address exists.
     * @param value The address of the pair to check.
     * @return A boolean indicating whether the pair exists or not.
     */
    function containsPair(address value) external view returns (bool) {
        return pairImpls_.contains(value);
    }

    /**
     * @dev Function that returns an array of all pair implementation addresses.
     * @return _pairImpls An array of all pair implementation addresses.
     */
    function pairTypeValues() external view override returns (address[] memory _pairImpls) {
        uint256 _len = pairImpls_.length();
        _pairImpls = new address[](_len);
        for (uint256 i = 0; i < _len; i++) {
            _pairImpls[i] = pairImpls_.at(i);
        }
        return _pairImpls;
    }

    /**
     * @dev Function that returns the number of pair implementations.
     * @return The number of pair implementations.
     */
    function pairTypeAmount() external view returns (uint256) {
        return pairImpls_.length();
    }

    /**
     * @dev Function that returns the pair implementation address at the given index.
     * @param _index The index of the pair implementation address to return.
     * @return The pair implementation address at the given index.
     */
    function atPairType(uint256 _index) external view override returns (address) {
        return pairImpls_.at(_index);
    }

    /**
     * @dev Function that returns the pair address for the given tokens and pair type.
     * @param _tokens The tokens to create the pair with.
     * @param _pairType The type of the pair to create.
     * @return The pair address for the given tokens and pair type.
     */
    function getPairAddress(address[] memory _tokens, uint8 _pairType) public view override returns (address) {
        bytes32 salt = keccak256(abi.encodePacked(_tokens.sortArray(), _pairType));
        return Clones.predictDeterministicAddress(pairParams[_pairType].impl, salt);
    }

    /**
     * @dev Function that creates a new pair with the given tokens and pair type.
     * @param _tokens The tokens to create the pair with.
     * @param _pairType The type of the pair to create.
     * @param _data Additional data to pass to the pair's `initialize` function.
     * @return _pair The address of the newly created pair.
     */
    function createPair(
        address[] memory _tokens,
        uint8 _pairType,
        bytes memory _data
    ) external override returns (address _pair) {
        _tokens = _tokens.sortArray();
        require(pairParams[_pairType].impl != address(0), "PairFactory: No impl of this type");
        require(!isPair[getPairAddress(_tokens, _pairType)], "PairFactory: Pair already exists");

        bytes32 salt = keccak256(abi.encodePacked(_tokens, _pairType));

        PairParams memory _params = pairParams[_pairType];
        if (_pairType == 1) {
            _data = abi.encode(defSwapFeeRate, defAdminFeeRate);
        }
        if (_params.auth) {
            require(manager == msg.sender, "PairFactory: this pair type can only be created by manager");
        }
        _pair = Clones.cloneDeterministic(_params.impl, salt);

        IPair(_pair).initialize(_tokens, _data);

        allPairs.push(_pair);
        isPair[_pair] = true;

        emit PairCreated(_tokens, _pairType, _pair, allPairs.length);
    }

    /**
     * @dev Function that sets the pending manager address.
     * @param _pendingManager The address of the pending manager.
     */
    function setPendingManager(address _pendingManager) external onlyManager {
        require(manager != _pendingManager && pendingManager != _pendingManager, "PairFactory: manager has been set");

        pendingManager = _pendingManager;

        emit SetPendingManager(manager, _pendingManager);
    }

    /**
     * @dev Function that accepts the pending manager address.
     */
    function acceptManager() external {
        require(msg.sender == pendingManager, "PairFactory: not pending fee manager");
        address _oldManager = manager;

        manager = pendingManager;
        pendingManager = address(0);

        emit ChangeManager(_oldManager, manager);
    }

    /**
     * @dev Function that sets the default swap fee rate.
     * @param _defSwapFeeRate The new default swap fee rate.
     */
    function setDefSwapFeeRate(uint256 _defSwapFeeRate) public onlyManager {
        require(_defSwapFeeRate <= MAX_SWAP_FEE, "PairFactory: Over MAX_SWAP_FEE is not allowed");

        uint256 _oldDefSwapFeeRate = defSwapFeeRate;
        require(_defSwapFeeRate != _oldDefSwapFeeRate, "PairFactory: _defSwapFeeRate invalid");

        defSwapFeeRate = _defSwapFeeRate;

        emit SetDefSwapFeeRate(_oldDefSwapFeeRate, _defSwapFeeRate);
    }

    /**
     * @dev Function that sets the default admin fee rate.
     * @param _defAdminFeeRate The new default admin fee rate.
     */
    function setDefAdminFeeRate(uint256 _defAdminFeeRate) public onlyManager {
        require(_defAdminFeeRate <= MAX_ADMIN_FEE, "PairFactory: Over MAX_ADMIN_FEE is not allowed");

        uint256 _oldDefAdminFeeRate = defAdminFeeRate;
        require(_defAdminFeeRate != _oldDefAdminFeeRate, "PairFactory: _defAdminFeeRate invalid");

        defAdminFeeRate = _defAdminFeeRate;

        emit SetDefAdminFeeRate(_oldDefAdminFeeRate, _defAdminFeeRate);
    }

    /**
     * @dev Function that adds a new pair type.
     * @param _impl The address of the pair implementation.
     */
    function addPairType(address _impl) external onlyManager {
        uint8 _type = IPair(_impl).PAIR_TYPE();
        bool _auth = IPair(_impl).AUTH();

        require(pairImpls_.add(_impl), "PairFactory: This pair already exists");

        require(pairParams[_type].impl == address(0), "PairFactory: This pair type already exists");

        pairParams[_type] = PairParams({ impl: _impl, auth: _auth });

        emit SetPairType(_impl, _type, _auth, address(0));
    }

    /**
     * @dev Function that removes a pair type.
     * @param _impl The address of the pair implementation.
     */
    function removePairType(address _impl) external onlyManager {
        require(pairImpls_.contains(_impl), "PairFactory: This pair does not exist");

        uint8 _type = IPair(_impl).PAIR_TYPE();

        pairImpls_.remove(_impl);
        PairParams memory _old = pairParams[_type];
        delete pairParams[_type];

        emit RemovePairType(_impl, _type, _old.auth);
    }
}

contracts/Router.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-upgradeable/proxy/Initializable.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./utils/TransferHelper.sol";

import "./interface/IWETH.sol";
import "./interface/IPair.sol";
import "./interface/IPairRouter.sol";
import "./interface/IPairFactory.sol";

/**
 * @title Router contract
 * @dev This contract handles the routing of tokens in the dForce AMM ecosystem
 */
contract Router is Initializable {
    using Address for address;
    using TransferHelper for address;

    /**
     * @dev Struct to represent a route between two tokens
     */
    struct Route {
        address from; // Address of the token to convert from
        address to; // Address of the token to convert to
        address pair; // Address of the pair contract that connects the two tokens
    }

    /**
     * @dev Address of the factory contract
     */
    address public factory;

    /**
     * @dev Address of the WETH contract
     */
    IWETH internal weth_;

    /**
     * @dev Mapping of pair types to their respective router contracts
     */
    mapping(uint8 => address) internal pairTypes_;

    /**
     * @dev Event emitted when a pair type is set
     */
    event SetPairTypes(uint8 pairType, address router);

    /**
     * @dev Constructor function for the Router contract
     * @param _factory Address of the factory contract
     * @param _weth Address of the WETH contract
     */
    constructor(address _factory, address _weth) public {
        initialize(_factory, _weth);
    }

    /**
     * @dev Initializes the Router contract
     * @param _factory Address of the factory contract
     * @param _weth Address of the WETH contract
     */
    function initialize(address _factory, address _weth) public initializer {
        factory = _factory;
        weth_ = IWETH(_weth);
    }

    /**
     * @dev Fallback function to receive ETH from WETH contract
     */
    receive() external payable {
        assert(msg.sender == address(weth_)); // only accept ETH via fallback from the WETH contract
    }

    /**
     * @dev Modifier to restrict access to only the manager
     */
    modifier onlyManager() {
        require(msg.sender == IPairFactory(factory).manager(), "Router: not manager");
        _;
    }

    /**
     * @dev Modifier to check if the given pair type is valid
     * @param _pairType The pair type to check
     */
    modifier checkPairType(uint8 _pairType) {
        require(pairTypes_[_pairType] != address(0), "Router: invalid pair type");
        _;
    }

    /**
     * @dev Returns the address of the WETH contract
     */
    function weth() external view returns (address) {
        return address(weth_);
    }

    /**
     * @dev Returns the address of the router contract for a given pair type
     * @param _pairType The pair type
     */
    function pairTypes(uint8 _pairType) external view returns (address) {
        return pairTypes_[_pairType];
    }

    /**
     * @dev Returns the amount of tokens that will be received for a given input amount and route
     * @param _amountIn The input amount
     * @param _routes The route to take
     * @return _amounts The amounts of tokens that will be received
     */
    function _getAmountsOut(
        uint256 _amountIn,
        Route[] memory _routes
    ) internal view returns (uint256[] memory _amounts) {
        _amounts = new uint256[](_routes.length + 1);
        _amounts[0] = _amountIn;

        for (uint256 i = 0; i < _routes.length; i++)
            _amounts[i + 1] = IPair(_routes[i].pair).getAmountOut(_routes[i].from, _routes[i].to, _amounts[i]);
    }

    /**
     * @dev Returns the amount of tokens that will be received for a given input amount and route
     * @param _amountIn The input amount
     * @param _routes The route to take
     */
    function getAmountsOutPath(
        uint256 _amountIn,
        Route[] memory _routes
    ) external view returns (uint256[] memory _amounts) {
        _amounts = _getAmountsOut(_amountIn, _routes);
    }

    /**
     * @dev Returns the amount of tokens that will be received for a given input amount and route
     * @param _amountIn The input amount
     * @param _routes The route to take
     */
    function getAmountsOut(uint256 _amountIn, Route[] memory _routes) external view returns (uint256) {
        require(_routes.length >= 1, "Router: INVALID_PATH");
        uint256[] memory _amounts = _getAmountsOut(_amountIn, _routes);
        return _amounts[_amounts.length - 1];
    }

    /**
     * @dev Returns the address of the pair for the given tokens and pair type
     * @param _tokens The tokens to get the pair address for
     * @param _type The pair type
     * @return _pair The address of the pair
     * @return _has Whether or not the pair exists
     */
    function pairFor(address[] memory _tokens, uint8 _type) external view returns (address _pair, bool _has) {
        _pair = IPairFactory(factory).getPairAddress(_tokens, _type);
        _has = IPairFactory(factory).isPair(_pair);
    }

    /**
     * @dev Returns the reserves of the volatile pair for the given tokens
     * @param _tokens The tokens to get the reserves for
     * @return _reserveA The reserve of token A
     * @return _reserveB The reserve of token B
     */
    function getReserves(address[] memory _tokens) external view returns (uint256 _reserveA, uint256 _reserveB) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, 1);
        if (IPairFactory(factory).isPair(_pair))
            (_reserveA, _reserveB) = IVolatileRouter(pairTypes_[1]).getReserves(_pair, _tokens);
    }

    /**
     * @dev Returns the amount of tokens required to add liquidity to a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token to add liquidity for
     */
    function quoteAddLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view returns (uint256[] memory _amountIn, uint256 liquidity) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteAddLiquidity.selector, _tokens, _amountDesireds)
        );
        (_amountIn, liquidity) = abi.decode(_returns, (uint256[], uint256));
    }

    /**
     * @dev Returns the amount of tokens that will be received when removing liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     */
    function quoteRemoveLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity
    ) external view returns (uint256[] memory _amounts) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteRemoveLiquidity.selector, _tokens, _liquidity)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    /**
     * @dev Returns the amount of a specific token that will be received when removing liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _token The token to receive
     * @param _liquidity The amount of liquidity to remove
     */
    function quoteRemoveLiquidityOneToken(
        uint8 _pairType,
        address[] calldata _tokens,
        address _token,
        uint256 _liquidity
    ) external view returns (uint256 _amount) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityOneToken.selector, _tokens, _token, _liquidity)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    /**
     * @dev Returns the amount of tokens that will be removed when removing liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The desired amounts of each token to remove liquidity for
     */
    function quoteRemoveLiquidityImbalance(
        uint8 _pairType,
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external view returns (uint256 _amount) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityImbalance.selector, _tokens, _amounts)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    /**
     * @dev Adds liquidity to a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token to add liquidity for
     * @param _amountsMin The minimum amounts of each token to add liquidity for
     * @param _minLiquidity The minimum liquidity to add
     * @param _to The address to send the liquidity to
     * @param _deadline The deadline for the transaction
     * @return _amounts The amounts of tokens added
     * @return _liquidity The amount of liquidity added
     */
    function addLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts, uint256 _liquidity) {
        (_amounts, _liquidity) = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.addLiquidity.selector,
                    _tokens,
                    _amountDesireds,
                    _amountsMin,
                    _minLiquidity,
                    _to,
                    _deadline
                )
            ),
            (uint256[], uint256)
        );
    }

    /**
     * @dev Adds liquidity to a given pair with ETH
     * @param _pairType The pair type
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token to add liquidity for
     * @param _amountsMin The minimum amounts of each token to add liquidity for
     * @param _minLiquidity The minimum liquidity to add
     * @param _to The address to send the liquidity to
     * @param _deadline The deadline for the transaction
     * @return _amounts The amounts of tokens added
     * @return _liquidity The amount of liquidity added
     */
    function addLiquidityETH(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external payable checkPairType(_pairType) returns (uint256[] memory _amounts, uint256 _liquidity) {
        (_amounts, _liquidity) = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.addLiquidityETH.selector,
                    _tokens,
                    _amountDesireds,
                    _amountsMin,
                    _minLiquidity,
                    _to,
                    _deadline
                )
            ),
            (uint256[], uint256)
        );
    }

    /**
     * @dev Removes liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @return _amounts The amounts of tokens received
     */
    function removeLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts) {
        _amounts = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.removeLiquidity.selector,
                    _tokens,
                    _liquidity,
                    _amountsMin,
                    _to,
                    _deadline
                )
            ),
            (uint256[])
        );
    }

    /**
     * @dev Removes liquidity from a given pair with ETH
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @return _amounts The amounts of tokens received
     */
    function removeLiquidityETH(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts) {
        _amounts = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.removeLiquidityETH.selector,
                    _tokens,
                    _liquidity,
                    _amountsMin,
                    _to,
                    _deadline
                )
            ),
            (uint256[])
        );
    }

    /**
     * @dev Removes liquidity for a single token from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive
     * @param _minAmount The minimum amount of token to receive
     * @param _to The address to send the token to
     * @param _deadline The deadline for the transaction
     * @return _amount The amount of token received
     */
    function removeLiquidityOneToken(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256 _amount) {
        _amount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityOneToken.selector,
                    _tokens,
                    _liquidity,
                    _token,
                    _minAmount,
                    _to,
                    _deadline
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity for a single token from a given pair with ETH
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive
     * @param _minAmount The minimum amount of token to receive
     * @param _to The address to send the token to
     * @param _deadline The deadline for the transaction
     * @return _amount The amount of token received
     */
    function removeLiquidityOneTokenETH(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256 _amount) {
        _amount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityOneTokenETH.selector,
                    _tokens,
                    _liquidity,
                    _token,
                    _minAmount,
                    _to,
                    _deadline
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity from a given pair with an imbalance of tokens
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of each token to remove
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @return _amount The amount of liquidity removed
     */
    function removeLiquidityImbalance(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256 _amount) {
        _amount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityImbalance.selector,
                    _tokens,
                    _amounts,
                    _maxBurnAmount,
                    _to,
                    _deadline
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity from a given pair with an imbalance of ETH and tokens
     * @param _pairType The type of pair
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of each token to remove
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @return _amount The amount of liquidity removed
     */
    function removeLiquidityImbalanceETH(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256 _amount) {
        _amount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityImbalanceETH.selector,
                    _tokens,
                    _amounts,
                    _maxBurnAmount,
                    _to,
                    _deadline
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity from a given pair with a permit
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _amounts The amounts of tokens removed
     */
    function removeLiquidityWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts) {
        _amounts = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.removeLiquidityWithPermit.selector,
                    _tokens,
                    _liquidity,
                    _amountsMin,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256[])
        );
    }

    /**
     * @dev Removes liquidity from a given pair with a permit
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _amounts The amounts of tokens removed
     */
    function removeLiquidityETHWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts) {
        _amounts = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.removeLiquidityETHWithPermit.selector,
                    _tokens,
                    _liquidity,
                    _amountsMin,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256[])
        );
    }

    /**
     * @dev Removes liquidity for a single token with a permit
     * @param _pairType The type of pair
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to remove liquidity for
     * @param _minAmount The minimum amount of tokens to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _tokenAmount The amount of tokens removed
     */
    function removeLiquidityOneTokenWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256 _tokenAmount) {
        require(pairTypes_[_pairType] != address(0), "Router: invalid pair type");
        _tokenAmount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityOneTokenWithPermit.selector,
                    _tokens,
                    _liquidity,
                    _token,
                    _minAmount,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity for a single token with a permit
     * @param _pairType The type of pair
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to remove liquidity for
     * @param _minAmount The minimum amount of tokens to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _tokenAmount The amount of tokens removed
     */
    function removeLiquidityOneTokenETHWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256 _tokenAmount) {
        require(pairTypes_[_pairType] != address(0), "Router: invalid pair type");
        _tokenAmount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityOneTokenETHWithPermit.selector,
                    _tokens,
                    _liquidity,
                    _token,
                    _minAmount,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity imbalance for multiple tokens with a permit
     * @param _pairType The type of pair
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _actualBurnAmount The actual amount of liquidity removed
     */
    function removeLiquidityImbalanceWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external checkPairType(_pairType) returns (uint256 _actualBurnAmount) {
        _actualBurnAmount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityImbalanceWithPermit.selector,
                    _tokens,
                    _amounts,
                    _maxBurnAmount,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity imbalance for multiple tokens with a permit
     * @param _pairType The type of pair
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _actualBurnAmount The actual amount of liquidity removed
     */
    function removeLiquidityImbalanceETHWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external checkPairType(_pairType) returns (uint256 _actualBurnAmount) {
        _actualBurnAmount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityImbalanceETHWithPermit.selector,
                    _tokens,
                    _amounts,
                    _maxBurnAmount,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Swaps tokens through multiple pairs
     * @param _routes The routes to swap through
     * @param _amountIn The amount of tokens to swap
     * @param _amountOutMin The minimum amount of tokens to receive
     * @param _to The address to send the swapped tokens to
     * @param _deadline The deadline for the transaction
     * @return _amountOut The amount of tokens received
     */
    function swap(
        Route[] memory _routes,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amountOut) {
        // Get the amounts of tokens to receive for each pair in the route
        uint256[] memory _amounts = _getAmountsOut(_amountIn, _routes);
        // Ensure that the amount of tokens received is greater than or equal to the minimum amount specified
        require(_amounts[_amounts.length - 1] >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");
        // Transfer the input tokens from the sender to this contract
        _routes[0].from.safeTransferFrom(msg.sender, address(this), _amountIn);
        // Swap the tokens through each pair in the route
        for (uint256 i = 0; i < _routes.length; i++) {
            // Get the pair type for the current pair in the route
            uint8 _pairType = IPair(_routes[i].pair).PAIR_TYPE();
            // Set the receiver address for the current pair in the route
            address _receiver = address(this);
            if (i == _routes.length - 1) _receiver = _to;

            // Call the swap function on the router contract for the current pair in the route
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.swap.selector,
                    _routes[i],
                    _amounts[i],
                    _amounts[i + 1],
                    _receiver,
                    _deadline
                )
            );
        }
        // Set the amount of tokens received to the output amount
        _amountOut = _amounts[_amounts.length - 1];
    }

    /**
     * @dev Swaps ETH for tokens through multiple pairs
     * @param _routes The routes to swap through
     * @param _amountIn The amount of ETH to swap
     * @param _amountOutMin The minimum amount of tokens to receive
     * @param _to The address to send the swapped tokens to
     * @param _deadline The deadline for the transaction
     * @return _amountOut The amount of tokens received
     */
    function swapETH(
        Route[] memory _routes,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256 _amountOut) {
        // Get the amounts of tokens to receive for each pair in the route
        uint256[] memory _amounts = _getAmountsOut(_amountIn, _routes);
        // Ensure that the amount of tokens received is greater than or equal to the minimum amount specified
        require(_amounts[_amounts.length - 1] >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");

        // If the input token is WETH, ensure that the amount of ETH sent is equal to the amount to swap
        if (_routes[0].from == address(weth_)) {
            require(msg.value == _amountIn, "Router: msg.value is not equal to _amountIn");
            // Deposit the ETH into WETH
            weth_.deposit{ value: msg.value }();
        } else {
            require(msg.value == 0, "Router: msg.value is not equal to 0");
            // Transfer the input tokens from the sender to this contract
            _routes[0].from.safeTransferFrom(msg.sender, address(this), _amountIn);
        }

        // Swap the tokens through each pair in the route
        for (uint256 i = 0; i < _routes.length; i++) {
            // Get the pair type for the current pair in the route
            uint8 _pairType = IPair(_routes[i].pair).PAIR_TYPE();
            // Set the receiver address for the current pair in the route
            address _receiver = address(this);
            if (i == _routes.length - 1 && _routes[i].to != address(weth_)) _receiver = _to;

            // Call the swap function on the router contract for the current pair in the route
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.swap.selector,
                    _routes[i],
                    _amounts[i],
                    _amounts[i + 1],
                    _receiver,
                    _deadline
                )
            );
        }
        // Set the amount of tokens received to the output amount
        _amountOut = _amounts[_amounts.length - 1];

        // If the output token is WETH, withdraw the WETH and transfer the ETH to the recipient
        if (_routes[_routes.length - 1].to == address(weth_)) {
            weth_.withdraw(_amountOut);
            _to.safeTransferETH(_amountOut);
        }
    }

    /**
     * @dev Sets the pair types for the router contract
     * @param _pairRouter The address of the pair router contract
     */
    function setPairTypes(address _pairRouter) external onlyManager {
        // Get the pair type for the pair router contract
        uint8 _pairType = IPairRouter(_pairRouter).PAIR_TYPE();
        // Set the pair type for the router contract
        pairTypes_[_pairType] = _pairRouter;
        // Emit an event indicating that the pair types have been set
        emit SetPairTypes(_pairType, _pairRouter);
    }
}

contracts/RouterBeta.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-upgradeable/proxy/Initializable.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./utils/TransferHelper.sol";

import "./interface/IWETH.sol";
import "./interface/IPair.sol";
import "./interface/IPairRouter.sol";
import "./interface/IPairFactory.sol";

/**
 * @title RouterBeta contract
 * @dev This contract handles the routing of tokens in the dForce AMM ecosystem
 */
contract RouterBeta is Initializable {
    using Address for address;
    using TransferHelper for address;

    /**
     * @dev Struct to represent a route between two tokens
     */
    struct Route {
        address from; // Address of the token to convert from
        address to; // Address of the token to convert to
        address pair; // Address of the pair contract that connects the two tokens
    }

    /**
     * @dev Address of the factory contract
     */
    address public factory;

    /**
     * @dev Address of the WETH contract
     */
    IWETH internal weth_;

    /**
     * @dev Mapping of pair types to their respective router contracts
     */
    mapping(uint8 => address) internal pairTypes_;

    /**
     * @dev Event emitted when a pair type is set
     */
    event SetPairTypes(uint8 pairType, address router);

    /**
     * @dev Constructor function for the Router contract
     * @param _factory Address of the factory contract
     * @param _weth Address of the WETH contract
     */
    constructor(address _factory, address _weth) public {
        initialize(_factory, _weth);
    }

    /**
     * @dev Initializes the Router contract
     * @param _factory Address of the factory contract
     * @param _weth Address of the WETH contract
     */
    function initialize(address _factory, address _weth) public initializer {
        factory = _factory;
        weth_ = IWETH(_weth);
    }

    /**
     * @dev Fallback function to receive ETH from WETH contract
     */
    receive() external payable {
        assert(msg.sender == address(weth_)); // only accept ETH via fallback from the WETH contract
    }

    /**
     * @dev Modifier to restrict access to only the manager
     */
    modifier onlyManager() {
        require(msg.sender == IPairFactory(factory).manager(), "Router: not manager");
        _;
    }

    /**
     * @dev Modifier to check if the given pair type is valid
     * @param _pairType The pair type to check
     */
    modifier checkPairType(uint8 _pairType) {
        require(pairTypes_[_pairType] != address(0), "Router: invalid pair type");
        _;
    }

    /**
     * @dev Returns the address of the WETH contract
     */
    function weth() external view returns (address) {
        return address(weth_);
    }

    /**
     * @dev Returns the address of the router contract for a given pair type
     * @param _pairType The pair type
     */
    function pairTypes(uint8 _pairType) external view returns (address) {
        return pairTypes_[_pairType];
    }

    /**
     * @dev Returns the amount of tokens that will be received for a given input amount and route
     * @param _amountIn The input amount
     * @param _routes The route to take
     * @return _amounts The amounts of tokens that will be received
     */
    function _getAmountsOut(
        uint256 _amountIn,
        Route[] memory _routes
    ) internal view returns (uint256[] memory _amounts) {
        _amounts = new uint256[](_routes.length + 1);
        _amounts[0] = _amountIn;

        for (uint256 i = 0; i < _routes.length; i++)
            _amounts[i + 1] = IPair(_routes[i].pair).getAmountOut(_routes[i].from, _routes[i].to, _amounts[i]);
    }

    /**
     * @dev Returns the amount of tokens that will be received for a given input amount and route
     * @param _amountIn The input amount
     * @param _routes The route to take
     */
    function getAmountsOutPath(
        uint256 _amountIn,
        Route[] memory _routes
    ) external view returns (uint256[] memory _amounts) {
        _amounts = _getAmountsOut(_amountIn, _routes);
    }

    /**
     * @dev Returns the amount of tokens that will be received for a given input amount and route
     * @param _amountIn The input amount
     * @param _routes The route to take
     */
    function getAmountsOut(uint256 _amountIn, Route[] memory _routes) external view returns (uint256) {
        require(_routes.length >= 1, "Router: INVALID_PATH");
        uint256[] memory _amounts = _getAmountsOut(_amountIn, _routes);
        return _amounts[_amounts.length - 1];
    }

    /**
     * @dev Returns the address of the pair for the given tokens and pair type
     * @param _tokens The tokens to get the pair address for
     * @param _type The pair type
     * @return _pair The address of the pair
     * @return _has Whether or not the pair exists
     */
    function pairFor(address[] memory _tokens, uint8 _type) external view returns (address _pair, bool _has) {
        _pair = IPairFactory(factory).getPairAddress(_tokens, _type);
        _has = IPairFactory(factory).isPair(_pair);
    }

    /**
     * @dev Returns the reserves of the volatile pair for the given tokens
     * @param _tokens The tokens to get the reserves for
     * @return _reserveA The reserve of token A
     * @return _reserveB The reserve of token B
     */
    function getReserves(address[] memory _tokens) external view returns (uint256 _reserveA, uint256 _reserveB) {
        address _pair = IPairFactory(factory).getPairAddress(_tokens, 1);
        if (IPairFactory(factory).isPair(_pair))
            (_reserveA, _reserveB) = IVolatileRouter(pairTypes_[1]).getReserves(_pair, _tokens);
    }

    /**
     * @dev Returns the amount of tokens required to add liquidity to a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token to add liquidity for
     */
    function quoteAddLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds
    ) external view returns (uint256[] memory _amountIn, uint256 liquidity) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteAddLiquidity.selector, _tokens, _amountDesireds)
        );
        (_amountIn, liquidity) = abi.decode(_returns, (uint256[], uint256));
    }

    /**
     * @dev Returns the amount of tokens that will be received when removing liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     */
    function quoteRemoveLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity
    ) external view returns (uint256[] memory _amounts) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IPairRouter.quoteRemoveLiquidity.selector, _tokens, _liquidity)
        );
        _amounts = abi.decode(_returns, (uint256[]));
    }

    /**
     * @dev Returns the amount of a specific token that will be received when removing liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _token The token to receive
     * @param _liquidity The amount of liquidity to remove
     */
    function quoteRemoveLiquidityOneToken(
        uint8 _pairType,
        address[] calldata _tokens,
        address _token,
        uint256 _liquidity
    ) external view returns (uint256 _amount) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityOneToken.selector, _tokens, _token, _liquidity)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    /**
     * @dev Returns the amount of tokens that will be removed when removing liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The desired amounts of each token to remove liquidity for
     */
    function quoteRemoveLiquidityImbalance(
        uint8 _pairType,
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external view returns (uint256 _amount) {
        address _router = pairTypes_[_pairType];
        bytes memory _returns = _router.functionStaticCall(
            abi.encodeWithSelector(IStablPairRouter.quoteRemoveLiquidityImbalance.selector, _tokens, _amounts)
        );
        _amount = abi.decode(_returns, (uint256));
    }

    /**
     * @dev Adds liquidity to a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token to add liquidity for
     * @param _amountsMin The minimum amounts of each token to add liquidity for
     * @param _minLiquidity The minimum liquidity to add
     * @param _to The address to send the liquidity to
     * @param _deadline The deadline for the transaction
     * @return _amounts The amounts of tokens added
     * @return _liquidity The amount of liquidity added
     */
    function addLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts, uint256 _liquidity) {
        (_amounts, _liquidity) = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.addLiquidity.selector,
                    _tokens,
                    _amountDesireds,
                    _amountsMin,
                    _minLiquidity,
                    _to,
                    _deadline
                )
            ),
            (uint256[], uint256)
        );
    }

    /**
     * @dev Removes liquidity from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @return _amounts The amounts of tokens received
     */
    function removeLiquidity(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts) {
        _amounts = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.removeLiquidity.selector,
                    _tokens,
                    _liquidity,
                    _amountsMin,
                    _to,
                    _deadline
                )
            ),
            (uint256[])
        );
    }

    /**
     * @dev Removes liquidity for a single token from a given pair
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive
     * @param _minAmount The minimum amount of token to receive
     * @param _to The address to send the token to
     * @param _deadline The deadline for the transaction
     * @return _amount The amount of token received
     */
    function removeLiquidityOneToken(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256 _amount) {
        _amount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityOneToken.selector,
                    _tokens,
                    _liquidity,
                    _token,
                    _minAmount,
                    _to,
                    _deadline
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity from a given pair with an imbalance of tokens
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of each token to remove
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @return _amount The amount of liquidity removed
     */
    function removeLiquidityImbalance(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) external checkPairType(_pairType) returns (uint256 _amount) {
        _amount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityImbalance.selector,
                    _tokens,
                    _amounts,
                    _maxBurnAmount,
                    _to,
                    _deadline
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity from a given pair with a permit
     * @param _pairType The pair type
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _amounts The amounts of tokens removed
     */
    function removeLiquidityWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external checkPairType(_pairType) returns (uint256[] memory _amounts) {
        _amounts = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.removeLiquidityWithPermit.selector,
                    _tokens,
                    _liquidity,
                    _amountsMin,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256[])
        );
    }

    /**
     * @dev Removes liquidity for a single token with a permit
     * @param _pairType The type of pair
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to remove liquidity for
     * @param _minAmount The minimum amount of tokens to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _tokenAmount The amount of tokens removed
     */
    function removeLiquidityOneTokenWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external returns (uint256 _tokenAmount) {
        require(pairTypes_[_pairType] != address(0), "Router: invalid pair type");
        _tokenAmount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityOneTokenWithPermit.selector,
                    _tokens,
                    _liquidity,
                    _token,
                    _minAmount,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Removes liquidity imbalance for multiple tokens with a permit
     * @param _pairType The type of pair
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline for the transaction
     * @param _approveMax Whether to approve the maximum amount
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _actualBurnAmount The actual amount of liquidity removed
     */
    function removeLiquidityImbalanceWithPermit(
        uint8 _pairType,
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external checkPairType(_pairType) returns (uint256 _actualBurnAmount) {
        _actualBurnAmount = abi.decode(
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IStablPairRouter.removeLiquidityImbalanceWithPermit.selector,
                    _tokens,
                    _amounts,
                    _maxBurnAmount,
                    _to,
                    _deadline,
                    _approveMax,
                    _v,
                    _r,
                    _s
                )
            ),
            (uint256)
        );
    }

    /**
     * @dev Swaps tokens through multiple pairs
     * @param _routes The routes to swap through
     * @param _amountIn The amount of tokens to swap
     * @param _amountOutMin The minimum amount of tokens to receive
     * @param _to The address to send the swapped tokens to
     * @param _deadline The deadline for the transaction
     * @return _amountOut The amount of tokens received
     */
    function swap(
        Route[] memory _routes,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 _amountOut) {
        // Get the amounts of tokens to receive for each pair in the route
        uint256[] memory _amounts = _getAmountsOut(_amountIn, _routes);
        // Ensure that the amount of tokens received is greater than or equal to the minimum amount specified
        require(_amounts[_amounts.length - 1] >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");
        // Transfer the input tokens from the sender to this contract
        _routes[0].from.safeTransferFrom(msg.sender, address(this), _amountIn);
        // Swap the tokens through each pair in the route
        for (uint256 i = 0; i < _routes.length; i++) {
            // Get the pair type for the current pair in the route
            uint8 _pairType = IPair(_routes[i].pair).PAIR_TYPE();
            // Set the receiver address for the current pair in the route
            address _receiver = address(this);
            if (i == _routes.length - 1) _receiver = _to;

            // Call the swap function on the router contract for the current pair in the route
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.swap.selector,
                    _routes[i],
                    _amounts[i],
                    _amounts[i + 1],
                    _receiver,
                    _deadline
                )
            );
        }
        // Set the amount of tokens received to the output amount
        _amountOut = _amounts[_amounts.length - 1];
    }

    /**
     * @dev Swaps ETH for tokens through multiple pairs
     * @param _routes The routes to swap through
     * @param _amountIn The amount of ETH to swap
     * @param _amountOutMin The minimum amount of tokens to receive
     * @param _to The address to send the swapped tokens to
     * @param _deadline The deadline for the transaction
     * @return _amountOut The amount of tokens received
     */
    function swapETH(
        Route[] memory _routes,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256 _amountOut) {
        // Get the amounts of tokens to receive for each pair in the route
        uint256[] memory _amounts = _getAmountsOut(_amountIn, _routes);
        // Ensure that the amount of tokens received is greater than or equal to the minimum amount specified
        require(_amounts[_amounts.length - 1] >= _amountOutMin, "Router: INSUFFICIENT_OUTPUT_AMOUNT");

        // If the input token is WETH, ensure that the amount of ETH sent is equal to the amount to swap
        if (_routes[0].from == address(weth_)) {
            require(msg.value == _amountIn, "Router: msg.value is not equal to _amountIn");
            // Deposit the ETH into WETH
            weth_.deposit{ value: msg.value }();
        } else {
            // Transfer the input tokens from the sender to this contract
            _routes[0].from.safeTransferFrom(msg.sender, address(this), _amountIn);
        }

        // Swap the tokens through each pair in the route
        for (uint256 i = 0; i < _routes.length; i++) {
            // Get the pair type for the current pair in the route
            uint8 _pairType = IPair(_routes[i].pair).PAIR_TYPE();
            // Set the receiver address for the current pair in the route
            address _receiver = address(this);
            if (i == _routes.length - 1 && _routes[i].to != address(weth_)) _receiver = _to;

            // Call the swap function on the router contract for the current pair in the route
            pairTypes_[_pairType].functionDelegateCall(
                abi.encodeWithSelector(
                    IPairRouter.swap.selector,
                    _routes[i],
                    _amounts[i],
                    _amounts[i + 1],
                    _receiver,
                    _deadline
                )
            );
        }
        // Set the amount of tokens received to the output amount
        _amountOut = _amounts[_amounts.length - 1];

        // If the output token is WETH, withdraw the WETH and transfer the ETH to the recipient
        if (_routes[_routes.length - 1].to == address(weth_)) {
            weth_.withdraw(_amountOut);
            _to.safeTransferETH(_amountOut);
        }
    }

    /**
     * @dev Sets the pair types for the router contract
     * @param _pairRouter The address of the pair router contract
     */
    function setPairTypes(address _pairRouter) external onlyManager {
        // Get the pair type for the pair router contract
        uint8 _pairType = IPairRouter(_pairRouter).PAIR_TYPE();
        // Set the pair type for the router contract
        pairTypes_[_pairType] = _pairRouter;
        // Emit an event indicating that the pair types have been set
        emit SetPairTypes(_pairType, _pairRouter);
    }
}

contracts/routers/StableRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "../utils/TransferHelper.sol";

import "../interface/IPairRouter.sol";
import "../interface/IPairFactory.sol";
import "../interface/IPair.sol";
import "../interface/IWETH.sol";
import "../interface/IPairERC20.sol";

/**
 * @title StableRouter
 * @notice Router for stablecoin pairs
 * @dev This contract implements the IStablPairRouter interface
 */
contract StableRouter is IStablPairRouter {
    using TransferHelper for address;

    // The type of pair
    uint8 internal constant PAIR_TYPE_ = 2;

    // The address of the factory contract
    address internal immutable factory_;
    // The WETH contract
    IWETH internal immutable weth_;

    constructor(address _factory, address _weth) public {
        factory_ = _factory;
        weth_ = IWETH(_weth);
    }

    // Get the type of pair
    function PAIR_TYPE() external view override returns (uint8) {
        return PAIR_TYPE_;
    }

    // Get the address of the factory contract
    function factory() external view returns (address) {
        return factory_;
    }

    // Get the address of the WETH contract
    function weth() external view returns (address) {
        return address(weth_);
    }

    /**
     * @dev Calculates the amount of tokens needed to add liquidity to a pair
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token
     * @return _amountIn The amounts of each token needed to add liquidity
     * @return _liquidity The amount of liquidity that will be added
     */
    function quoteAddLiquidity(
        address[] calldata _tokens,
        uint256[] calldata _amountDesireds
    ) external view override returns (uint256[] memory _amountIn, uint256 _liquidity) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        bool _isPair = IPairFactory(factory_).isPair(_pair);
        if (_isPair) {
            _amountIn = _amountDesireds;
            (bool _success, bytes memory _res) = _pair.staticcall(
                abi.encodeWithSignature(
                    "calculateTokenAmount(address[],uint256[],bool)",
                    _tokens,
                    _amountDesireds,
                    true
                )
            );
            if (_success) _liquidity = abi.decode(_res, (uint256));
        }
    }

    /**
     * @dev Calculates the amount of tokens that will be received upon removing liquidity from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @return _amounts The amounts of each token that will be received
     */
    function quoteRemoveLiquidity(
        address[] calldata _tokens,
        uint256 _liquidity
    ) external view override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        if (IPairFactory(factory_).isPair(_pair))
            _amounts = IStablePair(_pair).calculateRemoveLiquidity(_tokens, _liquidity);
    }

    /**
     * @dev Calculates the amount of a specific token that will be received upon removing liquidity from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _token The token to calculate the amount for
     * @param _liquidity The amount of liquidity to remove
     * @return _amount The amount of the specified token that will be received
     */
    function quoteRemoveLiquidityOneToken(
        address[] calldata _tokens,
        address _token,
        uint256 _liquidity
    ) external view override returns (uint256 _amount) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        if (IPairFactory(factory_).isPair(_pair))
            _amount = IStablePair(_pair).calculateRemoveLiquidityOneToken(_token, _liquidity);
    }

    /**
     * @dev Calculates the amount of liquidity that will be removed when removing an imbalanced amount of tokens from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of each token to remove
     * @return _liquidity The amount of liquidity that will be removed
     */
    function quoteRemoveLiquidityImbalance(
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external view override returns (uint256 _liquidity) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        if (IPairFactory(factory_).isPair(_pair))
            _liquidity = IStablePair(_pair).calculateTokenAmount(_tokens, _amounts, false) + 1;
    }

    /**
     * @dev Adds liquidity to a pair
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token to add
     * @param _amountMin Unused
     * @param _minLiquidity The minimum amount of liquidity to add
     * @param _to The address to send the liquidity to
     * @param _deadline The deadline to add liquidity by
     * @return _amounts The actual amounts of each token added
     * @return _liquidity The amount of liquidity added
     */
    function addLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountMin, // Unused
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external override returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amountMin;
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        require(IPairFactory(factory_).isPair(_pair), "StableRouter: is not pair");

        // Transfer tokens from sender to contract and approve for pair
        _amounts = _amountDesireds;
        for (uint256 i = 0; i < _tokens.length; i++) {
            _tokens[i].safeTransferFrom(msg.sender, address(this), _amounts[i]);
            _tokens[i].safeApprove(_pair, _amounts[i]);
        }

        // Add liquidity to pair
        _liquidity = IStablePair(_pair).addLiquidity(_tokens, _amounts, _minLiquidity, _to, _deadline);
    }

    /**
     * @dev Adds liquidity to a pair with ETH
     * @param _tokens The tokens to add liquidity for
     * @param _amountDesireds The desired amounts of each token to add
     * @param _amountMin Unused
     * @param _minLiquidity The minimum amount of liquidity to add
     * @param _to The address to send the liquidity to
     * @param _deadline The deadline to add liquidity by
     * @return _amounts The actual amounts of each token added
     * @return _liquidity The amount of liquidity added
     */
    function addLiquidityETH(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountMin, // Unused
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external payable override returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amountMin;
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        require(IPairFactory(factory_).isPair(_pair), "StableRouter: is not pair");

        // Transfer tokens from sender to contract and approve for pair
        _amounts = _amountDesireds;
        uint256 _amountETH;
        for (uint256 i = 0; i < _tokens.length; i++) {
            _tokens[i].safeApprove(_pair, _amounts[i]);
            if (_tokens[i] == address(weth_)) {
                _amountETH = _amounts[i];
                weth_.deposit{ value: _amounts[i] }();
                continue;
            }
            _tokens[i].safeTransferFrom(msg.sender, address(this), _amounts[i]);
        }

        // Add liquidity to pair
        _liquidity = IStablePair(_pair).addLiquidity(_tokens, _amounts, _minLiquidity, _to, _deadline);

        if (msg.value > _amountETH) TransferHelper.safeTransferETH(msg.sender, msg.value - _amountETH);
    }

    /**
     * @dev Remove liquidity from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _pairTokens The tokens in the pair
     * @return _amounts The actual amounts of each token received
     */
    function _removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) internal returns (address[] memory _pairTokens, uint256[] memory _amounts) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        require(IPairFactory(factory_).isPair(_pair), "StableRouter: is not pair"); // send liquidity to pair

        address _lpToken = IStablePair(_pair).lpToken();
        _lpToken.safeTransferFrom(msg.sender, address(this), _liquidity);
        _lpToken.safeApprove(_pair, _liquidity);

        _amounts = IStablePair(_pair).removeLiquidity(_liquidity, _tokens, _amountsMin, _to, _deadline);
        _pairTokens = IPair(_pair).tokens();
    }

    /**
     * @dev Transfer tokens out of the contract with ETH
     * @param _tokens The tokens to transfer out
     * @param _amounts The amounts of each token to transfer out
     * @param _to The address to send the tokens to
     */
    function _transferOutWithETH(address[] memory _tokens, uint256[] memory _amounts, address _to) internal {
        uint256 _amountETH;
        for (uint256 i = 0; i < _tokens.length; i++) {
            if (_tokens[i] == address(weth_)) {
                _amountETH = _amounts[i];
                weth_.withdraw(_amountETH);
                // _to.safeTransferETH(_amounts[i]);
                continue;
            }
            _tokens[i].safeTransfer(_to, _amounts[i]);
        }
        _to.safeTransferETH(_amountETH);
    }

    /**
     * @dev Remove liquidity from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _amounts The actual amounts of each token received
     */
    function removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override returns (uint256[] memory _amounts) {
        (, _amounts) = _removeLiquidity(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    /**
     * @dev Remove liquidity from a pair with ETH
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of each token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _amounts The actual amounts of each token received
     */
    function removeLiquidityETH(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override returns (uint256[] memory _amounts) {
        address[] memory _pairTokens;
        (_pairTokens, _amounts) = _removeLiquidity(_tokens, _liquidity, _amountsMin, address(this), _deadline);
        _transferOutWithETH(_pairTokens, _amounts, _to);
    }

    /**
     * @dev Remove liquidity from a pair for a single token
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive
     * @param _minAmount The minimum amount of token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _tokenAmount The actual amount of token received
     */
    function _removeLiquidityOneToken(
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) internal returns (uint256 _tokenAmount) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        require(IPairFactory(factory_).isPair(_pair), "StableRouter: is not pair");

        address _lpToken = IStablePair(_pair).lpToken();
        _lpToken.safeTransferFrom(msg.sender, address(this), _liquidity);
        _lpToken.safeApprove(_pair, _liquidity);

        _tokenAmount = IStablePair(_pair).removeLiquidityOneToken(_liquidity, _token, _minAmount, _to, _deadline);
    }

    /**
     * @dev Remove liquidity for a single token from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive
     * @param _minAmount The minimum amount of token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _tokenAmount The actual amount of token received
     */
    function removeLiquidityOneToken(
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) public override returns (uint256 _tokenAmount) {
        _tokenAmount = _removeLiquidityOneToken(_tokens, _liquidity, _token, _minAmount, _to, _deadline);
    }

    /**
     * @dev Remove liquidity for a single token from a pair with ETH
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive (must be WETH)
     * @param _minAmount The minimum amount of token to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _tokenAmount The actual amount of token received
     */
    function removeLiquidityOneTokenETH(
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline
    ) public override returns (uint256 _tokenAmount) {
        require(_token == address(weth_), "StableRouter: token must be WETH");

        _tokenAmount = _removeLiquidityOneToken(_tokens, _liquidity, _token, _minAmount, address(this), _deadline);

        weth_.withdraw(_tokenAmount);
        _to.safeTransferETH(_tokenAmount);
    }

    /**
     * @dev Remove liquidity imbalance for multiple tokens from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove liquidity for
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _actualBurnAmount The actual amount of liquidity burned
     */
    function _removeLiquidityImbalance(
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) internal returns (uint256 _actualBurnAmount) {
        // Get the pair address from the factory
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        // Ensure that the pair exists
        require(IPairFactory(factory_).isPair(_pair), "StableRouter: is not pair");

        // Get the LP token address
        address _lpToken = IStablePair(_pair).lpToken();
        // Transfer the LP tokens from the sender to this contract
        _lpToken.safeTransferFrom(msg.sender, address(this), _maxBurnAmount);
        // Approve the LP tokens for the pair
        _lpToken.safeApprove(_pair, _maxBurnAmount);

        // Remove the liquidity imbalance
        _actualBurnAmount = IStablePair(_pair).removeLiquidityImbalance(
            _tokens,
            _amounts,
            _maxBurnAmount,
            _to,
            _deadline
        );

        // If the actual burn amount is less than the maximum burn amount, transfer the remaining LP tokens back to the sender
        if (_maxBurnAmount > _actualBurnAmount) {
            _lpToken.safeApprove(_pair, 0);
            _lpToken.safeTransfer(msg.sender, _maxBurnAmount - _actualBurnAmount);
        }
    }

    /**
     * @dev Remove liquidity imbalance for multiple tokens from a pair
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove liquidity for
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _actualBurnAmount The actual amount of liquidity burned
     */
    function removeLiquidityImbalance(
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) public override returns (uint256 _actualBurnAmount) {
        _actualBurnAmount = _removeLiquidityImbalance(_tokens, _amounts, _maxBurnAmount, _to, _deadline);
    }

    /**
     * @dev Remove liquidity imbalance for multiple tokens from a pair with ETH
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove liquidity for
     * @param _maxBurnAmount The maximum amount of liquidity to remove
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @return _actualBurnAmount The actual amount of liquidity burned
     */
    function removeLiquidityImbalanceETH(
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline
    ) public override returns (uint256 _actualBurnAmount) {
        // Remove liquidity imbalance for multiple tokens from a pair
        _actualBurnAmount = _removeLiquidityImbalance(_tokens, _amounts, _maxBurnAmount, address(this), _deadline);
        // Transfer tokens out with ETH
        _transferOutWithETH(_tokens, _amounts, _to);
    }

    /**
     * @dev Approve the LP tokens for the pair using permit
     * @param _pair The address of the pair
     * @param _liquidity The amount of liquidity to approve
     * @param _deadline The deadline to approve by
     * @param _approveMax Whether to approve the maximum amount of liquidity
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     */
    function _withPermit(
        address _pair,
        uint256 _liquidity,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) internal {
        // Determine the value to approve
        uint256 _value = _approveMax ? uint256(-1) : _liquidity;
        // Get the LP token address
        address _lpToken = IStablePair(_pair).lpToken();
        // Approve the LP tokens using permit
        IPairERC20(_lpToken).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
    }

    /**
     * @dev Remove liquidity with permit
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of tokens to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @param _approveMax Whether to approve the maximum amount of liquidity
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _amounts The actual amounts of tokens received
     */
    function removeLiquidityWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        // Approve the LP tokens for the pair using permit
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _liquidity,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );

        // Remove liquidity for multiple tokens from a pair
        _amounts = removeLiquidity(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    /**
     * @dev Remove liquidity with permit
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _amountsMin The minimum amounts of tokens to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @param _approveMax Whether to approve the maximum amount of liquidity
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _amounts The actual amounts of tokens received
     */
    function removeLiquidityETHWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        // Approve the LP tokens for the pair using permit
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _liquidity,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );

        // Remove liquidity for ETH from a pair
        _amounts = removeLiquidityETH(_tokens, _liquidity, _amountsMin, _to, _deadline);
    }

    /**
     * @dev Remove liquidity for a single token with permit
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive
     * @param _minAmount The minimum amount of tokens to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @param _approveMax Whether to approve the maximum amount of liquidity
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _tokenAmount The actual amount of tokens received
     */
    function removeLiquidityOneTokenWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256 _tokenAmount) {
        // Approve the LP tokens for the pair using permit
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _liquidity,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );

        // Remove liquidity for a single token from a pair
        _tokenAmount = removeLiquidityOneToken(_tokens, _liquidity, _token, _minAmount, _to, _deadline);
    }

    /**
     * @dev Remove liquidity for a single token with permit and receive ETH
     * @param _tokens The tokens to remove liquidity for
     * @param _liquidity The amount of liquidity to remove
     * @param _token The token to receive
     * @param _minAmount The minimum amount of tokens to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @param _approveMax Whether to approve the maximum amount of liquidity
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _tokenAmount The actual amount of tokens received
     */
    function removeLiquidityOneTokenETHWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        address _token,
        uint256 _minAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256 _tokenAmount) {
        // Approve the LP tokens for the pair using permit
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _liquidity,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );

        // Remove liquidity for a single token from a pair and receive ETH
        _tokenAmount = removeLiquidityOneTokenETH(_tokens, _liquidity, _token, _minAmount, _to, _deadline);
    }

    /**
     * @dev Remove liquidity with permit when the amount of tokens to remove is imbalanced
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove
     * @param _maxBurnAmount The maximum amount of liquidity to burn
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @param _approveMax Whether to approve the maximum amount of liquidity
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _actualBurnAmount The actual amount of liquidity burned
     */
    function removeLiquidityImbalanceWithPermit(
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256 _actualBurnAmount) {
        // Approve the LP tokens for the pair using permit
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _maxBurnAmount,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );

        // Remove liquidity with imbalanced amounts of tokens from a pair
        _actualBurnAmount = removeLiquidityImbalance(_tokens, _amounts, _maxBurnAmount, _to, _deadline);
    }

    /**
     * @dev Remove liquidity with permit when the amount of tokens to remove is imbalanced and receive ETH
     * @param _tokens The tokens to remove liquidity for
     * @param _amounts The amounts of tokens to remove
     * @param _maxBurnAmount The maximum amount of liquidity to burn
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to remove liquidity by
     * @param _approveMax Whether to approve the maximum amount of liquidity
     * @param _v The v value of the permit signature
     * @param _r The r value of the permit signature
     * @param _s The s value of the permit signature
     * @return _actualBurnAmount The actual amount of liquidity burned
     */
    function removeLiquidityImbalanceETHWithPermit(
        address[] memory _tokens,
        uint256[] memory _amounts,
        uint256 _maxBurnAmount,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256 _actualBurnAmount) {
        // Approve the LP tokens for the pair using permit
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _maxBurnAmount,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );

        // Remove liquidity with imbalanced amounts of tokens from a pair and receive ETH
        _actualBurnAmount = removeLiquidityImbalanceETH(_tokens, _amounts, _maxBurnAmount, _to, _deadline);
    }

    /**
     * @dev Swap tokens through a route
     * @param _route The route to swap through
     * @param _amountIn The amount of tokens to swap in
     * @param _amountOutMin The minimum amount of tokens to receive
     * @param _to The address to send the tokens to
     * @param _deadline The deadline to swap by
     * @return _amountOut The actual amount of tokens received
     */
    function swap(
        Route memory _route,
        uint256 _amountIn,
        uint256 _amountOutMin,
        address _to,
        uint256 _deadline
    ) external payable override returns (uint256 _amountOut) {
        // Ensure that the pair exists
        require(IPairFactory(factory_).isPair(_route.pair), "StableRouter: is not pair");

        // Approve the pair to spend the input token
        _route.from.safeApprove(_route.pair, _amountIn);

        // Swap the tokens through the route
        _amountOut = IStablePair(_route.pair).swap(_route.from, _route.to, _amountIn, _amountOutMin, _to, _deadline);
    }
}

contracts/routers/VolatileRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";

import "../utils/TransferHelper.sol";
import "../utils/Math.sol";

import "../interface/IPairRouter.sol";
import "../interface/IPairFactory.sol";
import "../interface/IPair.sol";
import "../interface/IWETH.sol";
import "../interface/IPairERC20.sol";

/**
 * @title VolatileRouter
 * @notice Router contract for volatile pairs
 */

contract VolatileRouter is IVolatileRouter {
    using SafeMath for uint256;
    using TransferHelper for address;

    // Pair type for volatile pairs
    uint8 internal constant PAIR_TYPE_ = 1;
    // Minimum liquidity for volatile pairs
    uint256 internal constant MINIMUM_LIQUIDITY = 10 ** 3;

    // Address of the factory contract
    address internal immutable factory_;
    // Wrapped Ether contract
    IWETH internal immutable weth_;

    /**
     * @dev Constructor function for VolatileRouter
     * @param _factory Address of the factory contract
     * @param _weth Address of the Wrapped Ether contract
     */
    constructor(address _factory, address _weth) public {
        factory_ = _factory;
        weth_ = IWETH(_weth);
    }

    /**
     * @dev Modifier to ensure that the deadline has not passed
     * @param _deadline Deadline timestamp
     */
    modifier ensure(uint256 _deadline) {
        require(_deadline >= block.timestamp, "VolatileRouter: EXPIRED");
        _;
    }

    /**
     * @dev Returns the pair type for volatile pairs
     * @return Pair type
     */
    function PAIR_TYPE() external view override returns (uint8) {
        return PAIR_TYPE_;
    }

    /**
     * @dev Returns the address of the factory contract
     * @return Factory contract address
     */
    function factory() external view returns (address) {
        return factory_;
    }

    /**
     * @dev Returns the address of the Wrapped Ether contract
     * @return Wrapped Ether contract address
     */
    function weth() external view returns (address) {
        return address(weth_);
    }

    /**
     * @dev Returns the reserves of the given pair and tokens
     * @param _pair Address of the pair contract
     * @param _tokens Array of token addresses
     * @return _reserveA Reserve of token A
     * @return _reserveB Reserve of token B
     */
    function getReserves(
        address _pair,
        address[] memory _tokens
    ) external view override returns (uint256 _reserveA, uint256 _reserveB) {
        (_reserveA, _reserveB) = _getReserves(_pair, _tokens);
    }

    /**
     * @dev Returns the reserves of the given pair and tokens
     * @param _pair Address of the pair contract
     * @param _tokens Array of token addresses
     * @return _reserveA Reserve of token A
     * @return _reserveB Reserve of token B
     */
    function _getReserves(
        address _pair,
        address[] memory _tokens
    ) internal view returns (uint256 _reserveA, uint256 _reserveB) {
        address _token0 = IVolatilePair(_pair).token0();
        (uint112 _reserve0, uint112 _reserve1, ) = IVolatilePair(_pair).getReserves();
        (_reserveA, _reserveB) = _tokens[0] == _token0
            ? (uint256(_reserve0), uint256(_reserve1))
            : (uint256(_reserve1), uint256(_reserve0));
    }

    /**
     * @dev Calculates the optimal amount of token B to add to a pair given an amount of token A
     * @param _amountA Amount of token A to add
     * @param _reserveA Reserve of token A in the pair
     * @param _reserveB Reserve of token B in the pair
     * @return _amountB Amount of token B to add
     */
    function _quoteLiquidity(
        uint256 _amountA,
        uint256 _reserveA,
        uint256 _reserveB
    ) internal pure returns (uint256 _amountB) {
        require(_amountA > 0, "VolatileRouter: INSUFFICIENT_AMOUNT");
        require(_reserveA > 0 && _reserveB > 0, "VolatileRouter: INSUFFICIENT_LIQUIDITY");
        _amountB = _amountA.mul(_reserveB) / _reserveA;
    }

    /**
     * @dev Calculates the optimal amount of token A and token B to add to a pair given desired amounts of each
     * @param _tokens Array of token addresses
     * @param _amountDesireds Array of desired amounts of each token
     * @return _amountsIn Array of amounts of each token to add
     * @return _liquidity Amount of liquidity to add
     */
    function quoteAddLiquidity(
        address[] calldata _tokens,
        uint256[] calldata _amountDesireds
    ) external view override returns (uint256[] memory _amountsIn, uint256 _liquidity) {
        _amountsIn = new uint256[](_amountDesireds.length);
        // create the pair if it doesn't exist yet
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        if (!IPairFactory(factory_).isPair(_pair)) return (_amountsIn, _liquidity);

        (uint256 _reserveA, uint256 _reserveB) = (0, 0);
        uint256 _totalSupply = 0;
        if (_pair != address(0)) {
            _totalSupply = IPairERC20(_pair).totalSupply();
            (_reserveA, _reserveB) = _getReserves(_pair, _tokens);
        }

        if (_reserveA == 0 && _reserveB == 0) {
            _amountsIn = _amountDesireds;
            _liquidity = Math.sqrt(_amountsIn[0].mul(_amountsIn[1])).sub(MINIMUM_LIQUIDITY);
        } else {
            uint256 amountBOptimal = _quoteLiquidity(_amountDesireds[0], _reserveA, _reserveB);
            if (amountBOptimal <= _amountDesireds[1]) {
                (_amountsIn[0], _amountsIn[1]) = (_amountDesireds[0], amountBOptimal);
                _liquidity = Math.min(
                    _amountsIn[0].mul(_totalSupply).div(_reserveA),
                    _amountsIn[1].mul(_totalSupply).div(_reserveB)
                );
            } else {
                uint256 amountAOptimal = _quoteLiquidity(_amountDesireds[1], _reserveB, _reserveA);
                (_amountsIn[0], _amountsIn[1]) = (amountAOptimal, _amountDesireds[1]);
                _liquidity = Math.min(
                    _amountsIn[0].mul(_totalSupply).div(_reserveA),
                    _amountsIn[1].mul(_totalSupply).div(_reserveB)
                );
            }
        }
    }

    /**
     * @dev Calculates the amount of tokens to receive upon removing liquidity from a pair
     * @param _tokens Array of token addresses
     * @param _liquidity Amount of liquidity to remove
     * @return _amounts Array of amounts of each token to receive
     */
    function quoteRemoveLiquidity(
        address[] calldata _tokens,
        uint256 _liquidity
    ) external view override returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        _amounts = new uint256[](2);
        if (!IPairFactory(factory_).isPair(_pair)) {
            return _amounts;
        }

        (uint256 _reserveA, uint256 _reserveB) = _getReserves(_pair, _tokens);
        uint256 _totalSupply = IPairERC20(_pair).totalSupply();
        if (_totalSupply > 0) {
            _amounts[0] = _liquidity.mul(_reserveA) / _totalSupply; // calculate the amount of token A to receive
            _amounts[1] = _liquidity.mul(_reserveB) / _totalSupply; // calculate the amount of token B to receive
        }
    }

    /**
     * @dev Adds liquidity to a pair of tokens
     * @param _tokens Array of token addresses
     * @param _amountDesireds Array of desired amounts of each token
     * @param _amountsMin Array of minimum amounts of each token
     * @return _amountA Amount of token A added to the liquidity pool
     * @return _amountB Amount of token B added to the liquidity pool
     */
    function _addLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin
    ) internal returns (uint256 _amountA, uint256 _amountB) {
        require(_amountDesireds[0] >= _amountsMin[0], "VolatileRouter: token[0] desired invalid");
        require(_amountDesireds[1] >= _amountsMin[1], "VolatileRouter: token[1] desired invalid");

        // create the pair if it doesn't exist yet
        (uint256 _reserveA, uint256 _reserveB) = (0, 0);
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        if (!IPairFactory(factory_).isPair(_pair)) {
            _pair = IPairFactory(factory_).createPair(_tokens, PAIR_TYPE_, new bytes(0));
        } else {
            (_reserveA, _reserveB) = _getReserves(_pair, _tokens);
        }

        require(_pair != address(0), "VolatileRouter: pair does not exist");
        if (_reserveA == 0 && _reserveB == 0) {
            (_amountA, _amountB) = (_amountDesireds[0], _amountDesireds[1]);
        } else {
            uint256 _amountBOptimal = _quoteLiquidity(_amountDesireds[0], _reserveA, _reserveB);
            if (_amountBOptimal <= _amountDesireds[1]) {
                require(_amountBOptimal >= _amountsMin[1], "VolatileRouter: INSUFFICIENT_B_AMOUNT");
                (_amountA, _amountB) = (_amountDesireds[0], _amountBOptimal);
            } else {
                uint256 _amountAOptimal = _quoteLiquidity(_amountDesireds[1], _reserveB, _reserveA);
                assert(_amountAOptimal <= _amountDesireds[0]);
                require(_amountAOptimal >= _amountsMin[0], "VolatileRouter: INSUFFICIENT_A_AMOUNT");
                (_amountA, _amountB) = (_amountAOptimal, _amountDesireds[1]);
            }
        }
    }

    /**
     * @dev Adds liquidity to a pair of tokens
     * @param _tokens Array of token addresses
     * @param _amountDesireds Array of desired amounts of each token
     * @param _amountsMin Array of minimum amounts of each token
     * @param _minLiquidity Minimum amount of liquidity to mint
     * @param _to Address to receive the minted liquidity
     * @param _deadline Timestamp after which the transaction will revert
     * @return _amounts Amounts of tokens added to the liquidity pool
     * @return _liquidity Amount of liquidity minted
     */
    function addLiquidity(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = _amountDesireds;
        (_amounts[0], _amounts[1]) = _addLiquidity(_tokens, _amountDesireds, _amountsMin);
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        for (uint256 i = 0; i < _tokens.length; i++) {
            // Transfer tokens from sender to pair
            _tokens[i].safeTransferFrom(msg.sender, _pair, _amounts[i]);
        }
        // Mint liquidity and transfer to recipient
        _liquidity = IVolatilePair(_pair).mint(_to);
        require(_liquidity >= _minLiquidity, "VolatileRouter: Couldn't mint min requested");
    }

    /**
     * @dev Adds liquidity to a pair of tokens
     * @param _tokens Array of token addresses
     * @param _amountDesireds Array of desired amounts of each token
     * @param _amountsMin Array of minimum amounts of each token
     * @param _minLiquidity Minimum amount of liquidity to mint
     * @param _to Address to receive the minted liquidity
     * @param _deadline Timestamp after which the transaction will revert
     * @return _amounts Amounts of tokens added to the liquidity pool
     * @return _liquidity Amount of liquidity minted
     */
    function addLiquidityETH(
        address[] memory _tokens,
        uint256[] memory _amountDesireds,
        uint256[] memory _amountsMin,
        uint256 _minLiquidity,
        address _to,
        uint256 _deadline
    ) external payable override ensure(_deadline) returns (uint256[] memory _amounts, uint256 _liquidity) {
        _amounts = _amountDesireds;
        (_amounts[0], _amounts[1]) = _addLiquidity(_tokens, _amountDesireds, _amountsMin);
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        uint256 _amountETH;
        for (uint256 i = 0; i < _tokens.length; i++) {
            if (_tokens[i] == address(weth_)) {
                _amountETH = _amounts[i];
                weth_.deposit{ value: _amounts[i] }();
                assert(weth_.transfer(_pair, _amounts[i]));
                continue;
            }

            _tokens[i].safeTransferFrom(msg.sender, _pair, _amounts[i]);
        }
        _liquidity = IVolatilePair(_pair).mint(_to);
        require(_liquidity >= _minLiquidity, "VolatileRouter: Couldn't mint min requested");

        if (msg.value > _amountETH) TransferHelper.safeTransferETH(msg.sender, msg.value - _amountETH);
    }

    /**
     * @dev Removes liquidity from a pair of tokens
     * @param _tokens Array of token addresses
     * @param _liquidity Amount of liquidity to remove
     * @param _amountsMin Array of minimum amounts of each token to receive
     * @param _to Address to receive the tokens
     * @return _amounts Amounts of tokens received
     */
    function _removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to
    ) internal returns (uint256[] memory _amounts) {
        address _pair = IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_);
        require(IPairFactory(factory_).isPair(_pair), "VolatileRouter: is not pair");

        IPairERC20(_pair).transferFrom(msg.sender, _pair, _liquidity);
        (uint256 _amount0, uint256 _amount1) = IVolatilePair(_pair).burn(_to);

        address _token0 = IVolatilePair(_pair).token0();
        _amounts = new uint256[](_amountsMin.length);
        (_amounts[0], _amounts[1]) = _token0 == _tokens[0] ? (_amount0, _amount1) : (_amount1, _amount0);

        for (uint256 i = 0; i < _amounts.length; i++) {
            require(_amounts[i] >= _amountsMin[i], "VolatileRouter: _amount < _amountsMin");
        }
    }

    /**
     * @dev Removes liquidity from a pair of tokens
     * @param _tokens Array of token addresses
     * @param _liquidity Amount of liquidity to remove
     * @param _amountsMin Array of minimum amounts of each token to receive
     * @param _to Address to receive the tokens
     * @param _deadline Timestamp after which the transaction will revert
     * @return _amounts Amounts of tokens received
     */
    function removeLiquidity(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override ensure(_deadline) returns (uint256[] memory _amounts) {
        _amounts = _removeLiquidity(_tokens, _liquidity, _amountsMin, _to);
    }

    /**
     * @dev Removes liquidity from a pair of tokens with ETH
     * @param _tokens Array of token addresses
     * @param _liquidity Amount of liquidity to remove
     * @param _amountsMin Array of minimum amounts of each token to receive
     * @param _to Address to receive the tokens
     * @param _deadline Timestamp after which the transaction will revert
     * @return _amounts Amounts of tokens received
     */
    function removeLiquidityETH(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline
    ) public override ensure(_deadline) returns (uint256[] memory _amounts) {
        _amounts = _removeLiquidity(_tokens, _liquidity, _amountsMin, address(this));
        uint256 _amountETH;
        for (uint256 i = 0; i < _tokens.length; i++) {
            if (_tokens[i] == address(weth_)) {
                _amountETH = _amounts[i];
                weth_.withdraw(_amountETH);
                // _to.safeTransferETH(_amounts[i]);
                continue;
            }
            _tokens[i].safeTransfer(_to, _amounts[i]);
        }
        _to.safeTransferETH(_amountETH);
    }

    /**
     * @dev Adds permit functionality to removeLiquidity and removeLiquidityETH functions
     * @param _pair Address of the pair
     * @param _liquidity Amount of liquidity to remove
     * @param _deadline Timestamp after which the transaction will revert
     * @param _approveMax Whether to approve the maximum amount or not
     * @param _v ECDSA signature parameter v
     * @param _r ECDSA signature parameter r
     * @param _s ECDSA signature parameter s
     */
    function _withPermit(
        address _pair,
        uint256 _liquidity,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) internal {
        uint256 _value = _approveMax ? uint256(-1) : _liquidity;
        IPairERC20(_pair).permit(msg.sender, address(this), _value, _deadline, _v, _r, _s);
    }

    /**
     * @dev Removes liquidity from a pair of tokens with permit functionality
     * @param _tokens Array of token addresses
     * @param _liquidity Amount of liquidity to remove
     * @param _amountMin Array of minimum amounts of each token to receive
     * @param _to Address to receive the tokens
     * @param _deadline Timestamp after which the transaction will revert
     * @param _approveMax Whether to approve the maximum amount or not
     * @param _v ECDSA signature parameter v
     * @param _r ECDSA signature parameter r
     * @param _s ECDSA signature parameter s
     * @return _amounts Amounts of tokens received
     */
    function removeLiquidityWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _liquidity,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );
        _amounts = removeLiquidity(_tokens, _liquidity, _amountMin, _to, _deadline);
        return _amounts;
    }

    /**
     * @dev Removes liquidity from a pair of tokens with ETH and permit functionality
     * @param _tokens Array of token addresses
     * @param _liquidity Amount of liquidity to remove
     * @param _amountsMin Array of minimum amounts of each token to receive
     * @param _to Address to receive the tokens
     * @param _deadline Timestamp after which the transaction will revert
     * @param _approveMax Whether to approve the maximum amount or not
     * @param _v ECDSA signature parameter v
     * @param _r ECDSA signature parameter r
     * @param _s ECDSA signature parameter s
     * @return _amounts Amounts of tokens received
     */
    function removeLiquidityETHWithPermit(
        address[] memory _tokens,
        uint256 _liquidity,
        uint256[] memory _amountsMin,
        address _to,
        uint256 _deadline,
        bool _approveMax,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external override returns (uint256[] memory _amounts) {
        _withPermit(
            IPairFactory(factory_).getPairAddress(_tokens, PAIR_TYPE_),
            _liquidity,
            _deadline,
            _approveMax,
            _v,
            _r,
            _s
        );
        _amounts = removeLiquidityETH(_tokens, _liquidity, _amountsMin, _to, _deadline);
        return _amounts;
    }

    /**
     * @dev Swaps tokens according to the given route
     * @param _route Route containing the pair and the token to swap from
     * @param _amountIn Amount of tokens to swap
     * @param _amountOut Expected amount of tokens to receive
     * @param _to Address to receive the swapped tokens
     */
    function _swap(Route memory _route, uint256 _amountIn, uint256 _amountOut, address _to) internal {
        require(IPairFactory(factory_).isPair(_route.pair), "VolatileRouter: is not pair");
        address _token0 = IVolatilePair(_route.pair).token0();
        (uint256 _amount0Out, uint256 _amount1Out) = _route.from == _token0
            ? (uint256(0), _amountOut)
            : (_amountOut, uint256(0));

        _route.from.safeTransfer(_route.pair, _amountIn);

        IVolatilePair(_route.pair).swap(_amount0Out, _amount1Out, _to, new bytes(0));
    }

    /**
     * @dev Swaps tokens according to the given route
     * @param _route Route containing the pair and the token to swap from
     * @param _amountIn Amount of tokens to swap
     * @param _amountOut Expected amount of tokens to receive
     * @param _to Address to receive the swapped tokens
     * @param _deadline Timestamp after which the transaction will revert
     * @return Amount of tokens received
     */
    function swap(
        Route memory _route,
        uint256 _amountIn,
        uint256 _amountOut,
        address _to,
        uint256 _deadline
    ) external payable override ensure(_deadline) returns (uint256) {
        _swap(_route, _amountIn, _amountOut, _to);

        return _amountOut;
    }
}

contracts/StablePair.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts-upgradeable/proxy/Initializable.sol";
import "./utils/SwapUtils.sol";
import "./utils/AmplificationUtils.sol";
import "./utils/ERC20Call.sol";
import "./interface/IPairERC20.sol";
import "./interface/IPairFactory.sol";
import "./interface/IPair.sol";

/**
 * @title Swap - A StableSwap implementation in solidity.
 * @notice This contract is responsible for custody of closely pegged assets (eg. group of stablecoins)
 * and automatic market making system. Users become an LP (Liquidity Provider) by depositing their tokens
 * in desired ratios for an exchange of the pool token that represents their share of the pool.
 * Users can burn pool tokens and withdraw their share of token(s).
 *
 * Each time a swap between the pooled tokens happens, a set fee incurs which effectively gets
 * distributed to the LPs.
 *
 * In case of emergencies, admin can pause additional deposits, swaps, or single-asset withdraws - which
 * stops the ratio of the tokens in the pool from changing.
 * Users can always withdraw their tokens via multi-asset withdraws.
 *
 * @dev Most of the logic is stored as a library `SwapUtils` for the sake of reducing contract's
 * deployment size.
 */
contract StablePair is Initializable, IStablePair {
    using SwapUtils for SwapUtils.Swap;
    using AmplificationUtils for SwapUtils.Swap;
    using ERC20Call for address;

    // The type of the pair
    uint8 public constant override PAIR_TYPE = 2;
    // Whether the pair is authorized
    bool public constant override AUTH = true;

    // The factory that created this pair
    address public factory;

    // A lock to prevent reentrancy
    uint256 private unlocked_;

    // Struct storing data responsible for automatic market maker functionalities. In order to
    // access this data, this contract uses SwapUtils library. For more details, see SwapUtils.sol
    SwapUtils.Swap public swapStorage;

    // Maps token address to an index in the pool. Used to prevent duplicate tokens in the pool.
    // getTokenIndex function also relies on this mapping to retrieve token index.
    mapping(address => uint8) internal tokenIndexes_;

    /*** EVENTS ***/

    // events replicated from SwapUtils to make the ABI easier for dumb
    // clients
    /**
     * @dev Event emitted when a token swap occurs.
     * @param buyer The address of the buyer.
     * @param tokensSold The amount of tokens sold.
     * @param tokensBought The amount of tokens bought.
     * @param soldId The ID of the sold token.
     * @param boughtId The ID of the bought token.
     */
    event TokenSwap(address indexed buyer, uint256 tokensSold, uint256 tokensBought, uint128 soldId, uint128 boughtId);

    /**
     * @dev Event emitted when liquidity is added to the pool.
     * @param provider The address of the liquidity provider.
     * @param tokenAmounts The amounts of tokens added.
     * @param fees The fees paid for adding liquidity.
     * @param invariant The invariant of the pool.
     * @param lpTokenSupply The total supply of LP tokens.
     */
    event AddLiquidity(
        address indexed provider,
        uint256[] tokenAmounts,
        uint256[] fees,
        uint256 invariant,
        uint256 lpTokenSupply
    );

    /**
     * @dev Event emitted when liquidity is removed from the pool.
     * @param provider The address of the liquidity provider.
     * @param tokenAmounts The amounts of tokens removed.
     * @param lpTokenSupply The total supply of LP tokens.
     */
    event RemoveLiquidity(address indexed provider, uint256[] tokenAmounts, uint256 lpTokenSupply);

    /**
     * @dev Event emitted when a single asset is removed from the pool.
     * @param provider The address of the liquidity provider.
     * @param lpTokenAmount The amount of LP tokens burned.
     * @param lpTokenSupply The total supply of LP tokens.
     * @param boughtId The ID of the bought token.
     * @param tokensBought The amount of tokens bought.
     */
    event RemoveLiquidityOne(
        address indexed provider,
        uint256 lpTokenAmount,
        uint256 lpTokenSupply,
        uint256 boughtId,
        uint256 tokensBought
    );

    /**
     * @dev Event emitted when liquidity is removed from the pool in an imbalanced way.
     * @param provider The address of the liquidity provider.
     * @param tokenAmounts The amounts of tokens removed.
     * @param fees The fees paid for removing liquidity.
     * @param invariant The invariant of the pool.
     * @param lpTokenSupply The total supply of LP tokens.
     */
    event RemoveLiquidityImbalance(
        address indexed provider,
        uint256[] tokenAmounts,
        uint256[] fees,
        uint256 invariant,
        uint256 lpTokenSupply
    );

    /**
     * @dev Event emitted when the swap fees are updated.
     * @param tokens The tokens in the pool.
     * @param swapFees The new swap fees.
     */
    event SwapFee(address[] tokens, uint256[] swapFees);

    /**
     * @dev Event emitted when the admin fee is updated.
     * @param newAdminFee The new admin fee.
     */
    event NewAdminFee(uint256 newAdminFee);

    /**
     * @dev Event emitted when the swap fee is updated.
     * @param newSwapFee The new swap fee.
     */
    event NewSwapFee(uint256 newSwapFee);

    /**
     * @dev Event emitted when the withdraw fee is updated.
     * @param newWithdrawFee The new withdraw fee.
     */
    event NewWithdrawFee(uint256 newWithdrawFee);

    /**
     * @dev Event emitted when the amplification coefficient is ramped.
     * @param oldA The old amplification coefficient.
     * @param newA The new amplification coefficient.
     * @param initialTime The initial time.
     * @param futureTime The future time.
     */
    event RampA(uint256 oldA, uint256 newA, uint256 initialTime, uint256 futureTime);

    /**
     * @dev Event emitted when the amplification coefficient ramp is stopped.
     * @param currentA The current amplification coefficient.
     * @param time The time.
     */
    event StopRampA(uint256 currentA, uint256 time);

    /**
     * @dev Initializes this StablePair contract with the given parameters.
     * @param _tokens an array of ERC20s this pool will accept
     * @param _data encoded parameters for the StablePair contract
     */
    function initialize(address[] calldata _tokens, bytes calldata _data) external override initializer {
        factory = msg.sender;
        unlocked_ = 1;
        string memory _lpTokenName = "dForce AMM Stable - ";
        string memory _lpTokenSymbol = "sAMM-";
        string memory _separator = "-";
        uint8[] memory _decimals = new uint8[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _decimals[i] = IPairERC20(_tokens[i]).decimals();
            string memory _tokenSymbol = _tokens[i].callSymbol();
            if (i == _tokens.length - 1) _separator = "";
            _lpTokenName = string(abi.encodePacked(_lpTokenName, _tokenSymbol, _separator));
            _lpTokenSymbol = string(abi.encodePacked(_lpTokenSymbol, _tokenSymbol, _separator));
        }

        (uint256 _swapFee, uint256 _adminFeeRate, uint256 _a, address _lpTokenTargetAddress) = abi.decode(
            _data,
            (uint256, uint256, uint256, address)
        );
        __SwapV2_init(
            _tokens,
            _decimals,
            _lpTokenName,
            _lpTokenSymbol,
            _a,
            _swapFee,
            _adminFeeRate,
            _lpTokenTargetAddress
        );
    }

    /**
     * @notice Initializes this Swap contract with the given parameters.
     * This will also clone a LPToken contract that represents users'
     * LP positions. The owner of LPToken will be this contract - which means
     * only this contract is allowed to mint/burn tokens.
     *
     * @param _pooledTokens an array of ERC20s this pool will accept
     * @param _decimals the decimals to use for each pooled token,
     * eg 8 for WBTC. Cannot be larger than POOL_PRECISION_DECIMALS
     * @param _lpTokenName the long-form name of the token to be deployed
     * @param _lpTokenSymbol the short symbol for the token to be deployed
     * @param _a the amplification coefficient * n * (n - 1). See the
     * StableSwap paper for details
     * @param _fee default swap fee to be initialized with
     * @param _adminFee default adminFee to be initialized with
     * @param _lpTokenTargetAddress the address of an existing LPToken contract to use as a target
     */
    function __SwapV2_init(
        address[] memory _pooledTokens,
        uint8[] memory _decimals,
        string memory _lpTokenName,
        string memory _lpTokenSymbol,
        uint256 _a,
        uint256 _fee,
        uint256 _adminFee,
        address _lpTokenTargetAddress
    ) internal virtual {
        // Check _pooledTokens and precisions parameter
        require(_pooledTokens.length > 1, "StablePair: _pooledTokens.length <= 1");
        require(_pooledTokens.length <= 32, "StablePair: _pooledTokens.length > 32");
        require(_pooledTokens.length == _decimals.length, "StablePair: _pooledTokens decimals mismatch");

        uint256[] memory _precisionMultipliers = new uint256[](_decimals.length);
        IERC20[] memory _poolTokens = new IERC20[](_decimals.length);

        for (uint8 i = 0; i < _pooledTokens.length; i++) {
            if (i > 0) {
                // Check if index is already used. Check if 0th element is a duplicate.
                require(
                    tokenIndexes_[_pooledTokens[i]] == 0 && _pooledTokens[0] != _pooledTokens[i],
                    "StablePair: Duplicate tokens"
                );
            }
            require(
                _pooledTokens[i] != address(0) && _pooledTokens[i] != address(this),
                "StablePair: The 0 address isn't an ERC-20"
            );
            require(_decimals[i] <= SwapUtils.POOL_PRECISION_DECIMALS, "StablePair: Token decimals exceeds max");
            _precisionMultipliers[i] = 10 ** (uint256(SwapUtils.POOL_PRECISION_DECIMALS) - uint256(_decimals[i]));
            _poolTokens[i] = IERC20(_pooledTokens[i]);
            tokenIndexes_[_pooledTokens[i]] = i;
        }

        // Check _a, _fee, _adminFee, _withdrawFee parameters
        require(_a < AmplificationUtils.MAX_A, "StablePair: _a exceeds maximum");
        require(_fee < SwapUtils.MAX_SWAP_FEE, "StablePair: _fee exceeds maximum");
        require(_adminFee < SwapUtils.MAX_ADMIN_FEE, "StablePair: _adminFee exceeds maximum");

        // Clone and initialize a LPToken contract
        LPToken _lpToken = LPToken(Clones.clone(_lpTokenTargetAddress));
        require(_lpToken.initialize(_lpTokenName, _lpTokenSymbol), "StablePair: could not init lpToken clone");

        // Initialize swapStorage struct
        swapStorage.lpToken = _lpToken;
        swapStorage.pooledTokens = _poolTokens;
        swapStorage.tokenPrecisionMultipliers = _precisionMultipliers;
        swapStorage.balances = new uint256[](_pooledTokens.length);
        swapStorage.initialA = _a * AmplificationUtils.A_PRECISION;
        swapStorage.futureA = _a * AmplificationUtils.A_PRECISION;
        // swapStorage.initialATime = 0;
        // swapStorage.futureATime = 0;
        swapStorage.swapFee = _fee;
        swapStorage.adminFee = _adminFee;
    }

    /*** MODIFIERS ***/

    /**
     * @notice Modifier to check sender against factory manager.
     */
    modifier onlyManager() {
        require(IPairFactory(factory).manager() == msg.sender, "StablePair: : not manager");
        _;
    }

    /**
     * @notice contract function lock modifier.
     */
    modifier lock() {
        require(unlocked_ == 1, "StablePair: LOCKED");
        unlocked_ = 0;
        _;
        unlocked_ = 1;
    }

    /**
     * @notice Modifier to check _deadline against current timestamp
     * @param _deadline latest timestamp to accept this transaction
     */
    modifier deadlineCheck(uint256 _deadline) {
        require(block.timestamp <= _deadline, "StablePair: Deadline not met");
        _;
    }

    /*** VIEW FUNCTIONS ***/

    /**
     * @notice Return A, the amplification coefficient * n * (n - 1)
     * @dev See the StableSwap paper for details
     * @return A parameter
     */
    function getA() external view virtual returns (uint256) {
        return swapStorage.getA();
    }

    /**
     * @notice Return A in its raw precision form
     * @dev See the StableSwap paper for details
     * @return A parameter in its raw precision form
     */
    function getAPrecise() external view virtual returns (uint256) {
        return swapStorage.getAPrecise();
    }

    /**
     * @notice Return address of the pooled token at given index. Reverts if _tokenIndex is out of range.
     * @param _index the index of the token
     * @return address of the token at given index
     */
    function getToken(uint8 _index) public view virtual returns (address) {
        require(_index < swapStorage.pooledTokens.length, "StablePair: Out of range");
        return address(swapStorage.pooledTokens[_index]);
    }

    /**
     * @notice Query all token addresses in pair.
     * @return _tokens all token addresses
     */
    function tokens() external view override returns (address[] memory _tokens) {
        _tokens = new address[](swapStorage.pooledTokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) _tokens[i] = address(swapStorage.pooledTokens[i]);
    }

    /**
     * @notice Query lpToken addresse.
     * @return lpToken addresse
     */
    function lpToken() external view override returns (address) {
        return address(swapStorage.lpToken);
    }

    /**
     * @notice Return the index of the given token address. Reverts if no matching
     * token is found.
     * @param _tokenAddress address of the token
     * @return the index of the given token address
     */
    function getTokenIndex(address _tokenAddress) public view virtual returns (uint8) {
        uint8 _index = tokenIndexes_[_tokenAddress];
        require(getToken(_index) == _tokenAddress, "StablePair: Token does not exist");
        return _index;
    }

    /**
     * @notice Return current balance of the pooled token at given index
     * @param _index the index of the token
     * @return current balance of the pooled token at given index with token's native precision
     */
    function getTokenBalance(uint8 _index) external view virtual returns (uint256) {
        require(_index < swapStorage.pooledTokens.length, "StablePair: Index out of range");
        return swapStorage.balances[_index];
    }

    /**
     * @notice Return current balances of the pooled tokens
     * @return current balances of the pooled tokens
     */
    function getTokenBalances() external view virtual returns (uint256[] memory) {
        return swapStorage.balances;
    }

    /**
     * @notice Get the virtual price, to help calculate profit
     * @return the virtual price, scaled to the POOL_PRECISION_DECIMALS
     */
    function getVirtualPrice() external view virtual returns (uint256) {
        return swapStorage.getVirtualPrice();
    }

    /**
     * @notice Calculate amount of tokens you receive on swap
     * @param _tokenFrom the token address the user wants to sell
     * @param _tokenTo the token address the user wants to buy
     * @param _dx the amount of tokens the user wants to sell. If the token charges
     * a fee on transfers, use the amount that gets transferred after the fee.
     * @return amount of tokens the user will receive
     */
    function getAmountOut(address _tokenFrom, address _tokenTo, uint256 _dx) external view override returns (uint256) {
        return swapStorage.calculateSwap(tokenIndexes_[_tokenFrom], tokenIndexes_[_tokenTo], _dx);
    }

    /**
     * @notice Convert the array index, according to tokenIndexes_.
     * @param _tokens an array of all token addresses for the pair,
     * @param _amounts an array of token amounts, corresponding to param _tokens.
     * @return _newAmounts amount of tokens after conversion
     */
    function _convertIndex(
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) internal view returns (uint256[] memory _newAmounts) {
        _newAmounts = new uint256[](_amounts.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _newAmounts[getTokenIndex(_tokens[i])] = _amounts[i];
        }
    }

    /**
     * @notice A simple method to calculate prices from deposits or
     * withdrawals, excluding fees but including slippage. This is
     * helpful as an input into the various "min" parameters on calls
     * to fight front-running
     *
     * @dev This shouldn't be used outside frontends for user estimates.
     *
     * @param _tokens an array of all token addresses for the pair,
     * @param _amounts an array of token amounts to deposit or withdrawal,
     * corresponding to param _tokens. The amount should be in each
     * pooled token's native precision. If a token charges a fee on transfers,
     * use the amount that gets transferred after the fee.
     * @param _deposit whether this is a deposit or a withdrawal
     * @return token amount the user will receive
     */
    function calculateTokenAmount(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        bool _deposit
    ) external view virtual override returns (uint256) {
        return swapStorage.calculateTokenAmount(_convertIndex(_tokens, _amounts), _deposit);
    }

    /**
     * @notice A simple method to calculate amount of each underlying
     * tokens that is returned upon burning given amount of LP tokens
     * @param _tokens token address list
     * @param _amount the amount of LP tokens that would be burned on withdrawal
     * @return array of token balances that the user will receive
     */
    function calculateRemoveLiquidity(
        address[] calldata _tokens,
        uint256 _amount
    ) external view virtual override returns (uint256[] memory) {
        uint256[] memory _amounts = swapStorage.calculateRemoveLiquidity(_amount);
        uint256[] memory _actualAmounts = new uint256[](_amounts.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            _actualAmounts[i] = _amounts[getTokenIndex(_tokens[i])];
        }
        return _actualAmounts;
    }

    /**
     * @notice Calculate the amount of underlying token available to withdraw
     * when withdrawing via only single token
     * @param _token address of tokens that will be withdrawn
     * @param _tokenAmount the amount of LP token to burn
     * @return calculated amount of underlying token
     * available to withdraw
     */
    function calculateRemoveLiquidityOneToken(
        address _token,
        uint256 _tokenAmount
    ) external view virtual override returns (uint256) {
        return swapStorage.calculateWithdrawOneToken(_tokenAmount, tokenIndexes_[_token]);
    }

    /**
     * @notice This function reads the accumulated amount of admin fees of the token with given index
     * @param _index Index of the pooled token
     * @return admin's token balance in the token's precision
     */
    function getAdminBalance(uint256 _index) external view virtual returns (uint256) {
        return swapStorage.getAdminBalance(_index);
    }

    /*** STATE MODIFYING FUNCTIONS ***/

    /**
     * @notice Calculate amount of tokens you receive on swap
     * @param _tokenFrom the token address the user wants to sell
     * @param _tokenTo the token address the user wants to buy
     * @param _dx the amount of tokens the user wants to swap from
     * @param _minDy the min amount the user would like to receive, or revert.
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     */
    function swap(
        address _tokenFrom,
        address _tokenTo,
        uint256 _dx,
        uint256 _minDy,
        address _receiver,
        uint256 _deadline
    ) external override lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.swap(tokenIndexes_[_tokenFrom], tokenIndexes_[_tokenTo], _dx, _minDy, _receiver);
    }

    /**
     * @notice Add liquidity to the pool with the given amounts of tokens
     * @param _tokens token address list
     * @param _amounts the amounts of each token to add, in their native precision,corresponding to param _tokens
     * @param _minToMint the minimum LP tokens adding this amount of liquidity
     * should mint, otherwise revert. Handy for front-running mitigation
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amount of LP token user minted and received
     */
    function addLiquidity(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        uint256 _minToMint,
        address _receiver,
        uint256 _deadline
    ) external override lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.addLiquidity(_convertIndex(_tokens, _amounts), _minToMint, _receiver);
    }

    /**
     * @notice Burn LP tokens to remove liquidity from the pool. Withdraw fee that decays linearly
     * over period of 4 weeks since last deposit will apply.
     * @dev Liquidity can always be removed, even when the pool is paused.
     * @param _amount the amount of LP tokens to burn
     * @param _tokens token address list
     * @param _minAmounts the minimum amounts of each token in the pool
     *        acceptable for this burn. Useful as a front-running mitigation
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amounts of tokens user received
     */
    function removeLiquidity(
        uint256 _amount,
        address[] calldata _tokens,
        uint256[] calldata _minAmounts,
        address _receiver,
        uint256 _deadline
    ) external virtual override lock deadlineCheck(_deadline) returns (uint256[] memory) {
        return swapStorage.removeLiquidity(_amount, _convertIndex(_tokens, _minAmounts), _receiver);
    }

    /**
     * @notice Remove liquidity from the pool all in one token. Withdraw fee that decays linearly
     * over period of 4 weeks since last deposit will apply.
     * @param _tokenAmount the amount of the token you want to receive
     * @param _token address of the token you want to receive
     * @param _minAmount the minimum amount to withdraw, otherwise revert
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amount of chosen token user received
     */
    function removeLiquidityOneToken(
        uint256 _tokenAmount,
        address _token,
        uint256 _minAmount,
        address _receiver,
        uint256 _deadline
    ) external override lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.removeLiquidityOneToken(_tokenAmount, tokenIndexes_[_token], _minAmount, _receiver);
    }

    /**
     * @notice Remove liquidity from the pool, weighted differently than the
     * pool's current balances. Withdraw fee that decays linearly
     * over period of 4 weeks since last deposit will apply.
     * @param _tokens token address list
     * @param _amounts how much of each token to withdraw
     * @param _maxBurnAmount the max LP token provider is willing to pay to
     * remove liquidity. Useful as a front-running mitigation.
     * @param _receiver recipient address
     * @param _deadline latest timestamp to accept this transaction
     * @return amount of LP tokens burned
     */
    function removeLiquidityImbalance(
        address[] calldata _tokens,
        uint256[] calldata _amounts,
        uint256 _maxBurnAmount,
        address _receiver,
        uint256 _deadline
    ) external override lock deadlineCheck(_deadline) returns (uint256) {
        return swapStorage.removeLiquidityImbalance(_convertIndex(_tokens, _amounts), _maxBurnAmount, _receiver);
    }

    /*** ADMIN FUNCTIONS ***/

    /**
     * @notice Withdraw all admin fees to the contract factory manager
     */
    function claimFees() external returns (uint256[] memory) {
        return swapStorage.withdrawAdminFees(IPairFactory(factory).manager());
    }

    /**
     * @notice Update the admin fee. Admin fee takes portion of the swap fee.
     * @param _newAdminFee new admin fee to be applied on future transactions
     */
    function setAdminFeeRate(uint256 _newAdminFee) external onlyManager {
        swapStorage.setAdminFee(_newAdminFee);
    }

    /**
     * @notice Update the swap fee to be applied on swaps
     * @param _newSwapFee new swap fee to be applied on future transactions
     */
    function setSwapFee(uint256 _newSwapFee) external onlyManager {
        swapStorage.setSwapFee(_newSwapFee);
    }

    /**
     * @notice Start ramping up or down A parameter towards given futureA and futureTime
     * Checks if the change is too rapid, and commits the new A value only when it falls under
     * the limit range.
     * @param _futureA the new A to ramp towards
     * @param _futureTime timestamp when the new A should be reached
     */
    function rampA(uint256 _futureA, uint256 _futureTime) external onlyManager {
        swapStorage.rampA(_futureA, _futureTime);
    }

    /**
     * @notice Stop ramping A immediately. Reverts if ramp A is already stopped.
     */
    function stopRampA() external onlyManager {
        swapStorage.stopRampA();
    }
}

